import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function LoginPage({ onLogin }) {
  const navigate = useNavigate();
  const [form, setForm] = useState({ email: "", password: "" });
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");

    try {
      const res = await fetch("http://localhost:5000/api/users/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form)
      });
      const data = await res.json();
      setLoading(false);

      if (data.success) {
        onLogin(data.user);
        navigate("/home");
      } else {
        setError(data.message || "Invalid credentials");
      }
    } catch (err) {
      console.error(err);
      setError("Server error");
      setLoading(false);
    }
  };

  return (
    <div style={{ display: "flex", justifyContent: "center", alignItems: "center", height: "100vh" }}>
      <form onSubmit={handleSubmit} style={{ padding: "24px", background: "#fff", borderRadius: "8px", boxShadow: "0 4px 10px rgba(0,0,0,0.1)" }}>
        <h2 style={{ marginBottom: "16px" }}>Login</h2>
        {error && <p style={{ color: "red" }}>{error}</p>}
        <input type="email" name="email" placeholder="Email" value={form.email} onChange={handleChange} required style={{ width: "100%", padding: "8px", marginBottom: "12px" }} />
        <input type="password" name="password" placeholder="Password" value={form.password} onChange={handleChange} required style={{ width: "100%", padding: "8px", marginBottom: "12px" }} />
        <button type="submit" disabled={loading} style={{ width: "100%", padding: "10px", background: "#2563eb", color: "#fff", fontWeight: "bold", border: "none", cursor: "pointer" }}>
          {loading ? "Logging in..." : "Login"}
        </button>
      </form>
    </div>
  );
}
