import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import { useNavigate, useLocation } from "react-router-dom";

export default function RouteDetails() {
  const location = useLocation();
  const navigate = useNavigate();
  const routeSpots = location.state?.itinerary || [];
  const [currentIndex, setCurrentIndex] = useState(0);
  const [currentSegment, setCurrentSegment] = useState(null);
  const [loading, setLoading] = useState(true);
  const [weather, setWeather] = useState(null);

  const ORS_API_KEY = "YOUR_OPENROUTESERVICE_KEY";
  const WEATHER_API_KEY = "YOUR_OPENWEATHERMAP_KEY";

  const currentPOI = routeSpots[currentIndex];
  const nextPOI = routeSpots[currentIndex + 1];

  // Fetch route segment
  const fetchRouteSegment = async (from, to) => {
    const isTrail = ["forest", "mountain", "trail"].some(t =>
      from.type?.toLowerCase().includes(t)
    );
    const profile = isTrail ? "foot-hiking" : "driving-car";

    const url = `https://api.openrouteservice.org/v2/directions/${profile}?api_key=${ORS_API_KEY}&start=${from.lng},${from.lat}&end=${to.lng},${to.lat}`;

    try {
      const res = await fetch(url);
      const data = await res.json();
      const coords = data.features[0].geometry.coordinates.map(([lng, lat]) => [lat, lng]);
      const summary = data.features[0].properties.summary; // distance in m, duration in s
      return { coords, isTrail, summary };
    } catch (err) {
      console.error("Route fetch failed:", err);
      return { coords: [[from.lat, from.lng], [to.lat, to.lng]], isTrail: false, summary: { distance: 0, duration: 0 } };
    }
  };

  // Fetch weather at current POI
  const fetchWeather = async (lat, lng) => {
    try {
      const res = await fetch(`https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lng}&units=metric&appid=${WEATHER_API_KEY}`);
      const data = await res.json();
      setWeather({
        temp: data.main.temp,
        desc: data.weather[0].description,
      });
    } catch (err) {
      console.error("Weather fetch failed:", err);
      setWeather(null);
    }
  };

  useEffect(() => {
    const loadSegmentAndWeather = async () => {
      setLoading(true);
      if (currentPOI && nextPOI) {
        const segment = await fetchRouteSegment(currentPOI, nextPOI);
        setCurrentSegment(segment);
      } else {
        setCurrentSegment(null);
      }
      if (currentPOI) await fetchWeather(currentPOI.lat, currentPOI.lng);
      setLoading(false);
    };

    loadSegmentAndWeather();
  }, [currentIndex, routeSpots]);

  const handleNextPOI = () => {
    if (currentIndex < routeSpots.length - 1) setCurrentIndex(currentIndex + 1);
    else alert("You have reached the end of the route!");
  };

  const handleCancel = () => {
    if (window.confirm("Cancel route? You will lose progress.")) {
      navigate("/home");
    }
  };

  const handleFinish = () => {
    if (window.confirm("Finish route?")) navigate("/home");
  };

  if (!routeSpots.length) return <p>No route available.</p>;

  return (
    <div style={{ display: "grid", gridTemplateColumns: "1.2fr 0.8fr", gap: 20, padding: 20 }}>
      {/* Map */}
      <div style={{ height: "600px", borderRadius: 12, overflow: "hidden", boxShadow: "0 6px 18px rgba(0,0,0,0.15)" }}>
        {loading ? (
          <p style={{ textAlign: "center", marginTop: "40%" }}>Loading route...</p>
        ) : (
          <MapContainer
            key={currentPOI.id} // recenter on POI change
            center={[currentPOI.lat, currentPOI.lng]}
            zoom={14}
            style={{ height: "100%", width: "100%" }}
          >
            <TileLayer
              attribution='&copy; <a href="https://www.openstreetmap.org/">OSM contributors</a>'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            {currentSegment && (
              <Polyline
                positions={currentSegment.coords}
                color={currentSegment.isTrail ? "green" : "blue"}
                dashArray={currentSegment.isTrail ? "5,10" : undefined}
                weight={5}
              />
            )}
            {routeSpots.filter(spot => spot.lat && spot.lng).map((spot, i) => (
              <Marker key={i} position={[spot.lat, spot.lng]}>
                <Popup>
                  <strong>{spot.name}</strong>
                  <p>{spot.description}</p>
                  {spot.image && <img src={spot.image} alt={spot.name} style={{ width: 160, borderRadius: 8, marginTop: 6 }} />}
                </Popup>
              </Marker>
            ))}
          </MapContainer>
        )}
      </div>

      {/* Info Panel */}
      <div style={{ background: "#fff", borderRadius: 12, padding: 20, boxShadow: "0 6px 18px rgba(0,0,0,0.12)" }}>
        <h2>{currentPOI.name}</h2>
        <p>{currentPOI.description}</p>
        {currentPOI.image && <img src={currentPOI.image} alt={currentPOI.name} style={{ width: "100%", maxWidth: 280, borderRadius: 10 }} />}
        {currentSegment && (
          <div style={{ marginTop: 12 }}>
            <p><strong>Next POI:</strong> {nextPOI?.name}</p>
            <p><strong>Distance:</strong> {(currentSegment.summary.distance / 1000).toFixed(2)} km</p>
            <p><strong>Estimated Time:</strong> {(currentSegment.summary.duration / 60).toFixed(0)} min</p>
          </div>
        )}
        {weather && (
          <div style={{ marginTop: 12 }}>
            <p><strong>Weather:</strong> {weather.temp}Â°C, {weather.desc}</p>
          </div>
        )}
        <p style={{ marginTop: 20, fontStyle: "italic" }}>POI {currentIndex + 1} of {routeSpots.length}</p>
      </div>

      {/* Footer Buttons */}
      <div style={{ position: "fixed", bottom: 0, left: 0, right: 0, background: "linear-gradient(90deg, #2196f3, #42a5f5)", padding: "12px 20px", display: "flex", justifyContent: "center", gap: 16, zIndex: 1000 }}>
        {currentIndex < routeSpots.length - 1 && <button onClick={handleNextPOI} style={{ padding: 10, border: "none", borderRadius: 8, background: "#4caf50", color: "#fff" }}>Next POI</button>}
        <button onClick={handleCancel} style={{ padding: 10, border: "none", borderRadius: 8, background: "#f44336", color: "#fff" }}>Cancel</button>
        {currentIndex === routeSpots.length - 1 && <button onClick={handleFinish} style={{ padding: 10, border: "none", borderRadius: 8, background: "#ff9800", color: "#fff" }}>Finish</button>}
      </div>
    </div>
  );
}
