import React, { useEffect, useState } from "react";

export default function ProfilePage() {
  const storedUser = JSON.parse(localStorage.getItem("user"));
  const token = localStorage.getItem("token");

  const [user, setUser] = useState(null);
  const [preferences, setPreferences] = useState([]);
  const [editing, setEditing] = useState(false);

  const availablePreferences = [
    "Nature",
    "Culture",
    "Adventure",
    "Food",
    "Relaxation",
    "History"
  ];

  useEffect(() => {
    if (!storedUser) return;

    fetch(`http://localhost:5000/api/users/email/${storedUser.email}`, {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => {
        if (!res.ok) throw new Error("Failed to fetch profile");
        return res.json();
      })
      .then((data) => {
        setUser(data);
        // Always parse preferences into an array
        const prefArray = data.preferences
          ? data.preferences.split(",").map((p) => p.trim())
          : [];
        setPreferences(prefArray);
      })
      .catch((err) => console.error("Error fetching user:", err));
  }, [storedUser, token]);

  const handleSave = () => {
    if (!user) return;

    const prefString = preferences.join(",");

    fetch(`http://localhost:5000/api/users/${user.id}/preferences`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ preferences: prefString }),
    })
      .then((res) => {
        if (!res.ok) throw new Error("Failed to update preferences");
        return res.json();
      })
      .then(() => {
        setEditing(false);
        setUser({ ...user, preferences: prefString });
      })
      .catch((err) => console.error("Error updating preferences:", err));
  };

  const handleCheckboxChange = (pref) => {
    setPreferences((prev) =>
      prev.includes(pref)
        ? prev.filter((p) => p !== pref)
        : [...prev, pref]
    );
  };

  if (!user) return <div style={{ padding: "24px" }}>Loading...</div>;

  return (
    <div style={{ padding: "24px", maxWidth: "600px", margin: "0 auto" }}>
      <h1 style={{ fontSize: "1.8rem", fontWeight: "bold", marginBottom: "16px" }}>
        Profile
      </h1>

      <div
        style={{
          border: "1px solid #ddd",
          borderRadius: "16px",
          padding: "20px",
          background: "#fff",
          boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
        }}
      >
        <p><strong>Username:</strong> {user.username}</p>
        <p><strong>Full Name:</strong> {user.first_name} {user.middle_name} {user.last_name}</p>
        <p><strong>Age:</strong> {user.age}</p>
        <p><strong>Email:</strong> {user.email}</p>

        <div style={{ marginTop: "16px" }}>
          <strong>Preferences:</strong>
          {editing ? (
            <div style={{ marginTop: "8px" }}>
              {availablePreferences.map((pref) => (
                <label key={pref} style={{ display: "block", marginBottom: "6px" }}>
                  <input
                    type="checkbox"
                    checked={preferences.includes(pref)}
                    onChange={() => handleCheckboxChange(pref)}
                  />
                  <span style={{ marginLeft: "8px" }}>{pref}</span>
                </label>
              ))}
              <div style={{ marginTop: "8px" }}>
                <button
                  onClick={handleSave}
                  style={{
                    background: "#007bff",
                    color: "#fff",
                    padding: "8px 16px",
                    border: "none",
                    borderRadius: "8px",
                  }}
                >
                  Save
                </button>
                <button
                  onClick={() => {
                    setEditing(false);
                    setPreferences(
                      user.preferences ? user.preferences.split(",").map((p) => p.trim()) : []
                    );
                  }}
                  style={{
                    background: "#ccc",
                    padding: "8px 16px",
                    border: "none",
                    borderRadius: "8px",
                    marginLeft: "8px",
                  }}
                >
                  Cancel
                </button>
              </div>
            </div>
          ) : (
            <p style={{ marginTop: "8px" }}>
              {user.preferences || "No preferences set."}
            </p>
          )}
        </div>

        {!editing && (
          <button
            onClick={() => setEditing(true)}
            style={{
              marginTop: "16px",
              background: "#28a745",
              color: "#fff",
              padding: "8px 16px",
              border: "none",
              borderRadius: "8px",
            }}
          >
            Edit Preferences
          </button>
        )}
      </div>
    </div>
  );
}
