import React, { useEffect, useMemo, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import "../leafletfix";
import Papa from "papaparse";

const CENTER = { lat: 14.1467, lng: 121.4708 };

export default function Home() {
  const [spots, setSpots] = useState([]);

  useEffect(() => {
    Papa.parse("/data/spots.csv", {
      download: true, // ✅ important! this fetches from public folder
      header: true,
      complete: (res) => {
        const rows = res.data
          .filter((r) => r.name && r.lat && r.lng)
          .map((r) => ({
            name: r.name,
            lat: parseFloat(r.lat),
            lng: parseFloat(r.lng),
            visitors: r.visitors || "Moderate",
            // image paths relative to public folder
            image: r.image ? r.image.startsWith("/") ? r.image : `/images/${r.image}` : "",
          }));
        setSpots(rows);
      },
      error: (err) => console.error("CSV load error:", err),
    });
  }, []);

  const topSpots = useMemo(() => {
    const order = { "Very High": 3, High: 2, Moderate: 1, Low: 0 };
    return [...spots]
      .sort((a, b) => (order[b.visitors] || 0) - (order[a.visitors] || 0))
      .slice(0, 5);
  }, [spots]);

  return (
    <div className="grid" style={{ display: "grid", gap: 16, gridTemplateColumns: "1fr 1fr" }}>
      <div className="card">
        <h2>Majayjay Overview</h2>
        <div style={{ height: "500px" }}>
          <MapContainer center={CENTER} zoom={13} className="h-full w-full">
            <TileLayer
              attribution='&copy; <a href="https://www.openstreetmap.org/">OSM</a>'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            {topSpots.map((s, i) => (
              <Marker key={i} position={[s.lat, s.lng]}>
                <Popup>
                  <strong>{s.name}</strong>
                  <br />
                  Visitors: {s.visitors}
                  {s.image && <div><img src={s.image} alt={s.name} style={{ width: "150px", borderRadius: "8px", marginTop: "4px" }} /></div>}
                </Popup>
              </Marker>
            ))}
          </MapContainer>
        </div>
      </div>

      <div className="card">
        <h2>Most Visited Places</h2>
        <ul>
          {topSpots.map((s, i) => (
            <li key={i}><strong>{s.name}</strong> — {s.visitors} visitors</li>
          ))}
          {topSpots.length === 0 && <li>No spots found</li>}
        </ul>
      </div>
    </div>
  );
}
