import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import { useNavigate, useLocation } from "react-router-dom";

export default function RouteDetails() {
  const location = useLocation();
  const navigate = useNavigate();
  const filteredPois = location.state?.filteredPois || [];
  const transportMode = location.state?.transportMode || "driving-car";
  const startPoint = location.state?.startPoint || null;

  const [selectedPois, setSelectedPois] = useState(filteredPois);
  const [poiDetails, setPoiDetails] = useState([]);
  const [userLocation, setUserLocation] = useState(startPoint);
  const [routeToStop, setRouteToStop] = useState([]);
  const [activeIndex, setActiveIndex] = useState(0);
  const [tracking, setTracking] = useState(false);
  const [nextPreview, setNextPreview] = useState(null);

  const mapRef = useRef(null);
  const watchIdRef = useRef(null);

  const ORS_API_KEY =
    "eyJvcmciOiI1YjNjZTM1OTc4NTExMTAwMDFjZjYyNDgiLCJpZCI6ImJhNWU3NmIxOTRmMzQyYTg4NzY4MDc2MzVmZDY1ZDRhIiwiaCI6Im11cm11cjY0In0=";


  // ---------- ICONS ----------
  const numberedIcon = (number, isActive = false) =>
    L.divIcon({
      html: `<div style="
        background:${isActive ? '#388e3c' : '#1976d2'};
        color:#fff;
        width:28px;
        height:28px;
        border-radius:50%;
        text-align:center;
        line-height:28px;
        font-weight:600;
        border:2px solid #fff;
        box-shadow:0 2px 6px rgba(0,0,0,0.3);
      ">${number}</div>`,
      className: "",
      iconSize: [28, 28],
    });

  const userIcon = L.divIcon({
    html: `<div style="
      background:#4caf50;
      color:#fff;
      width:30px;
      height:30px;
      border-radius:50%;
      text-align:center;
      line-height:30px;
      font-weight:700;
      border:2px solid #fff;
      box-shadow:0 2px 8px rgba(0,0,0,0.3);
    ">U</div>`,
    className: "",
    iconSize: [30, 30],
  });

  // ---------- LOCATION ----------
  useEffect(() => {
    if (!navigator.geolocation) return;
    navigator.geolocation.getCurrentPosition(
      (pos) => setUserLocation({ lat: pos.coords.latitude, lng: pos.coords.longitude }),
      () => startPoint && setUserLocation(startPoint),
      { enableHighAccuracy: true }
    );
  }, [startPoint]);

  // ---------- FETCH POI DETAILS ----------
  useEffect(() => {
    const fetchDetails = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/pois");
        const data = await res.json();
        // Match the POIs by id or name
        const matched = filteredPois.map((poi) =>
          data.find((p) => p.id === poi.id || p.name === poi.name)
        );
        setPoiDetails(matched);
      } catch (err) {
        console.error(err);
      }
    };
    fetchDetails();
  }, [filteredPois]);

  // ---------- SNAP TO ROAD ----------
  const snapToRoad = async (poi) => {
    try {
      const res = await fetch("https://api.openrouteservice.org/v2/snap/driving-car", {
        method: "POST",
        headers: { Authorization: ORS_API_KEY, "Content-Type": "application/json" },
        body: JSON.stringify({ coordinates: [[poi.lng, poi.lat]] }),
      });
      const data = await res.json();
      if (data.snappedLocations?.length) {
        const [lng, lat] = data.snappedLocations[0].location;
        return { lat, lng };
      }
      return poi;
    } catch {
      return poi;
    }
  };

  // ---------- FETCH ROUTE ----------
  const fetchRoute = async (from, to) => {
    if (!from || !to) return [];
    const snappedFrom = await snapToRoad(from);
    const snappedTo = await snapToRoad(to);
    try {
      const res = await fetch(
        "https://api.openrouteservice.org/v2/directions/" + transportMode + "/geojson",
        {
          method: "POST",
          headers: { Authorization: ORS_API_KEY, "Content-Type": "application/json" },
          body: JSON.stringify({
            coordinates: [
              [snappedFrom.lng, snappedFrom.lat],
              [snappedTo.lng, snappedTo.lat],
            ],
            preference: "fastest",
            instructions: false,
          }),
        }
      );
      const data = await res.json();
      if (data.features?.length) {
        return data.features[0].geometry.coordinates.map(([lng, lat]) => [lat, lng]);
      }
    } catch {}
    return [[from.lat, from.lng], [to.lat, to.lng]];
  };

  // ---------- HANDLE POI SELECTION ----------
  const togglePoiSelection = (poi) => {
    setSelectedPois((prev) =>
      prev.includes(poi) ? prev.filter((p) => p !== poi) : [...prev, poi]
    );
  };

  // ---------- START TOUR ----------
  const startTour = async () => {
    if (!userLocation || selectedPois.length === 0) return;
    setTracking(true);
    setActiveIndex(0);
    setNextPreview(selectedPois[1] || null);
    const firstStop = selectedPois[0];
    const initialRoute = await fetchRoute(userLocation, firstStop);
    setRouteToStop(initialRoute);

    watchIdRef.current = navigator.geolocation.watchPosition(
      async (pos) => {
        const current = { lat: pos.coords.latitude, lng: pos.coords.longitude };
        setUserLocation(current);
        const liveRoute = await fetchRoute(current, selectedPois[activeIndex]);
        setRouteToStop(liveRoute);
      },
      (err) => console.warn(err),
      { enableHighAccuracy: true, maximumAge: 2000, timeout: 10000 }
    );
  };

  const handleNextStop = async () => {
    if (activeIndex + 1 >= selectedPois.length) {
      stopTour();
      alert("Tour completed!");
      return;
    }
    setActiveIndex((prev) => prev + 1);
    setNextPreview(selectedPois[activeIndex + 2] || null);

    if (userLocation) {
      const updatedRoute = await fetchRoute(userLocation, selectedPois[activeIndex + 1]);
      setRouteToStop(updatedRoute);
    }
  };

  const stopTour = () => {
    if (watchIdRef.current) navigator.geolocation.clearWatch(watchIdRef.current);
    setTracking(false);
    setRouteToStop([]);
    setActiveIndex(0);
    setNextPreview(null);
  };

  if (!selectedPois.length || !userLocation) return <p>Loading route...</p>;

  return (
    <div style={{ display: "grid", gridTemplateColumns: "1.3fr 0.7fr", gap: "24px", padding: "24px", minHeight: "100vh" }}>
      {/* MAP */}
      <div style={{ height: "650px", borderRadius: "18px", overflow: "hidden", boxShadow: "0 10px 25px rgba(0,0,0,0.15)" }}>
        <MapContainer center={[userLocation.lat, userLocation.lng]} zoom={13} style={{ height: "100%", width: "100%" }}>
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" attribution="Â© OpenStreetMap" />
          <Marker position={[userLocation.lat, userLocation.lng]} icon={userIcon}>
            <Popup>You are here</Popup>
          </Marker>

          {routeToStop.length > 0 && <Polyline positions={routeToStop} color="#1976d2" weight={6} opacity={0.9} />}

          {selectedPois.map((poi, i) => (
            <Marker key={i} position={[poi.lat, poi.lng]} icon={numberedIcon(i + 1, i === activeIndex)}>
              <Popup>
                <strong>Stop {i + 1}: {poi.name}</strong>
                <p>{poi.description || "No description available."}</p>
              </Popup>
            </Marker>
          ))}
        </MapContainer>
      </div>

      {/* SIDEBAR */}
      <div style={{ background: "#fff", borderRadius: "18px", padding: "24px", boxShadow: "0 8px 24px rgba(0,0,0,0.1)", overflowY: "auto", maxHeight: "650px" }}>
        {!tracking ? (
          <>
            <h2 style={{ color: "#1565c0", fontWeight: "700" }}>Select POIs for Tour</h2>
            {filteredPois.map((poi, i) => (
              <div key={i} style={{ marginBottom: "12px", borderBottom: "1px solid #eee", paddingBottom: "8px" }}>
                <input
                  type="checkbox"
                  checked={selectedPois.includes(poi)}
                  onChange={() => togglePoiSelection(poi)}
                />{" "}
                {poi.name}
              </div>
            ))}

            <button
              onClick={startTour}
              style={{ width: "100%", padding: "12px 20px", background: "#4caf50", color: "#fff", border: "none", borderRadius: "8px", fontWeight: "600", cursor: "pointer", marginTop: "16px" }}
            >
              Start Tour
            </button>
          </>
        ) : (
          <>
            <h3 style={{ color: "#1976d2" }}>Now Visiting:</h3>
            <strong>{selectedPois[activeIndex]?.name}</strong>
            <p>{selectedPois[activeIndex]?.description}</p>

            {nextPreview ? (
              <>
                <p style={{ color: "#666" }}>Next: <strong>{nextPreview.name}</strong></p>
                <button onClick={handleNextStop} style={{ width: "100%", padding: "12px 20px", background: "#1976d2", color: "#fff", border: "none", borderRadius: "8px", fontWeight: "600", cursor: "pointer", marginTop: "12px" }}>Next Stop</button>
              </>
            ) : (
              <button onClick={stopTour} style={{ width: "100%", padding: "12px 20px", background: "#1976d2", color: "#fff", border: "none", borderRadius: "8px", fontWeight: "600", cursor: "pointer", marginTop: "12px" }}>Finish Tour</button>
            )}

            <button onClick={stopTour} style={{ width: "100%", padding: "12px 20px", background: "#f44336", color: "#fff", border: "none", borderRadius: "8px", fontWeight: "600", cursor: "pointer", marginTop: "10px" }}>Cancel Tour</button>
          </>
        )}

        <button onClick={() => navigate("/home")} style={{ width: "100%", padding: "12px 20px", background: "#9e9e9e", color: "#fff", border: "none", borderRadius: "8px", fontWeight: "600", cursor: "pointer", marginTop: "12px" }}>
          Back to Home
        </button>
      </div>
    </div>
  );
}
