import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const ORS_API_KEY =
  "eyJvcmciOiI1YjNjZTM1OTc4NTExMTAwMDFjZjYyNDgiLCJpZCI6ImJhNWU3NmIxOTRmMzQyYTg4NzY4MDc2MzVmZDY1ZDRhIiwiaCI6Im11cm11cjY0In0=";

export default function RoutePlanner() {
  const [pois, setPois] = useState([]);
  const [categories, setCategories] = useState([]); // All available POI categories
  const [selectedCategories, setSelectedCategories] = useState([]);
  const [user, setUser] = useState(null);
  const [showPopup, setShowPopup] = useState(false);
  const [startTime, setStartTime] = useState("now");
  const [scheduledDate, setScheduledDate] = useState("");
  const [mode, setMode] = useState("car");
  const [userLocation, setUserLocation] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    setUser(storedUser);

    // Fetch all POIs
    fetch("http://localhost:5000/api/pois")
      .then((res) => res.json())
      .then((data) => {
        setPois(data);
        // Extract unique categories from POIs
        const cats = [...new Set(data.flatMap((p) => p.tags?.split(",").map((t) => t.trim()) || []))];
        setCategories(cats);
      })
      .catch(() => setPois([]));

    if ("geolocation" in navigator) {
      navigator.geolocation.getCurrentPosition(
        (pos) => setUserLocation({ lat: pos.coords.latitude, lng: pos.coords.longitude }),
        () => setUserLocation({ lat: 14.1461, lng: 121.5012 }) // fallback Majayjay
      );
    } else {
      setUserLocation({ lat: 14.1461, lng: 121.5012 });
    }
  }, []);

  const handleCategoryChange = (cat) => {
    setSelectedCategories((prev) =>
      prev.includes(cat) ? prev.filter((c) => c !== cat) : [...prev, cat]
    );
  };

  const recommendRoute = () => {
    if (!userLocation) {
      alert("Waiting for current location...");
      return;
    }
    if (selectedCategories.length === 0) {
      alert("Please select at least one category.");
      return;
    }

    // Filter POIs by selected categories
    const filteredPois = pois.filter((poi) =>
      selectedCategories.some((cat) => poi.tags?.toLowerCase().includes(cat.toLowerCase()))
    );

    if (filteredPois.length === 0) {
      alert("No POIs match your selected categories.");
      return;
    }

    // Pass filtered POIs to RouteDetails
    navigate("/routedetails", {
      state: {
        startPoint: { lat: userLocation.lat, lng: userLocation.lng },
        filteredPois,
        transportMode: mode === "foot" ? "foot-walking" : mode === "bike" ? "cycling-regular" : "driving-car",
        startTime: startTime === "now" ? new Date().toISOString() : scheduledDate,
      },
    });
    setShowPopup(false);
  };

  return (
    <div style={{ textAlign: "center", marginTop: 40 }}>
      <button
        onClick={() => setShowPopup(true)}
        style={{
          padding: "12px 24px",
          background: "#2196f3",
          color: "#fff",
          border: "none",
          borderRadius: 8,
          fontSize: 16,
          cursor: "pointer",
        }}
      >
        Plan My Route
      </button>

      {showPopup && (
        <div
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background: "rgba(0,0,0,0.6)",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            zIndex: 9999,
          }}
        >
          <div
            style={{
              background: "#fff",
              padding: 20,
              borderRadius: 10,
              width: 360,
              maxHeight: "80vh",
              overflowY: "auto",
              textAlign: "center",
              zIndex: 10000,
            }}
          >
            <h3 style={{ marginBottom: 12 }}>Select POI Categories</h3>
            {categories.map((cat) => (
              <label key={cat} style={{ display: "block", marginBottom: 6, textAlign: "left" }}>
                <input
                  type="checkbox"
                  value={cat}
                  checked={selectedCategories.includes(cat)}
                  onChange={() => handleCategoryChange(cat)}
                />{" "}
                {cat}
              </label>
            ))}

            <hr style={{ margin: "12px 0" }} />

            <label>Mode of Transport:</label>
            <select
              value={mode}
              onChange={(e) => setMode(e.target.value)}
              style={{ display: "block", margin: "8px auto", padding: 6 }}
            >
              <option value="foot">On Foot</option>
              <option value="bike">Bike</option>
              <option value="motor">Motor</option>
              <option value="car">Car</option>
            </select>

            <div style={{ marginTop: 16 }}>
              <button
                onClick={recommendRoute}
                style={{
                  marginRight: 8,
                  padding: "8px 16px",
                  background: "#3b82f6",
                  color: "#fff",
                  border: "none",
                  borderRadius: 6,
                  cursor: "pointer",
                }}
              >
                Confirm
              </button>
              <button
                onClick={() => setShowPopup(false)}
                style={{
                  padding: "8px 16px",
                  background: "#aaa",
                  color: "#fff",
                  border: "none",
                  borderRadius: 6,
                  cursor: "pointer",
                }}
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
