import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import { useNavigate, useLocation } from "react-router-dom";

export default function RouteDetails() {
  const location = useLocation();
  const navigate = useNavigate();
  const routeSpots = location.state?.itinerary || [];
  const [segments, setSegments] = useState([]);
  const [loading, setLoading] = useState(false);
  const [poiDetails, setPoiDetails] = useState([]);
  const [saving, setSaving] = useState(false);
  const [saveMessage, setSaveMessage] = useState("");
  const [status, setStatus] = useState("pending");
  const [userLocation, setUserLocation] = useState(
    location.state?.startPoint ? { lat: location.state.startPoint.lat, lng: location.state.startPoint.lng } : null
  );
  const mapRef = useRef(null);

  const ORS_API_KEY =
    "eyJvcmciOiI1YjNjZTM1OTc4NTExMTAwMDFjZjYyNDgiLCJpZCI6ImJhNWU3NmIxOTRmMzQyYTg4NzY4MDc2MzVmZDY1ZDRhIiwiaCI6Im11cm11cjY0In0=";

  useEffect(() => {
    if (userLocation && mapRef.current) {
      try {
        mapRef.current.setView([userLocation.lat, userLocation.lng], 14, { animate: true });
      } catch {}
    }
  }, [userLocation]);

  useEffect(() => {
    if (!("geolocation" in navigator)) {
      setUserLocation({ lat: 14.1461, lng: 121.5012 });
      return;
    }
    const watchId = navigator.geolocation.watchPosition(
      (pos) => {
        const { latitude, longitude } = pos.coords;
        const loc = { lat: latitude, lng: longitude };
        setUserLocation(loc);
        if (mapRef.current) {
          try {
            mapRef.current.setView([latitude, longitude], 14, { animate: true });
          } catch {}
        }
      },
      (err) => {
        console.warn("Geolocation error:", err.message);
        setUserLocation({ lat: 14.1461, lng: 121.5012 });
      },
      { enableHighAccuracy: true, timeout: 10000, maximumAge: 0 }
    );
    return () => navigator.geolocation.clearWatch(watchId);
  }, []);

  const fetchRouteSegment = async (from, to, mode = "driving-car") => {
    try {
      const res = await fetch(`https://api.openrouteservice.org/v2/directions/${mode}`, {
        method: "POST",
        headers: {
          Authorization: ORS_API_KEY,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ coordinates: [[from.lng, from.lat], [to.lng, to.lat]] }),
      });
      const data = await res.json();
      if (!data.features?.length) return [];
      const coords = data.features[0].geometry.coordinates.map(([lng, lat]) => [lat, lng]);
      return coords;
    } catch (err) {
      console.error("ORS fetch error:", err);
      return [];
    }
  };

  const fetchPOIDetails = async (lat, lng) => {
    try {
      const res = await fetch(`http://localhost:5000/api/pois/near?lat=${lat}&lng=${lng}`);
      if (!res.ok) throw new Error("POI fetch failed");
      const data = await res.json();
      return data;
    } catch {
      return [];
    }
  };

  useEffect(() => {
    let cancelled = false;
    const loadData = async () => {
      if (!userLocation) return;
      setLoading(true);
      const points = [userLocation, ...routeSpots];
      const newSegments = [];
      for (let i = 0; i < points.length - 1; i++) {
        if (cancelled) return;
        const coords = await fetchRouteSegment(points[i], points[i + 1]);
        newSegments.push(coords);
      }
      if (cancelled) return;
      setSegments(newSegments);
      const poiFetches = await Promise.all(routeSpots.map((spot) => fetchPOIDetails(spot.lat, spot.lng)));
      if (cancelled) return;
      setPoiDetails(poiFetches.flat());
      setLoading(false);
    };
    loadData();
    return () => {
      cancelled = true;
    };
  }, [userLocation, routeSpots]);

  const saveItinerary = async () => {
    setSaving(true);
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (!storedUser || !storedUser.id) {
      alert("Please log in first.");
      setSaving(false);
      return;
    }
    const itineraryData = {
      user_id: storedUser.id,
      name: `Trip - ${new Date().toLocaleString()}`,
      transport_mode: location.state?.transportMode || "driving-car",
      start_time: location.state?.startTime || new Date().toISOString(),
      start_lat: userLocation?.lat,
      start_lng: userLocation?.lng,
      points: routeSpots,
      status: "saved",
    };
    try {
      const res = await fetch("http://localhost:5000/api/itineraries/save", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(itineraryData),
      });
      if (res.ok) {
        setSaveMessage("Itinerary saved successfully!");
        setStatus("saved");
      } else {
        setSaveMessage("Failed to save itinerary.");
      }
    } catch {
      setSaveMessage("Error saving itinerary.");
    }
    setSaving(false);
    setTimeout(() => setSaveMessage(""), 3000);
  };

  const userIcon = new L.Icon({
    iconUrl: "https://cdn-icons-png.flaticon.com/512/149/149060.png",
    iconSize: [32, 32],
  });

  const poiIcon = new L.Icon({
    iconUrl: "https://cdn-icons-png.flaticon.com/512/854/854878.png",
    iconSize: [28, 28],
  });

  if (!userLocation) return <div style={{ textAlign: "center", marginTop: 50 }}>Loading location...</div>;

  return (
    <div style={{ display: "flex", height: "100vh" }}>
      <div style={{ flex: 3 }}>
        <MapContainer
          center={[userLocation.lat, userLocation.lng]}
          zoom={13}
          style={{ height: "100%", width: "100%" }}
          whenCreated={(map) => (mapRef.current = map)}
        >
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
          <Marker position={[userLocation.lat, userLocation.lng]} icon={userIcon}>
            <Popup>You are here</Popup>
          </Marker>
          {routeSpots.map((spot, i) => (
            <Marker key={i} position={[spot.lat, spot.lng]} icon={poiIcon}>
              <Popup>
                <b>{spot.name}</b>
                <br />
                {poiDetails.find((p) => p.name === spot.name)?.description || "No details available"}
              </Popup>
            </Marker>
          ))}
          {segments.map((seg, i) => (
            <Polyline key={i} positions={seg} color="blue" weight={4} />
          ))}
        </MapContainer>
      </div>
      <div style={{ flex: 1, padding: 20, overflowY: "auto", background: "#f5f5f5" }}>
        <h3 style={{ textAlign: "center" }}>Itinerary Details</h3>
        <p>
          <b>Mode:</b> {location.state?.transportMode}
        </p>
        <p>
          <b>Start Time:</b> {location.state?.startTime ? new Date(location.state.startTime).toLocaleString() : "N/A"}
        </p>
        <h4>Stops:</h4>
        <ol>
          {routeSpots.map((spot, i) => (
            <li key={i}>{spot.name}</li>
          ))}
        </ol>
        <button
          onClick={saveItinerary}
          disabled={saving || status === "saved"}
          style={{
            padding: "10px 20px",
            background: status === "saved" ? "#4caf50" : "#2196f3",
            color: "#fff",
            border: "none",
            borderRadius: 6,
            cursor: "pointer",
            width: "100%",
            marginTop: 12,
          }}
        >
          {saving ? "Saving..." : status === "saved" ? "Saved" : "Save Itinerary"}
        </button>
        {saveMessage && <p style={{ textAlign: "center", color: "#333" }}>{saveMessage}</p>}
        <button
          onClick={() => navigate("/")}
          style={{
            padding: "10px 20px",
            background: "#aaa",
            color: "#fff",
            border: "none",
            borderRadius: 6,
            cursor: "pointer",
            width: "100%",
            marginTop: 10,
          }}
        >
          Back to Planner
        </button>
      </div>
    </div>
  );
}
