import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import Papa from "papaparse";

const CENTER = { lat: 14.1467, lng: 121.4708 };

export default function Home() {
  const [spots, setSpots] = useState([]);
  const [route, setRoute] = useState([]);
  const [weather, setWeather] = useState({ weather: [{ main: "Clear" }] });
  const [loading, setLoading] = useState(false);

  // User preferences
  const [userPreferences, setUserPreferences] = useState({
    preferNature: true,
    preferCulture: false,
  });

  // Fetch CSV spots
  useEffect(() => {
    fetch("/data/spots.csv")
      .then((res) => res.text())
      .then((csv) => {
        Papa.parse(csv, {
          header: true,
          skipEmptyLines: true,
          complete: (results) => {
            const rows = results.data
              .filter((r) => r.name && r.lat && r.lng)
              .map((r) => ({
                name: r.name.trim(),
                lat: parseFloat(r.lat),
                lng: parseFloat(r.lng),
                visitors: r.visitors.trim() || "Moderate",
                image: r.image || null,
                description: r.description || "No description",
                type: r.tags || "nature",
              }));
            setSpots(rows);
          },
        });
      })
      .catch((err) => console.error("CSV load error:", err));
  }, []);

// ML-like route generation using real ML server
const generateRoute = async () => {
  console.log("Generate Route Clicked!");

  if (!spots.length) {
    alert("No spots available to generate route!");
    return;
  }

  try {
    const response = await fetch("http://localhost:5000/predict", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        userPreferences,
        weather
      })
    });

    if (!response.ok) throw new Error("ML server response not OK");

    const data = await response.json();
    console.log("ML Server route:", data.route);

    if (data.route && data.route.length > 0) {
      setRoute(data.route);
    } else {
      console.warn("ML Server returned empty route, using default scoring.");

      // fallback to simple scoring
      let fallback = spots.map((s) => {
        let score = 0;
        if (userPreferences.preferNature && s.tags.includes("nature")) score += 0.3;
        if (userPreferences.preferCulture && s.tags.includes("history")) score += 0.2;
        score += { "Very High": 3, High: 2, Moderate: 1, Low: 0 }[s.visitors] || 1;
        return { ...s, score };
      });
      fallback.sort((a, b) => b.score - a.score);
      setRoute(fallback);
    }

  } catch (err) {
    console.error("ML server not reachable. Using default scoring.", err);

    // fallback scoring
    let fallback = spots.map((s) => {
      let score = 0;
      if (userPreferences.preferNature && s.tags.includes("nature")) score += 0.3;
      if (userPreferences.preferCulture && s.tags.includes("history")) score += 0.2;
      score += { "Very High": 3, High: 2, Moderate: 1, Low: 0 }[s.visitors] || 1;
      return { ...s, score };
    });
    fallback.sort((a, b) => b.score - a.score);
    setRoute(fallback);
  }
};

  const displayedSpots = route.length ? route : spots;

  return (
    <div style={{ display: "grid", gap: 16, gridTemplateColumns: "1fr 1fr" }}>
      <div className="card">
        <h2>Majayjay Overview</h2>
        <div style={{ height: "500px" }}>
          {displayedSpots.length ? (
            <MapContainer center={CENTER} zoom={13} style={{ height: "100%", width: "100%" }}>
              <TileLayer
                attribution='&copy; <a href="https://www.openstreetmap.org/">OSM contributors</a>'
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              />
              {displayedSpots.map((s, i) => (
                <Marker key={i} position={[s.lat, s.lng]}>
                  <Popup>
                    <strong>{s.name}</strong>
                    <br />
                    Visitors: {s.visitors}
                    {s.image && <><br /><img src={s.image} alt={s.name} style={{ width: 150, borderRadius: 8, marginTop: 4 }} /></>}
                    <p>{s.description}</p>
                    {s.predicted_frequency && <p><strong>Predicted Frequency:</strong> {s.predicted_frequency}</p>}
                  </Popup>
                </Marker>
              ))}
            </MapContainer>
          ) : (
            <div>No spots to display</div>
          )}
        </div>
      </div>

      <div className="card">
        <h2>Most Visited Places</h2>
        <ul style={{ margin: 0, paddingLeft: 18 }}>
          {displayedSpots.length ? (
            displayedSpots.map((s, i) => (
              <li key={i}>
                <strong>{s.name}</strong> â€” {s.visitors} visitors
                {s.predicted_frequency && <> (Predicted: {s.predicted_frequency})</>}
              </li>
            ))
          ) : (
            <li>No spots found in CSV</li>
          )}
        </ul>
        <button onClick={generateRoute} disabled={loading}>
          {loading ? "Generating..." : "Generate Route"}
        </button>
      </div>
    </div>
  );
}
