import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline, useMap } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import { useNavigate, useLocation } from "react-router-dom";

const apiKeyORS = "AIzaSyA6D72kCqt2yln8Yhfg0Y0iE25F0ndZtO4"; // keep your API key

function MapUpdater({ center }) {
  const map = useMap();
  useEffect(() => {
    if (center) map.setView(center, 14, { animate: true });
  }, [center, map]);
  return null;
}

export default function RouteDetails() {
  const location = useLocation();
  const navigate = useNavigate();
  const routeSpots = location.state?.itinerary || [];
  const [currentIndex, setCurrentIndex] = useState(0);
  const [currentSegment, setCurrentSegment] = useState(null);
  const [loading, setLoading] = useState(true);

  const currentPOI = routeSpots[currentIndex];
  const nextPOI = routeSpots[currentIndex + 1];

  const fetchRouteSegment = async (from, to) => {
    if (!from || !to) return null;

    const profile = ["forest", "mountain", "trail"].some(t =>
      from.type?.toLowerCase().includes(t)
    ) ? "foot-hiking" : "driving-car";

    try {
      const res = await fetch(
        `https://api.openrouteservice.org/v2/directions/${profile}/geojson`,
        {
          method: "POST",
          headers: {
            "Authorization": apiKeyORS,
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ coordinates: [[from.lng, from.lat], [to.lng, to.lat]] }),
        }
      );
      const data = await res.json();
      const coords = data.features[0].geometry.coordinates.map(([lng, lat]) => [lat, lng]);
      const distance = data.features[0].properties.summary.distance; // meters
      const duration = data.features[0].properties.summary.duration; // seconds
      return { coords, distance, duration, isTrail: profile === "foot-hiking" };
    } catch (err) {
      console.error("Route fetch failed", err);
      return { coords: [[from.lat, from.lng], [to.lat, to.lng]], distance: 0, duration: 0, isTrail: false };
    }
  };

  useEffect(() => {
    const loadSegment = async () => {
      if (currentPOI && nextPOI) {
        setLoading(true);
        const segment = await fetchRouteSegment(currentPOI, nextPOI);
        setCurrentSegment(segment);
        setLoading(false);
      } else {
        setCurrentSegment(null);
        setLoading(false);
      }
    };
    loadSegment();
  }, [currentIndex]);

  const goNextPOI = () => {
    if (currentIndex < routeSpots.length - 1) setCurrentIndex(currentIndex + 1);
  };

  const finishRoute = () => {
    if (window.confirm("Finish route?")) navigate("/home");
  };

  if (!routeSpots.length) return <p>No route available.</p>;

  const formatDistance = (meters) => (meters / 1000).toFixed(2) + " km";
  const formatDuration = (seconds) => {
    const h = Math.floor(seconds / 3600);
    const m = Math.floor((seconds % 3600) / 60);
    return h ? `${h}h ${m}m` : `${m} min`;
  };

  return (
    <div style={{ display: "grid", gridTemplateColumns: "1.2fr 0.8fr", gap: 20, padding: 20, minHeight: "100vh" }}>
      <div style={{ height: "600px", borderRadius: 12, overflow: "hidden" }}>
        {loading ? (
          <p style={{ textAlign: "center", marginTop: "40%" }}>Loading route...</p>
        ) : (
          <MapContainer center={[currentPOI.lat, currentPOI.lng]} zoom={14} style={{ height: "100%", width: "100%" }}>
            <MapUpdater center={[currentPOI.lat, currentPOI.lng]} />
            <TileLayer
              attribution='&copy; <a href="https://www.openstreetmap.org/">OSM contributors</a>'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            {currentSegment && <Polyline positions={currentSegment.coords} color={currentSegment.isTrail ? "green" : "blue"} />}
            {[currentPOI, nextPOI].filter(Boolean).map((poi, i) => (
              <Marker key={i} position={[poi.lat, poi.lng]}>
                <Popup>
                  <strong>{poi.name}</strong>
                  <p>{poi.description}</p>
                  {poi.image && <img src={poi.image} alt={poi.name} style={{ width: 150, borderRadius: 8 }} />}
                </Popup>
              </Marker>
            ))}
          </MapContainer>
        )}
      </div>

      <div style={{ background: "#fff", borderRadius: 12, padding: 20 }}>
        <h2>Current POI</h2>
        <p><strong>{currentPOI.name}</strong></p>
        <p>{currentPOI.description}</p>
        {currentPOI.image && <img src={currentPOI.image} alt={currentPOI.name} style={{ width: "100%", maxWidth: 280, borderRadius: 10 }} />}
        {currentSegment && (
          <p style={{ marginTop: 10 }}>
            Distance: {formatDistance(currentSegment.distance)} <br />
            Duration: {formatDuration(currentSegment.duration)}
          </p>
        )}
        <p style={{ marginTop: 20 }}>POI {currentIndex + 1} of {routeSpots.length}</p>

        <div style={{ marginTop: 20, display: "flex", gap: 10 }}>
          {currentIndex < routeSpots.length - 1 && <button onClick={goNextPOI}>Next POI</button>}
          {currentIndex === routeSpots.length - 1 && <button onClick={finishRoute}>Finish Route</button>}
        </div>
      </div>
    </div>
  );
}
