import React, { useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";

export default function MapPage() {
  const [selectedLocation, setSelectedLocation] = useState(null);

  // Example locations (you can add real Majayjay spots later)
  const locations = [
    {
      name: "Taytay Falls",
      position: [14.1466, 121.4933],
      description: "A hidden gem waterfall surrounded by lush forests."
    },
    {
      name: "Majayjay Church",
      position: [14.1479, 121.4972],
      description: "One of the oldest churches in Laguna, built during the Spanish era."
    }
  ];

  return (
    <div className="map-page" style={{ display: "flex", height: "100vh" }}>
      {/* Map Section */}
      <div
        style={{
          flex: selectedLocation ? 0.6 : 1,
          transition: "flex 0.3s ease"
        }}
      >
        <MapContainer center={[14.1466, 121.4933]} zoom={13} style={{ height: "100%", width: "100%" }}>
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution="&copy; OpenStreetMap contributors"
          />
          {locations.map((loc, idx) => (
            <Marker
              key={idx}
              position={loc.position}
              eventHandlers={{
                click: () => {
                  setSelectedLocation(loc);
                }
              }}
            >
              <Popup>{loc.name}</Popup>
            </Marker>
          ))}
        </MapContainer>
      </div>

      {/* Info Panel */}
      {selectedLocation && (
        <div
          style={{
            flex: 0.4,
            padding: "20px",
            background: "#f9f9f9",
            borderLeft: "2px solid #ddd",
            display: "flex",
            flexDirection: "column",
            justifyContent: "space-between"
          }}
        >
          <div>
            <h2>{selectedLocation.name}</h2>
            <p>{selectedLocation.description}</p>
          </div>
          <button
            onClick={() => setSelectedLocation(null)}
            style={{
              padding: "10px",
              border: "none",
              background: "#007bff",
              color: "#fff",
              borderRadius: "8px",
              cursor: "pointer"
            }}
          >
            Minimize
          </button>
        </div>
      )}
    </div>
  );
}
