import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import "../leafletfix"; // fix marker icons
import Papa from "papaparse";

const CENTER = { lat: 14.1467, lng: 121.4708 };

export default function MapPage() {
  const [spots, setSpots] = useState([]);

  useEffect(() => {
    Papa.parse("/spots.csv", {
      download: true,
      header: true,
      complete: (res) => {
        const rows = res.data
          .filter((r) => r.name && r.lat && r.lng)
          .map((r) => ({
            name: r.name,
            lat: parseFloat(r.lat),
            lng: parseFloat(r.lng),
            tags: (r.tags || "").split(",").map((t) => t.trim()).filter(Boolean),
            visitors: r.visitors || "Moderate",
          }));
        setSpots(rows);
      },
    });
  }, []);

  return (
    <div className="card" style={{ padding: 0 }}>
      <div className="map-full">
        <MapContainer center={CENTER} zoom={13} className="h-full w-full" style={{ height: "100%", width: "100%" }}>
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/">OSM contributors</a>'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          {spots.map((s, i) => (
            <Marker key={i} position={[s.lat, s.lng]}>
              <Popup>
                <strong>{s.name}</strong>
                {s.tags?.length ? (
                  <>
                    <br />
                    <small>Tags: {s.tags.join(", ")}</small>
                  </>
                ) : null}
                <br />
                <small>Visitors: {s.visitors}</small>
              </Popup>
            </Marker>
          ))}
        </MapContainer>
      </div>
    </div>
  );
}
