import React, { useEffect, useState } from "react";

function toTitle(s) {
  if (!s && s !== 0) return "";
  return s.toString().trim().toLowerCase().replace(/\b\w/g, (c) => c.toUpperCase());
}

export default function ProfilePage() {
  const storedUser = JSON.parse(localStorage.getItem("user"));
  const token = localStorage.getItem("token");

  const [user, setUser] = useState(null);
  const [preferences, setPreferences] = useState([]);
  const [editing, setEditing] = useState(false);

  const preferenceOptions = ["Nature", "Culture", "Food", "History", "Adventure"];

  useEffect(() => {
    if (!storedUser?.email) return;
    const fetchUser = async () => {
      try {
        const res = await fetch(
          `http://localhost:5000/api/users/email/${encodeURIComponent(storedUser.email)}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        if (!res.ok) {
          console.error("Profile fetch failed", res.status);
          return;
        }
        const data = await res.json();
        setUser(data);

        let prefs = [];
        if (Array.isArray(data.preferences)) {
          prefs = data.preferences.map((p) => toTitle(p));
        } else if (typeof data.preferences === "string" && data.preferences.trim() !== "") {
          try {
            const parsed = JSON.parse(data.preferences);
            if (Array.isArray(parsed)) prefs = parsed.map((p) => toTitle(p));
            else prefs = data.preferences.split(",").map((p) => toTitle(p));
          } catch {
            prefs = data.preferences.split(",").map((p) => toTitle(p));
          }
        }
        setPreferences(prefs);
        console.log("Loaded user preferences:", prefs);
      } catch (err) {
        console.error("Error fetching user:", err);
      }
    };
    fetchUser();
  }, [storedUser, token]);

  const togglePref = (option) => {
    const key = toTitle(option);
    setPreferences((prev) => (prev.includes(key) ? prev.filter((p) => p !== key) : [...prev, key]));
  };

  const handleSave = async () => {
    if (!user) return;
    try {
      const res = await fetch(`http://localhost:5000/api/users/${user.id}/preferences`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ preferences }),
      });
      const json = await res.json();
      if (!res.ok) {
        console.error("Save failed:", json);
        alert("Failed to save preferences: " + (json.message || res.status));
        return;
      }
      setEditing(false);
      const updatedUser = { ...user, preferences };
      setUser(updatedUser);
      const stored = JSON.parse(localStorage.getItem("user")) || {};
      stored.preferences = preferences;
      localStorage.setItem("user", JSON.stringify(stored));
      alert("Preferences saved");
    } catch (err) {
      console.error("Error saving preferences:", err);
      alert("Error saving preferences");
    }
  };

  if (!user) return <div style={{ padding: 24 }}>Loading...</div>;

  return (
    <div style={{ padding: 24, maxWidth: 600, margin: "0 auto" }}>
      <h1 style={{ fontSize: "1.8rem", fontWeight: "bold", marginBottom: 16 }}>Profile</h1>

      <div style={{ border: "1px solid #ddd", borderRadius: 16, padding: 20, background: "#fff", boxShadow: "0 2px 6px rgba(0,0,0,0.1)" }}>
        <p><strong>Username:</strong> {user.username}</p>
        <p><strong>Email:</strong> {user.email}</p>
        <p><strong>Full Name:</strong> {`${user.first_name || ""} ${user.middle_name || ""} ${user.last_name || ""}`.trim()}</p>
        <p><strong>Age:</strong> {user.age || "Not provided"}</p>

        <div style={{ marginTop: 16 }}>
          <strong>Preferences:</strong>
          {editing ? (
            <div style={{ marginTop: 8 }}>
              {preferenceOptions.map((option) => {
                const checked = preferences.includes(toTitle(option));
                return (
                  <label key={option} style={{ display: "block", marginTop: 6, cursor: "pointer" }}>
                    <input
                      type="checkbox"
                      checked={checked}
                      onChange={() => togglePref(option)}
                      style={{ cursor: "pointer" }}
                    />
                    <span style={{ marginLeft: 8 }}>{option}</span>
                  </label>
                );
              })}
              <div style={{ marginTop: 12 }}>
                <button
                  onClick={handleSave}
                  style={{ background: "#007bff", color: "#fff", padding: "8px 16px", border: "none", borderRadius: 8 }}
                >
                  Save
                </button>
                <button
                  onClick={() => {
                    setEditing(false);
                    const original = Array.isArray(user.preferences) ? user.preferences : [];
                    setPreferences(original.map((p) => toTitle(p)));
                  }}
                  style={{ background: "#ccc", padding: "8px 16px", border: "none", borderRadius: 8, marginLeft: 8 }}
                >
                  Cancel
                </button>
              </div>
            </div>
          ) : (
            <ul style={{ marginTop: 8 }}>
              {Array.isArray(user.preferences) && user.preferences.length > 0 ? (
                user.preferences.map((p, i) => <li key={i}>{toTitle(p)}</li>)
              ) : (
                <li>No preferences set.</li>
              )}
            </ul>
          )}
        </div>

        {!editing && (
          <button
            onClick={() => setEditing(true)}
            style={{ marginTop: 16, background: "#28a745", color: "#fff", padding: "8px 16px", border: "none", borderRadius: 8 }}
          >
            Edit Preferences
          </button>
        )}
      </div>
    </div>
  );
}
