import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import Papa from "papaparse";

const CENTER = { lat: 14.1467, lng: 121.4708 };

export default function MapPage() {
  const [spots, setSpots] = useState([]);

  useEffect(() => {
    fetch("/data/spots.csv")
      .then((res) => res.text())
      .then((csv) => {
        Papa.parse(csv, {
          header: true,
          skipEmptyLines: true,
          complete: (res) => {
            const rows = res.data
              .filter((r) => r.name && r.lat && r.lng)
              .map((r) => ({
                name: r.name,
                lat: parseFloat(r.lat),
                lng: parseFloat(r.lng),
                description: r.description || "No description available",
                visitors: r.visitors || "Moderate",
              }));
            setSpots(rows);
          },
        });
      });
  }, []);

  return (
    <div style={{ height: "80vh", width: "100%" }}>
      <MapContainer center={CENTER} zoom={13} style={{ height: "100%", width: "100%" }}>
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/">OSM contributors</a>'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        {spots.map((s, i) => (
          <Marker key={i} position={[s.lat, s.lng]}>
            <Popup>
              <strong>{s.name}</strong>
              <br />
              Visitors: {s.visitors}
              <br />
              {s.description}
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
}
