import React from "react";

export default function RoutePlanner({ spots, setRoute, weather }) {
  const handleGenerateRoute = async () => {
    if (!spots.length) return alert("No spots to generate route!");

    const user = JSON.parse(localStorage.getItem("user"));
    const preferences = user?.preferences || {};

    const scoredSpots = spots.map((spot) => {
      let score = 0;

      if (preferences.nature && spot.tags.includes("nature")) score += 3;
      if (preferences.culture && spot.tags.includes("culture")) score += 3;

      if (weather === "Sunny" && spot.tags.includes("nature")) score += 2;
      if (weather === "Rainy" && spot.tags.includes("indoor")) score += 2;

      switch (spot.visitors) {
        case "Very High":
          score += 1;
          break;
        case "High":
          score += 2;
          break;
        case "Moderate":
          score += 3;
          break;
        case "Low":
          score += 4;
          break;
        default:
          score += 2;
      }

      return { ...spot, score };
    });

    scoredSpots.sort((a, b) => b.score - a.score);
    setRoute(scoredSpots);
  };

  return (
    <button
      onClick={handleGenerateRoute}
      style={{
        marginTop: 12,
        padding: "10px 16px",
        backgroundColor: "#10b981",
        color: "#fff",
        border: "none",
        borderRadius: "8px",
        cursor: "pointer",
      }}
    >
      Generate Recommended Route
    </button>
  );
}
