import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import { useNavigate, useLocation } from "react-router-dom";

export default function RouteDetails() {
  const location = useLocation();
  const navigate = useNavigate();

  // Source itinerary:
  // - location.state?.itinerary (object from profile)
  // - or location.state?.id (an id was passed)
  const passedItinerary = location.state?.itinerary || null;
  const passedId = location.state?.id || null;

  const [routeSpots, setRouteSpots] = useState([]); // array of { lat, lng, name, id? }
  const [itineraryId, setItineraryId] = useState(passedItinerary?.id || passedId || null);
  const [status, setStatus] = useState("pending");
  const [poiDetails, setPoiDetails] = useState([]); // enriched POI info from /api/pois
  const [segments, setSegments] = useState([]); // ORS segments between POIs
  const [startSegment, setStartSegment] = useState(null); // segment from user -> first POI when "Start Tour" clicked
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [saveMessage, setSaveMessage] = useState("");
  const [userLocation, setUserLocation] = useState(null);

  const mapRef = useRef(null);

  // Replace with your ORS key you used elsewhere (kept same format as your code)
  const ORS_API_KEY = "eyJvcmciOiI1YjNjZTM1OTc4..."; // truncated placeholder; keep your full key here

  // Leaflet icon creators
  const numberedIcon = (number) =>
    L.divIcon({
      html: `<div style="
        background:#1976d2;color:#fff;width:28px;height:28px;
        border-radius:50%;text-align:center;line-height:28px;
        font-weight:600;border:2px solid #fff;box-shadow:0 2px 6px rgba(0,0,0,0.3);
      ">${number}</div>`,
      className: "",
      iconSize: [28, 28],
    });

  const userIcon = L.divIcon({
    html: `<div style="
      background:#4caf50;color:#fff;width:30px;height:30px;
      border-radius:50%;text-align:center;line-height:30px;
      font-weight:700;border:2px solid #fff;box-shadow:0 2px 8px rgba(0,0,0,0.3);
    ">&nbsp;</div>`,
    className: "",
    iconSize: [30, 30],
  });

  // get current user location once
  useEffect(() => {
    if (!navigator.geolocation) return;
    navigator.geolocation.getCurrentPosition(
      (pos) => setUserLocation({ lat: pos.coords.latitude, lng: pos.coords.longitude }),
      (err) => {
        console.warn("Location error:", err);
        setUserLocation(null);
      },
      { enableHighAccuracy: true }
    );
  }, []);

  // if itinerary object passed in state, use it; otherwise fetch itinerary by id
  useEffect(() => {
    const token = localStorage.getItem("token");

    const initFromPassed = async () => {
      if (passedItinerary) {
        // If itinerary object contains route_data as parsed array or string
        const rd = Array.isArray(passedItinerary.route_data)
          ? passedItinerary.route_data
          : (() => {
              try {
                return JSON.parse(passedItinerary.route_data);
              } catch {
                return passedItinerary.route_data || [];
              }
            })();
        setRouteSpots(rd || []);
        setItineraryId(passedItinerary.id || null);
        setStatus(passedItinerary.status || "pending");
        setLoading(false);
      } else if (passedId && token) {
        // fetch itinerary from backend
        try {
          const res = await fetch(`http://localhost:5000/api/itineraries/${passedId}`, {
            headers: { Authorization: `Bearer ${token}` },
          });
          const data = await res.json();
          if (res.ok && data.success && data.itinerary) {
            const it = data.itinerary;
            setRouteSpots(it.route_data || []);
            setItineraryId(it.id);
            setStatus(it.status || "pending");
          } else {
            // API returned error shape
            console.warn("Could not load itinerary:", data);
            setSaveMessage("Could not load itinerary details.");
          }
        } catch (err) {
          console.error("Failed fetching itinerary:", err);
          setSaveMessage("Could not load itinerary details.");
        } finally {
          setLoading(false);
        }
      } else {
        // no itinerary provided
        setLoading(false);
      }
    };

    initFromPassed();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [passedItinerary, passedId]);

  // fetch POI master list and match to routeSpots (to get descriptions/images)
  const fetchPoiDetails = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/pois");
      const data = await res.json();
      // map each route spot to full POI if available
      const matched = routeSpots.map((spot) =>
        data.find((poi) => (poi.id && spot.id && poi.id === spot.id) || (poi.name && poi.name === spot.name)) || spot
      );
      setPoiDetails(matched);
    } catch (err) {
      console.error("Error fetching POIs:", err);
      setPoiDetails(routeSpots);
    }
  };

  // helper: requests ORS directions (driving-car) and returns coords and summary
  const fetchDirections = async (from, to) => {
    try {
      const res = await fetch("https://api.openrouteservice.org/v2/directions/driving-car/geojson", {
        method: "POST",
        headers: { Authorization: ORS_API_KEY, "Content-Type": "application/json" },
        body: JSON.stringify({
          coordinates: [
            [from.lng, from.lat],
            [to.lng, to.lat],
          ],
          preference: "fastest",
          instructions: false,
        }),
      });
      const data = await res.json();
      if (data.features?.length) {
        const coords = data.features[0].geometry.coordinates.map(([lng, lat]) => [lat, lng]);
        const summary = data.features[0].properties.summary || { distance: 0, duration: 0 };
        return { coords, summary };
      }
    } catch (err) {
      console.warn("Directions error:", err);
    }
    // fallback: straight line
    return { coords: [[from.lat, from.lng], [to.lat, to.lng]], summary: { distance: 0, duration: 0 } };
  };

  // build segments between POIs (existing behavior)
  useEffect(() => {
    const loadSegments = async () => {
      if (!routeSpots || routeSpots.length === 0) return;
      setLoading(true);
      await fetchPoiDetails();
      const segs = [];
      for (let i = 0; i < routeSpots.length - 1; i++) {
        const from = routeSpots[i];
        const to = routeSpots[i + 1];
        if (!from || !to) continue;
        const seg = await fetchDirections(from, to);
        if (seg) segs.push(seg);
      }
      setSegments(segs);
      setLoading(false);
    };
    loadSegments();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [routeSpots]);

  // If itineraryId exists, fetch its latest status from DB (keeps status in sync)
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!itineraryId || !token) return;

    const fetchStatus = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/itineraries/${itineraryId}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        const data = await res.json();
        if (res.ok && data.success && data.itinerary) {
          setStatus(data.itinerary.status || "pending");
        } else if (res.ok && data.itinerary && data.itinerary.status) {
          // sometimes backend might return shape directly
          setStatus(data.itinerary.status);
        } else if (data.status) {
          setStatus(data.status);
        } else {
          // fallback: do nothing
        }
      } catch (err) {
        console.error("Failed to fetch itinerary status:", err);
      }
    };

    fetchStatus();
  }, [itineraryId]);

  // Save itinerary (POST) — sets itineraryId from server response
  const handleSaveItinerary = async () => {
    const token = localStorage.getItem("token");
    if (!token) {
      alert("Please log in to save itineraries.");
      return;
    }
    setSaving(true);
    setSaveMessage("");
    try {
      const res = await fetch("http://localhost:5000/api/itineraries", {
        method: "POST",
        headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
        body: JSON.stringify({ name: `Route - ${new Date().toLocaleDateString()}`, route_data: routeSpots }),
      });
      const data = await res.json();
      if (res.ok && data.id) {
        setItineraryId(data.id);
        // If backend returns itinerary object, use its status
        const returnedStatus = (data.itinerary && data.itinerary.status) || "pending";
        setStatus(returnedStatus);
        setSaveMessage("Itinerary saved successfully.");
      } else {
        setSaveMessage(`Failed to save: ${data.message || "Unknown error"}`);
      }
    } catch (err) {
      console.error("Save error:", err);
      setSaveMessage("Server error while saving itinerary.");
    } finally {
      setSaving(false);
      setTimeout(() => setSaveMessage(""), 4000);
    }
  };

  // Update itinerary status (PUT)
  const updateStatus = async (newStatus) => {
    if (!itineraryId) {
      alert("Please save itinerary first.");
      return;
    }
    const token = localStorage.getItem("token");
    if (!token) {
      alert("Please log in to update status.");
      return;
    }
    try {
      const res = await fetch(`http://localhost:5000/api/itineraries/${itineraryId}/status`, {
        method: "PUT",
        headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
        body: JSON.stringify({ status: newStatus }),
      });
      const data = await res.json();
      if (res.ok) {
        setStatus(newStatus);
        setSaveMessage(`Itinerary marked as ${newStatus}.`);
      } else {
        setSaveMessage(`Failed to update: ${data.message || "Unknown error"}`);
      }
    } catch (err) {
      console.error("Update status error:", err);
      setSaveMessage("Server error while updating status.");
    } finally {
      setTimeout(() => setSaveMessage(""), 4000);
    }
  };

  // "Start Tour" — request directions from userLocation -> first POI and draw it
  const handleStartTour = async () => {
    if (!userLocation) {
      alert("Unable to read your location. Allow location permission and try again.");
      return;
    }
    if (!routeSpots || routeSpots.length === 0) {
      alert("Route has no stops.");
      return;
    }
    const first = routeSpots[0];
    const seg = await fetchDirections(userLocation, first);
    if (seg) {
      setStartSegment(seg);
      // zoom and fit
      if (mapRef.current) {
        const all = seg.coords;
        mapRef.current.fitBounds(all.map((c) => [c[0], c[1]]), { padding: [40, 40] });
      }
    } else {
      setSaveMessage("Could not get directions to the first stop.");
      setTimeout(() => setSaveMessage(""), 3000);
    }
  };

  if (loading) return <p style={{ padding: 24 }}>Loading route...</p>;
  if (!routeSpots || routeSpots.length === 0)
    return (
      <div style={{ padding: 24 }}>
        <p>No route data available.</p>
        <button className="btn-back" onClick={() => navigate("/home")}>
          Back to Home
        </button>
      </div>
    );

  // Use center = userLocation if available else first POI
  const center = userLocation ? [userLocation.lat, userLocation.lng] : [routeSpots[0].lat, routeSpots[0].lng];

  return (
    <div className="route-details-container" style={{ display: "grid", gridTemplateColumns: "1.3fr 0.7fr", gap: 24, padding: 24 }}>
      {/* Map */}
      <div className="map-section" style={{ height: 650, borderRadius: 18, overflow: "hidden", boxShadow: "0 10px 25px rgba(0,0,0,0.15)", border: "4px solid #fff" }}>
        <MapContainer
          ref={mapRef}
          center={center}
          zoom={13}
          style={{ height: "100%", width: "100%" }}
          whenCreated={(mapInstance) => (mapRef.current = mapInstance)}
        >
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" attribution="© OpenStreetMap" />

          {userLocation && <Marker position={[userLocation.lat, userLocation.lng]} icon={userIcon}><Popup>Your location</Popup></Marker>}

          {/* startSegment (user -> first POI) */}
          {startSegment && <Polyline positions={startSegment.coords} color="#ff9800" weight={6} opacity={0.9} />}

          {/* segments between POIs */}
          {segments.map((seg, i) => (
            <Polyline key={`seg-${i}`} positions={seg.coords} color="#1976d2" weight={5} opacity={0.9} />
          ))}

          {/* POI markers */}
          {routeSpots.map((poi, i) => (
            <Marker key={`poi-${i}`} position={[poi.lat, poi.lng]} icon={numberedIcon(i + 1)}>
              <Popup>
                <strong>
                  Stop {i + 1}: {poi.name || poiDetails[i]?.name || "Unnamed"}
                </strong>
                <div style={{ marginTop: 6 }}>
                  <div style={{ marginBottom: 6 }}>{poiDetails[i]?.description || poi.description || "No description available."}</div>
                  {poiDetails[i]?.image && <img src={`http://localhost:5000/images/${poiDetails[i].image}`} alt={poiDetails[i].name || poi.name} style={{ width: 160, borderRadius: 8 }} />}
                </div>
              </Popup>
            </Marker>
          ))}
        </MapContainer>
      </div>

      {/* Sidebar */}
      <div className="sidebar-section" style={{ background: "#fff", borderRadius: 18, padding: 24, boxShadow: "0 8px 24px rgba(0,0,0,0.1)", border: "1px solid #e0e0e0", overflowY: "auto", maxHeight: 650 }}>
        <h2 style={{ color: "#1565c0", fontWeight: 700, marginBottom: 12 }}>Route Overview</h2>

        <div
          className={`status-label ${status === "finished" ? "status-finished" : status === "cancelled" ? "status-cancelled" : "status-pending"}`}
          style={{
            padding: "8px 12px",
            borderRadius: 8,
            fontWeight: 600,
            marginBottom: 16,
            background: status === "finished" ? "#d1fae5" : status === "cancelled" ? "#fee2e2" : "#fef9c3",
            color: status === "finished" ? "#065f46" : status === "cancelled" ? "#991b1b" : "#92400e",
          }}
        >
          Status: {status?.toUpperCase()}
        </div>

        {poiDetails.map((poi, i) => {
          const seg = segments[i];
          return (
            <div key={i} className="poi-item" onClick={() => mapRef.current && mapRef.current.setView([poi.lat, poi.lng], 16)} style={{ marginBottom: 20, borderBottom: "1px solid #e0e0e0", paddingBottom: 16, cursor: "pointer" }}>
              <h3 style={{ color: "#1976d2", fontWeight: 600 }}>Stop {i + 1}: {poi?.name || routeSpots[i]?.name}</h3>
              <p style={{ color: "#444" }}>{poi?.description || "No description available."}</p>
              {poi?.image && <img src={`http://localhost:5000/images/${poi.image}`} alt={poi.name} className="poi-image" style={{ width: "100%", maxWidth: 280, borderRadius: 10, boxShadow: "0 6px 12px rgba(0,0,0,0.1)", marginBottom: 8 }} />}
              <p style={{ margin: "6px 0" }}><strong>Visitors:</strong> {poi.visitors || "N/A"}<br /><strong>Tags:</strong> {poi.tags || "None"}</p>
              {seg && <p style={{ margin: 0 }}><strong>To next stop:</strong> {(seg.summary && `${(seg.summary.distance/1000).toFixed(2)} km, ${Math.floor(seg.summary.duration/60)} min`) || "N/A"}</p>}
            </div>
          );
        })}

        {/* Buttons */}
        {status === "pending" ? (
          <>
            <button onClick={handleSaveItinerary} disabled={saving} className="btn-primary" style={{ width: "100%", padding: "12px 20px", background: saving ? "#ccc" : "#1976d2", color: "#fff", border: "none", borderRadius: 8, fontWeight: 600, cursor: "pointer", marginTop: 12 }}>
              {saving ? "Saving..." : "Save This Route"}
            </button>

            <button onClick={() => updateStatus("finished")} className="btn-success" style={{ width: "100%", padding: "12px 20px", background: "#4caf50", color: "#fff", border: "none", borderRadius: 8, fontWeight: 600, cursor: "pointer", marginTop: 10 }}>
              Mark as Finished
            </button>

            <button onClick={() => updateStatus("cancelled")} className="btn-cancel" style={{ width: "100%", padding: "12px 20px", background: "#f44336", color: "#fff", border: "none", borderRadius: 8, fontWeight: 600, cursor: "pointer", marginTop: 10 }}>
              Cancel Itinerary
            </button>

            {/* Start Tour button (user -> first POI) */}
            <button onClick={handleStartTour} style={{ width: "100%", padding: "12px 20px", background: "#ff9800", color: "#fff", border: "none", borderRadius: 8, fontWeight: 600, cursor: "pointer", marginTop: 10 }}>
              Start Tour (navigate from current location to first stop)
            </button>
          </>
        ) : (
          // when finished or cancelled only show back button
          <div>
            <button onClick={() => navigate("/home")} className="btn-back" style={{ width: "100%", padding: "12px 20px", background: "#9e9e9e", color: "#fff", border: "none", borderRadius: 8, fontWeight: 600, cursor: "pointer", marginTop: 10 }}>
              Back to Home
            </button>
          </div>
        )}

        {saveMessage && <p className="save-message" style={{ marginTop: 10, textAlign: "center", color: "#1565c0" }}>{saveMessage}</p>}
      </div>
    </div>
  );
}
