import React, { useEffect, useMemo, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import "../leafletfix"; // fix marker icons
import Papa from "papaparse";

const CENTER = { lat: 14.1467, lng: 121.4708 };

export default function Home() {
  const [spots, setSpots] = useState([]);

  useEffect(() => {
    Papa.parse("/src/data/spots.csv", {
      download: true,
      header: true,
      complete: (res) => {
        const rows = res.data
          .filter((r) => r.name && r.lat && r.lng)
          .map((r) => ({
            name: r.name,
            lat: parseFloat(r.lat),
            lng: parseFloat(r.lng),
            visitors: r.visitors || "Moderate",
            image: r.image || "", // ðŸ‘ˆ add image
          }));
        setSpots(rows);
      },
    });
  }, []);

  const topSpots = useMemo(() => {
    const order = { "Very High": 3, High: 2, Moderate: 1, Low: 0 };
    return [...spots]
      .sort((a, b) => (order[b.visitors] || 0) - (order[a.visitors] || 0))
      .slice(0, 5);
  }, [spots]);

  return (
    <div className="grid" style={{ display: "grid", gap: 16, gridTemplateColumns: "1fr 1fr" }}>
      <div className="card">
        <h2 style={{ margin: "0 0 8px" }}>Majayjay Overview</h2>
        <div className="map-wrap" style={{ height: "500px" }}>
          <MapContainer center={CENTER} zoom={13} className="h-full w-full" style={{ height: "100%", width: "100%" }}>
            <TileLayer
              attribution='&copy; <a href="https://www.openstreetmap.org/">OSM contributors</a>'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            {topSpots.map((s, i) => (
              <Marker key={i} position={[s.lat, s.lng]}>
                <Popup>
                  <strong>{s.name}</strong>
                  <br />
                  Visitors: {s.visitors}
                  {s.image && (
                    <>
                      <br />
                      <img
                        src={`/images/${s.image}`} // ðŸ‘ˆ images stored in public/images/
                        alt={s.name}
                        style={{ width: "150px", borderRadius: "8px", marginTop: "4px" }}
                      />
                    </>
                  )}
                </Popup>
              </Marker>
            ))}
          </MapContainer>
        </div>
      </div>

      <div className="card">
        <h2 style={{ margin: "0 0 8px" }}>Most Visited Places</h2>
        <ul style={{ margin: 0, paddingLeft: 18 }}>
          {topSpots.map((s, i) => (
            <li key={i}>
              <strong>{s.name}</strong> â€” {s.visitors} visitors
            </li>
          ))}
          {topSpots.length === 0 && <li>No spots found in spots.csv</li>}
        </ul>
      </div>
    </div>
  );
}
