import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import { useNavigate, useLocation } from "react-router-dom";
import "./RouteDetails.css";

export default function RouteDetails() {
  const location = useLocation();
  const navigate = useNavigate();
  const routeSpots = location.state?.itinerary || [];
  const itineraryIdFromState = location.state?.id || null;

  const [segments, setSegments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [poiDetails, setPoiDetails] = useState([]);
  const [saving, setSaving] = useState(false);
  const [saveMessage, setSaveMessage] = useState("");
  const [status, setStatus] = useState("pending");
  const [userLocation, setUserLocation] = useState(null);
  const [itineraryId, setItineraryId] = useState(itineraryIdFromState);

  const mapRef = useRef(null);
  const ORS_API_KEY = "YOUR_API_KEY_HERE";

  const numberedIcon = (number) =>
    L.divIcon({ html: `<div class="icon-number">${number}</div>`, className: "", iconSize: [28, 28] });

  const userIcon = L.divIcon({ html: `<div class="icon-user">üßç</div>`, className: "", iconSize: [30, 30] });

  useEffect(() => {
    navigator.geolocation?.getCurrentPosition(
      (pos) => setUserLocation({ lat: pos.coords.latitude, lng: pos.coords.longitude }),
      (err) => console.warn("Location error:", err)
    );
  }, []);

  // Fetch POIs & segments...
  useEffect(() => {
    const loadAllSegments = async () => {
      setLoading(true);
      // fetch POIs and segments logic here
      setLoading(false);
    };
    if (routeSpots.length > 0) loadAllSegments();
  }, [routeSpots]);

  const handleZoomToPoi = (poi) => {
    if (mapRef.current) mapRef.current.setView([poi.lat, poi.lng], 16, { animate: true });
  };

  if (!routeSpots.length || !userLocation) return <p>Loading route...</p>;

  return (
    <div className="route-details-container">
      <div className="map-section">
        <MapContainer ref={mapRef} center={[userLocation.lat, userLocation.lng]} zoom={13} className="route-map">
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" attribution="¬© OpenStreetMap" />
          <Marker position={[userLocation.lat, userLocation.lng]} icon={userIcon}><Popup>You are here</Popup></Marker>
          {segments.map((seg, i) => <Polyline key={i} positions={seg.coords} className="route-polyline" />)}
          {routeSpots.map((poi, i) => {
            const details = poiDetails[i];
            return (
              <Marker key={i} position={[poi.lat, poi.lng]} icon={numberedIcon(i + 1)}>
                <Popup>
                  <strong>Stop {i + 1}: {details?.name || poi.name}</strong>
                  <p>{details?.description || "No description available."}</p>
                  {details?.image && <img src={`http://localhost:5000/images/${details.image}`} alt={details.name} className="poi-image" />}
                </Popup>
              </Marker>
            );
          })}
        </MapContainer>
      </div>

      <div className="sidebar-section">
        <h2>Route Overview</h2>
        <p className={`status-label status-${status}`}>Status: {status.toUpperCase()}</p>
        {poiDetails.map((poi, i) => (
          <div key={i} className="poi-item" onClick={() => handleZoomToPoi(routeSpots[i])}>
            <h3>Stop {i + 1}: {poi?.name}</h3>
            <p>{poi?.description}</p>
            {poi?.image && <img src={`http://localhost:5000/images/${poi.image}`} alt={poi.name} className="poi-image" />}
          </div>
        ))}
        <button className="btn-primary" onClick={() => {}}>üíæ Save This Route</button>
        <button className="btn-success" onClick={() => {}}>‚úÖ Mark as Finished</button>
        <button className="btn-cancel" onClick={() => {}}>‚ùå Cancel Itinerary</button>
        <button className="btn-back" onClick={() => navigate("/home")}>Back to Home</button>
        {saveMessage && <p className="save-message">{saveMessage}</p>}
      </div>
    </div>
  );
}
