import React, { useEffect, useState } from "react";
import Papa from "papaparse";

export default function EstablishmentsPage() {
  const [lodgings, setLodgings] = useState([]);
  const [restaurants, setRestaurants] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null); // For modal

  useEffect(() => {
    fetch("/data/spots.csv")
      .then((res) => res.text())
      .then((csv) => {
        Papa.parse(csv, {
          header: true,
          skipEmptyLines: true,
          complete: (res) => {
            const rows = res.data.map((r) => ({
              ...r,
              image: r.image || null,
              tags: r.tags ? r.tags.toLowerCase() : "",
            }));

            const lodgingsFiltered = rows.filter((r) =>
              r.tags.includes("resort") ||
              r.tags.includes("lodge") ||
              r.tags.includes("motel") ||
              r.tags.includes("hotel")
            );

            const restaurantsFiltered = rows.filter((r) =>
              r.tags.includes("restaurant") ||
              r.tags.includes("coffee") ||
              r.tags.includes("cafe")
            );

            setLodgings(lodgingsFiltered);
            setRestaurants(restaurantsFiltered);
          },
        });
      });
  }, []);

  const renderCards = (items) => (
    <div style={{
      display: "grid",
      gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
      gap: "20px"
    }}>
      {items.map((item, i) => (
        <div key={i} 
             style={{
               border: "1px solid #ddd",
               borderRadius: "15px",
               overflow: "hidden",
               backgroundColor: "#fff",
               boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
               cursor: "pointer"
             }}
             onClick={() => setSelectedItem(item)}>
          {item.image ? (
            <img
              src={item.image}
              alt={item.name}
              style={{ width: "100%", height: "200px", objectFit: "cover" }}
            />
          ) : (
            <div style={{
              width: "100%",
              height: "200px",
              backgroundColor: "#f0f0f0",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              color: "#888"
            }}>
              No image
            </div>
          )}
          <div style={{ padding: "15px" }}>
            <h3 style={{ fontSize: "18px", fontWeight: "bold", marginBottom: "8px" }}>{item.name}</h3>
            <p style={{ fontSize: "14px", color: "#555" }}>{item.description}</p>
          </div>
        </div>
      ))}
    </div>
  );

  const renderModal = () => {
    if (!selectedItem) return null;
    return (
      <div style={{
        position: "fixed",
        top: 0, left: 0,
        width: "100%",
        height: "100%",
        backgroundColor: "rgba(0,0,0,0.5)",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        zIndex: 1000
      }}
      onClick={() => setSelectedItem(null)}>
        <div style={{
          backgroundColor: "#fff",
          borderRadius: "15px",
          padding: "30px",
          maxWidth: "600px",
          width: "90%",
          maxHeight: "80%",
          overflowY: "auto",
          position: "relative"
        }}
        onClick={(e) => e.stopPropagation()}>
          <button onClick={() => setSelectedItem(null)} 
                  style={{
                    position: "absolute",
                    top: "15px",
                    right: "15px",
                    border: "none",
                    background: "none",
                    fontSize: "20px",
                    cursor: "pointer"
                  }}>âœ•</button>
          {selectedItem.image && (
            <img src={selectedItem.image} alt={selectedItem.name} 
                 style={{ width: "100%", borderRadius: "10px", marginBottom: "20px" }} />
          )}
          <h2 style={{ fontSize: "24px", fontWeight: "bold", marginBottom: "15px" }}>{selectedItem.name}</h2>
          <p style={{ fontSize: "16px", color: "#555", marginBottom: "15px" }}>{selectedItem.description}</p>
          {selectedItem.visitors && <p><strong>Visitors:</strong> {selectedItem.visitors}</p>}
          {selectedItem.tags && <p><strong>Tags:</strong> {selectedItem.tags}</p>}
          {/* You can add more fields like Opening hours here */}
        </div>
      </div>
    )
  };

  return (
    <div style={{ padding: "30px" }}>
      <h1 style={{ fontSize: "28px", fontWeight: "bold", marginBottom: "20px" }}>Establishments</h1>

      <section style={{ marginBottom: "40px" }}>
        <h2 style={{ fontSize: "22px", fontWeight: "600", marginBottom: "15px" }}>Lodgings</h2>
        {lodgings.length > 0 ? renderCards(lodgings) : <p>No lodgings found.</p>}
      </section>

      <section>
        <h2 style={{ fontSize: "22px", fontWeight: "600", marginBottom: "15px" }}>Restaurants</h2>
        {restaurants.length > 0 ? renderCards(restaurants) : <p>No restaurants found.</p>}
      </section>

      {renderModal()}
    </div>
  );
}
