import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

export default function RoutePlanner() {
  const [pois, setPois] = useState([]);
  const [theme, setTheme] = useState("");
  const [user, setUser] = useState(null);
  const [showPopup, setShowPopup] = useState(false);
  const [startTime, setStartTime] = useState("now");
  const [scheduledDate, setScheduledDate] = useState("");
  const [mode, setMode] = useState("car");
  const [numPois, setNumPois] = useState(5);
  const [userLocation, setUserLocation] = useState(null);

  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    setUser(storedUser);

    if ("geolocation" in navigator) {
      navigator.geolocation.getCurrentPosition(
        (pos) => setUserLocation({ lat: pos.coords.latitude, lng: pos.coords.longitude }),
        () => setUserLocation({ lat: 14.1461, lng: 121.5012 })
      );
    } else {
      setUserLocation({ lat: 14.1461, lng: 121.5012 });
    }
  }, []);

  const fetchRecommendedPois = async () => {
    const userId = user?.id || 1;
    const res = await fetch(
      `http://localhost:5000/api/recommend_pois?user_id=${userId}&theme=${theme}&top_k=${numPois}`
    );
    const data = await res.json();
    setPois(data);
  };

  const recommendRoute = async () => {
    if (!theme) {
      alert("Please select a theme.");
      return;
    }
    if (!userLocation) {
      alert("Waiting for current location...");
      return;
    }

    await fetchRecommendedPois();

    if (pois.length === 0) {
      alert("No POIs match your selection.");
      return;
    }

    navigate("/routedetails", {
      state: {
        startPoint: { lat: userLocation.lat, lng: userLocation.lng },
        filteredPois: pois,
        transportMode: mode === "foot" ? "foot-walking" : mode === "bike" ? "cycling-regular" : "driving-car",
        startTime: startTime === "now" ? new Date().toISOString() : scheduledDate,
      },
    });
    setShowPopup(false);
  };

  return (
    <div style={{ textAlign: "center", marginTop: 40 }}>
      <button
        onClick={() => setShowPopup(true)}
        style={{ padding: "12px 24px", background: "#2196f3", color: "#fff", border: "none", borderRadius: 8, fontSize: 16, cursor: "pointer" }}
      >
        Plan My Route
      </button>

      {showPopup && (
        <div style={{ position: "fixed", top: 0, left: 0, width: "100%", height: "100%", background: "rgba(0,0,0,0.6)", display: "flex", alignItems: "center", justifyContent: "center", zIndex: 9999 }}>
          <div style={{ background: "#fff", padding: 20, borderRadius: 10, width: 360, maxHeight: "80vh", overflowY: "auto", textAlign: "center", zIndex: 10000 }}>
            <h3>Select Your Theme</h3>
            <select value={theme} onChange={(e) => setTheme(e.target.value)} style={{ display: "block", margin: "12px auto", padding: 6 }}>
              <option value="">--Choose--</option>
              <option value="nature adventure">Nature Adventure</option>
              <option value="history culture">History & Culture</option>
              <option value="food experience">Food Experience</option>
              <option value="religious">Religious</option>
              <option value="scenic view">Scenic View</option>
            </select>

            <label>Number of POIs:</label>
            <input type="number" min={1} max={20} value={numPois} onChange={(e) => setNumPois(Number(e.target.value))} style={{ display: "block", margin: "8px auto", padding: 6 }} />

            <label>Mode of Transport:</label>
            <select value={mode} onChange={(e) => setMode(e.target.value)} style={{ display: "block", margin: "8px auto", padding: 6 }}>
              <option value="foot">On Foot</option>
              <option value="bike">Bike</option>
              <option value="motor">Motor</option>
              <option value="car">Car</option>
            </select>

            <label>Start Time:</label>
            <select value={startTime} onChange={(e) => setStartTime(e.target.value)} style={{ display: "block", margin: "8px auto", padding: 6 }}>
              <option value="now">Now</option>
              <option value="scheduled">Scheduled</option>
            </select>

            {startTime === "scheduled" && (
              <input type="datetime-local" value={scheduledDate} onChange={(e) => setScheduledDate(e.target.value)} style={{ display: "block", margin: "8px auto", padding: 6 }} />
            )}

            <div style={{ marginTop: 16 }}>
              <button onClick={recommendRoute} style={{ marginRight: 8, padding: "8px 16px", background: "#3b82f6", color: "#fff", border: "none", borderRadius: 6, cursor: "pointer" }}>Confirm</button>
              <button onClick={() => setShowPopup(false)} style={{ padding: "8px 16px", background: "#aaa", color: "#fff", border: "none", borderRadius: 6, cursor: "pointer" }}>Cancel</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
