// src/pages/AdminDashboard.js
import React, { useState } from "react";

export default function AdminDashboard({ isAdmin }) {
  // Access control
  if (!isAdmin) return <h2>Access Denied</h2>;

  // Hardcoded users (replace with database later)
  const [users, setUsers] = useState([
    { id: 1, name: "Alice", email: "alice@example.com", verified: false },
    { id: 2, name: "Bob", email: "bob@example.com", verified: true },
    { id: 3, name: "Charlie", email: "charlie@example.com", verified: false },
  ]);

  // Functions must be inside the component
  const verifyUser = (id) => {
    setUsers(users.map(u => u.id === id ? { ...u, verified: true } : u));
  };

  const deleteUser = (id) => {
    setUsers(users.filter(u => u.id !== id));
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>Admin Dashboard</h1>

      <h2>Registered Users</h2>
      <table border="1" cellPadding="8" style={{ borderCollapse: "collapse", width: "100%" }}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Email</th>
            <th>Verified</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map(u => (
            <tr key={u.id}>
              <td>{u.id}</td>
              <td>{u.name}</td>
              <td>{u.email}</td>
              <td>{u.verified ? "Yes" : "No"}</td>
              <td>
                {!u.verified && <button onClick={() => verifyUser(u.id)}>Verify</button>}
                <button onClick={() => deleteUser(u.id)} style={{ marginLeft: 8 }}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
