import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup, useMapEvents } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import "../leafletfix"; // fix marker icons
import Papa from "papaparse";

const CENTER = { lat: 14.1467, lng: 121.4708 };

export default function MapPage() {
  const [spots, setSpots] = useState([]);
  const [selectedSpot, setSelectedSpot] = useState(null);

  useEffect(() => {
    Papa.parse("/spots.csv", {
      download: true,
      header: true,
      complete: (res) => {
        const rows = res.data
          .filter((r) => r.name && r.lat && r.lng)
          .map((r) => ({
            name: r.name,
            lat: parseFloat(r.lat),
            lng: parseFloat(r.lng),
            description: r.description || "No description available.",
            tags: (r.tags || "")
              .split(",")
              .map((t) => t.trim())
              .filter(Boolean),
            visitors: r.visitors || "Moderate",
          }));
        setSpots(rows);
      },
    });
  }, []);

  return (
    <div className="flex h-screen w-full">
      {/* Map Area */}
      <div
        className={`transition-all duration-300 ${
          selectedSpot ? "w-2/3" : "w-full"
        }`}
      >
        <MapContainer
          center={CENTER}
          zoom={13}
          className="h-full w-full"
          style={{ height: "100%", width: "100%" }}
        >
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/">OSM contributors</a>'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          {spots.map((s, i) => (
            <Marker
              key={i}
              position={[s.lat, s.lng]}
              eventHandlers={{
                click: () => setSelectedSpot(s),
              }}
            />
          ))}
        </MapContainer>
      </div>

      {/* Details Panel */}
      {selectedSpot && (
        <div className="w-1/3 bg-white p-4 shadow-lg overflow-y-auto">
          <button
            className="mb-2 text-sm bg-gray-200 px-3 py-1 rounded hover:bg-gray-300"
            onClick={() => setSelectedSpot(null)}
          >
            Minimize
          </button>
          <h2 className="text-xl font-bold mb-2">{selectedSpot.name}</h2>
          <p className="mb-2">{selectedSpot.description}</p>
          {selectedSpot.tags?.length > 0 && (
            <p className="text-sm text-gray-600">
              <strong>Tags:</strong> {selectedSpot.tags.join(", ")}
            </p>
          )}
          <p className="text-sm text-gray-600">
            <strong>Visitors:</strong> {selectedSpot.visitors}
          </p>
        </div>
      )}
    </div>
  );
}
