import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function RoutePlanner() {
  const [theme, setTheme] = useState("");
  const [user, setUser] = useState(JSON.parse(localStorage.getItem("user")));
  const [pois, setPois] = useState([]);
  const navigate = useNavigate();

  const fetchPois = async () => {
    if (!theme) return;
    const userId = user?.id || 1;
    const res = await fetch(`http://localhost:5000/api/recommend_pois?user_id=${userId}&theme=${theme}`);
    const data = await res.json();
    setPois(data);
    return data;
  };

  const planRoute = async () => {
    const recommendedPois = await fetchPois();
    if (!recommendedPois.length) {
      alert("No POIs found for this theme.");
      return;
    }
    navigate("/routedetails", { state: { filteredPois: recommendedPois } });
  };

  return (
    <div style={{ textAlign: "center", marginTop: 40 }}>
      <h2>Select Theme</h2>
      <select value={theme} onChange={(e) => setTheme(e.target.value)} style={{ padding: 6 }}>
        <option value="">--Choose--</option>
        <option value="nature adventure">Nature Adventure</option>
        <option value="history culture">History & Culture</option>
        <option value="food experience">Food Experience</option>
      </select>
      <button onClick={planRoute} style={{ marginLeft: 10, padding: "8px 16px" }}>Show Recommended POIs</button>
    </div>
  );
}
