import React, { useState, useEffect, useRef } from "react";

export default function AdminReports() {
  const [activeReport, setActiveReport] = useState("users");
  const [reportData, setReportData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const reportRef = useRef();

  // Filter states
  const [filters, setFilters] = useState({
    id: "",
    username: "",
    email: "",
    role: "",
    name: "",
    visitors: ""
  });

  useEffect(() => {
    fetchReport(activeReport);
  }, [activeReport, filters]);

  const fetchReport = async (type) => {
    setLoading(true);
    setError(null);

    try {
      const token = localStorage.getItem("token");
      const query = new URLSearchParams({ type });

      // Add filters
      if (type === "users") {
        if (filters.id) query.append("id", filters.id);
        if (filters.username) query.append("username", filters.username);
        if (filters.email) query.append("email", filters.email);
        if (filters.role) query.append("role", filters.role);
      } else if (type === "pois") {
        if (filters.id) query.append("id", filters.id);
        if (filters.name) query.append("name", filters.name);
        if (filters.visitors) query.append("visitors", filters.visitors);
      }

      const res = await fetch(`http://localhost:5000/api/reports?${query.toString()}`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      if (!res.ok) throw new Error(`Failed to fetch report: ${res.status}`);
      const data = await res.json();
      setReportData(data);
    } catch (err) {
      console.error(err);
      setError(err.message);
      setReportData(null);
    } finally {
      setLoading(false);
    }
  };

  const handlePrint = () => {
    if (!reportRef.current) return;
    const printContent = reportRef.current.innerHTML;
    const WinPrint = window.open("", "", "width=900,height=650");
    WinPrint.document.write(`<html><head><title>Report</title></head><body>${printContent}</body></html>`);
    WinPrint.document.close();
    WinPrint.focus();
    WinPrint.print();
    WinPrint.close();
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({ ...prev, [name]: value }));
  };

  const renderReport = () => {
    if (loading) return <p>Loading...</p>;
    if (error) return <p style={{ color: "red" }}>{error}</p>;
    if (!reportData) return <p>No report available.</p>;

    if (activeReport === "users") {
      return (
        <div>
          <p><strong>Total Users:</strong> {reportData.totalUsers}</p>

          {/* Filters */}
          <div style={{ marginBottom: 10 }}>
            <input name="id" placeholder="ID" value={filters.id} onChange={handleFilterChange} style={{ marginRight: 5 }} />
            <input name="username" placeholder="Username" value={filters.username} onChange={handleFilterChange} style={{ marginRight: 5 }} />
            <input name="email" placeholder="Email" value={filters.email} onChange={handleFilterChange} style={{ marginRight: 5 }} />
            <select name="role" value={filters.role} onChange={handleFilterChange}>
              <option value="">All Roles</option>
              <option value="user">User</option>
              <option value="admin">Admin</option>
            </select>
          </div>

          <table border="1" cellPadding="5" style={{ borderCollapse: "collapse" }}>
            <thead>
              <tr>
                <th>ID</th><th>Username</th><th>Email</th><th>Role</th>
              </tr>
            </thead>
            <tbody>
              {reportData.users.map(user => (
                <tr key={user.id}>
                  <td>{user.id}</td><td>{user.username}</td><td>{user.email}</td><td>{user.role}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      );
    }

    if (activeReport === "pois") {
      return (
        <div>
          <p><strong>Total POIs:</strong> {reportData.totalPOIs}</p>

          {/* Filters */}
          <div style={{ marginBottom: 10 }}>
            <input name="id" placeholder="ID" value={filters.id} onChange={handleFilterChange} style={{ marginRight: 5 }} />
            <input name="name" placeholder="Name" value={filters.name} onChange={handleFilterChange} style={{ marginRight: 5 }} />
            <input name="visitors" placeholder="Min Visitors" value={filters.visitors} onChange={handleFilterChange} />
          </div>

          <table border="1" cellPadding="5" style={{ borderCollapse: "collapse" }}>
            <thead>
              <tr>
                <th>ID</th><th>Name</th><th>Visitors</th>
              </tr>
            </thead>
            <tbody>
              {reportData.pois.map(poi => (
                <tr key={poi.id}>
                  <td>{poi.id}</td><td>{poi.name}</td><td>{poi.visitors}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      );
    }

    return <p>No report available.</p>;
  };

  return (
    <div style={{ marginLeft: 220, padding: 20 }}>
      <h2>Admin Reports</h2>

      <div style={{ marginBottom: 20 }}>
        <button
          onClick={() => setActiveReport("users")}
          style={{ marginRight: 10, backgroundColor: activeReport === "users" ? "#ccc" : "#fff" }}
        >
          User Reports
        </button>
        <button
          onClick={() => setActiveReport("pois")}
          style={{ backgroundColor: activeReport === "pois" ? "#ccc" : "#fff" }}
        >
          POI Reports
        </button>
      </div>

      <div ref={reportRef} style={{ marginBottom: 20 }}>
        {renderReport()}
      </div>

      <button onClick={handlePrint}>Print / Save as PDF</button>
    </div>
  );
}
