// src/pages/AdminDashboard.js
import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";

export default function AdminDashboard({ isAdmin }) {
  const [pois, setPois] = useState([]);
  const [editingPoi, setEditingPoi] = useState(null);

  useEffect(() => {
    fetch("http://localhost:5000/api/pois")
      .then(res => res.json())
      .then(data => setPois(data))
      .catch(err => console.error("Failed to fetch POIs", err));
  }, []);

  const saveEdit = (updatedPoi) => {
    fetch("http://localhost:5000/api/pois", {
      method: "POST", 
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(updatedPoi)
    })
      .then(res => res.json())
      .then(() => {
        setPois(pois.map(p => p.id === updatedPoi.id ? updatedPoi : p));
        setEditingPoi(null);
      });
  };

  if (!isAdmin) return <h2>Access Denied</h2>;

  return (
    <div style={{ padding: 20 }}>
      <h1>Admin Dashboard</h1>
      <MapContainer center={[14.1467, 121.4708]} zoom={13} style={{ height: "600px", width: "100%" }}>
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; OpenStreetMap contributors'
        />
        {pois.map(poi => (
          <Marker key={poi.id} position={[poi.lat, poi.lng]}>
            <Popup>
              <strong>{poi.name}</strong><br />
              {poi.description}<br />
              <button onClick={() => setEditingPoi(poi)}>Edit</button>
            </Popup>
          </Marker>
        ))}
      </MapContainer>

      {/* Edit form popup/modal */}
      {editingPoi && (
        <div style={{ position: "fixed", top: 50, left: "50%", transform: "translateX(-50%)", background: "white", padding: 20, border: "1px solid #ccc", zIndex: 1000 }}>
          <h3>Edit POI</h3>
          <input type="text" value={editingPoi.name} onChange={e => setEditingPoi({ ...editingPoi, name: e.target.value })} /><br />
          <textarea value={editingPoi.description} onChange={e => setEditingPoi({ ...editingPoi, description: e.target.value })} /><br />
          <button onClick={() => saveEdit(editingPoi)}>Save</button>
          <button onClick={() => setEditingPoi(null)} style={{ marginLeft: 8 }}>Cancel</button>
        </div>
      )}
    </div>
  );
}
