import React, { useEffect, useState } from "react";
import Papa from "papaparse";

export default function NatureCulturePage() {
  const [places, setPlaces] = useState([]);
  const [selectedPlace, setSelectedPlace] = useState(null);

  useEffect(() => {
    fetch("/data/spots.csv")
      .then((res) => res.text())
      .then((csv) => {
        Papa.parse(csv, {
          header: true,
          skipEmptyLines: true,
          complete: (res) => {
            const rows = res.data
              .filter((r) => r.name && r.tags) // only places with tags and name
              .map((r) => ({ ...r, image: r.image || null, tags: r.tags.toLowerCase() }));

            // Filter by nature or culture tags
            const filtered = rows.filter(
              (r) => r.tags.includes("nature") || r.tags.includes("culture")
            );

            setPlaces(filtered);
          },
        });
      });
  }, []);

  const renderGrid = () => (
    <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
      {places.map((p, i) => (
        <div
          key={i}
          onClick={() => setSelectedPlace(p)}
          className="cursor-pointer rounded-2xl shadow-md overflow-hidden border bg-white hover:shadow-lg transition"
        >
          {p.image ? (
            <img src={p.image} alt={p.name} className="h-48 w-full object-cover" />
          ) : (
            <div className="h-48 w-full flex items-center justify-center bg-gray-100 text-gray-500">No image</div>
          )}
          <div className="p-3">
            <h3 className="text-lg font-bold">{p.name}</h3>
          </div>
        </div>
      ))}
    </div>
  );

  const renderModal = () => {
    if (!selectedPlace) return null;
    return (
      <div
        className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50"
        onClick={() => setSelectedPlace(null)}
      >
        <div
          className="bg-white rounded-2xl p-6 max-w-2xl w-11/12 max-h-[80vh] overflow-y-auto relative"
          onClick={(e) => e.stopPropagation()}
        >
          <button
            onClick={() => setSelectedPlace(null)}
            className="absolute top-4 right-4 text-xl font-bold cursor-pointer"
          >
            âœ•
          </button>
          {selectedPlace.image && (
            <img
              src={selectedPlace.image}
              alt={selectedPlace.name}
              className="w-full h-64 object-cover rounded-lg mb-4"
            />
          )}
          <h2 className="text-2xl font-bold mb-2">{selectedPlace.name}</h2>
          <p className="text-gray-700 mb-2">{selectedPlace.description}</p>
          {selectedPlace.visitors && <p><strong>Visitors:</strong> {selectedPlace.visitors}</p>}
          {selectedPlace.tags && <p><strong>Tags:</strong> {selectedPlace.tags}</p>}
          {/* Add more details like opening hours, location, etc. if available */}
        </div>
      </div>
    );
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-6">Nature & Culture</h1>
      {places.length > 0 ? renderGrid() : <p>No places found.</p>}
      {renderModal()}
    </div>
  );
}
