import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import { useNavigate, useLocation } from "react-router-dom";

const ORS_API_KEY = "AIzaSyA6D72kCqt2yln8Yhfg0Y0iE25F0ndZtO4";

export default function RouteDetails() {
  const location = useLocation();
  const navigate = useNavigate();
  const routeSpots = location.state?.itinerary || [];

  const [currentIndex, setCurrentIndex] = useState(0);
  const [fullRoute, setFullRoute] = useState([]);
  const [loadingRoute, setLoadingRoute] = useState(true);

  // Fetch route segment along roads
  const fetchRouteSegment = async (from, to) => {
    const isTrail = ["forest", "mountain", "trail"].some(t =>
      from.type?.toLowerCase().includes(t)
    );
    const profile = isTrail ? "foot-hiking" : "driving-car";

    try {
      const res = await fetch(`https://api.openrouteservice.org/v2/directions/${profile}/geojson`, {
        method: "POST",
        headers: {
          "Authorization": ORS_API_KEY,
          "Content-Type": "application/json"
        },
        body: JSON.stringify({
          coordinates: [[from.lng, from.lat], [to.lng, to.lat]],
          instructions: false
        })
      });

      const data = await res.json();
      const coords = data.features[0].geometry.coordinates.map(([lng, lat]) => [lat, lng]);
      const summary = data.features[0].properties.summary;

      return { coords, summary, isTrail };
    } catch (err) {
      console.error("Route fetch failed:", err);
      return { coords: [[from.lat, from.lng], [to.lat, to.lng]], summary: { distance: 0, duration: 0 }, isTrail: false };
    }
  };

  // Build all route segments
  useEffect(() => {
    const buildRoute = async () => {
      const segments = [];
      for (let i = 0; i < routeSpots.length - 1; i++) {
        const seg = await fetchRouteSegment(routeSpots[i], routeSpots[i + 1]);
        segments.push(seg);
      }
      setFullRoute(segments);
      setLoadingRoute(false);
    };
    if (routeSpots.length > 1) buildRoute();
    else setLoadingRoute(false);
  }, [routeSpots]);

  const currentPOI = routeSpots[currentIndex];

  const nextPOI = () => {
    if (currentIndex < routeSpots.length - 1) setCurrentIndex(currentIndex + 1);
    else alert("You have reached the end of the route!");
  };

  const finishRoute = () => {
    if (window.confirm("Are you sure you want to finish the route?")) {
      alert("Route completed!");
      navigate("/home");
    }
  };

  const cancelRoute = () => {
    if (window.confirm("Cancel the route? You will lose progress.")) {
      alert("Route cancelled.");
      navigate("/home");
    }
  };

  // Convert meters to km and seconds to hh:mm
  const formatDistance = (m) => (m / 1000).toFixed(2) + " km";
  const formatDuration = (s) => {
    const h = Math.floor(s / 3600);
    const m = Math.floor((s % 3600) / 60);
    return `${h}h ${m}m`;
  };

  if (!routeSpots.length) return <p>No route available.</p>;

  return (
    <div style={{ display: "grid", gridTemplateColumns: "1.2fr 0.8fr", gap: 20, padding: 20, minHeight: "100vh" }}>
      <div style={{ height: "600px", borderRadius: 12, overflow: "hidden", boxShadow: "0 6px 18px rgba(0,0,0,0.15)" }}>
        {loadingRoute ? (
          <p style={{ textAlign: "center", marginTop: "40%" }}>Loading route...</p>
        ) : (
          <MapContainer
            center={[currentPOI.lat || 0, currentPOI.lng || 0]}
            zoom={14}
            style={{ height: "100%", width: "100%" }}
          >
            <TileLayer
              attribution='&copy; <a href="https://www.openstreetmap.org/">OSM contributors</a>'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />

            {/* Draw current segment only */}
            {fullRoute[currentIndex] && (
              <Polyline
                positions={fullRoute[currentIndex].coords}
                color={fullRoute[currentIndex].isTrail ? "green" : "blue"}
                dashArray={fullRoute[currentIndex].isTrail ? "5,10" : undefined}
              />
            )}

            {/* Show current POI */}
            <Marker position={[currentPOI.lat, currentPOI.lng]}>
              <Popup>
                <strong>{currentPOI.name}</strong>
                <p>{currentPOI.description}</p>
                {currentPOI.image && <img src={currentPOI.image} alt={currentPOI.name} style={{ width: "160px", borderRadius: 8, marginTop: 6 }} />}
                {fullRoute[currentIndex] && (
                  <p style={{ marginTop: 6 }}>
                    Distance: {formatDistance(fullRoute[currentIndex].summary.distance)} <br />
                    Duration: {formatDuration(fullRoute[currentIndex].summary.duration)}
                  </p>
                )}
              </Popup>
            </Marker>
          </MapContainer>
        )}
      </div>

      <div style={{ background: "#fff", borderRadius: 12, padding: 20, boxShadow: "0 6px 18px rgba(0,0,0,0.12)" }}>
        <h2>Current POI</h2>
        <p><strong>{currentPOI.name}</strong></p>
        <p>{currentPOI.description}</p>
        {currentPOI.image && (
          <img
            src={currentPOI.image}
            alt={currentPOI.name}
            style={{ width: "100%", maxWidth: 280, borderRadius: 10, boxShadow: "0 6px 14px rgba(0,0,0,0.2)" }}
          />
        )}
        {fullRoute[currentIndex] && (
          <p style={{ marginTop: 10 }}>
            Distance to next POI: {formatDistance(fullRoute[currentIndex].summary.distance)} <br />
            Estimated time: {formatDuration(fullRoute[currentIndex].summary.duration)}
          </p>
        )}
      </div>

      <div style={{ position: "fixed", bottom: 0, left: 0, right: 0, background: "#2196f3", padding: "12px 20px", display: "flex", justifyContent: "center", gap: 16, zIndex: 1000 }}>
        {currentIndex < routeSpots.length - 1 && (
          <button onClick={nextPOI} style={{ padding: "10px 20px", border: "none", borderRadius: 8, background: "#4caf50", color: "#fff", fontWeight: 600 }}>Next POI</button>
        )}
        <button onClick={cancelRoute} style={{ padding: "10px 20px", border: "none", borderRadius: 8, background: "#f44336", color: "#fff", fontWeight: 600 }}>Cancel Route</button>
        {currentIndex === routeSpots.length - 1 && (
          <button onClick={finishRoute} style={{ padding: "10px 20px", border: "none", borderRadius: 8, background: "#ff9800", color: "#fff", fontWeight: 600 }}>Finish Route</button>
        )}
      </div>
    </div>
  );
}
