import React, { useEffect, useState } from "react";

export default function ProfilePage() {
  const storedUser = JSON.parse(localStorage.getItem("user"));
  const token = localStorage.getItem("token");

  const [user, setUser] = useState(null);
  const [preferences, setPreferences] = useState([]);
  const [editing, setEditing] = useState(false);

  const preferenceOptions = ["Nature", "Culture", "Food", "History", "Adventure"];

  useEffect(() => {
    if (!storedUser) return;

    fetch(`http://localhost:5000/api/users/email/${storedUser.email}`, {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => {
        if (!res.ok) throw new Error("Failed to fetch profile");
        return res.json();
      })
      .then((data) => {
        setUser(data);

        let prefs = [];
        if (Array.isArray(data.preferences)) {
          prefs = data.preferences;
        } else if (typeof data.preferences === "string" && data.preferences.trim() !== "") {
          prefs = data.preferences.split(",").map((p) => p.trim());
        }
        setPreferences(prefs);
      })
      .catch((err) => console.error("Error fetching user:", err));
  }, [storedUser, token]);

  const handleSave = () => {
    if (!user) return;

    fetch(`http://localhost:5000/api/users/${user.id}/preferences`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ preferences }),
    })
      .then((res) => {
        if (!res.ok) throw new Error("Failed to update preferences");
        return res.json();
      })
      .then(() => {
        setEditing(false);
        setUser({ ...user, preferences });
      })
      .catch((err) => console.error("Error updating preferences:", err));
  };

  if (!user) return <div style={{ padding: "24px" }}>Loading...</div>;

  return (
    <div style={{ padding: "24px", maxWidth: "600px", margin: "0 auto" }}>
      <h1 style={{ fontSize: "1.8rem", fontWeight: "bold", marginBottom: "16px" }}>
        Profile
      </h1>

      <div
        style={{
          border: "1px solid #ddd",
          borderRadius: "16px",
          padding: "20px",
          background: "#fff",
          boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
        }}
      >
        <p><strong>Username:</strong> {user.username}</p>
        <p><strong>Email:</strong> {user.email}</p>
        <p><strong>Full Name:</strong> {`${user.first_name || ""} ${user.middle_name || ""} ${user.last_name || ""}`}</p>
        <p><strong>Age:</strong> {user.age || "Not provided"}</p>

        <div style={{ marginTop: "16px" }}>
          <strong>Preferences:</strong>
          {editing ? (
            <div>
              {preferenceOptions.map((option) => (
                <label key={option} style={{ display: "block", marginTop: "6px" }}>
                  <input
                    type="checkbox"
                    checked={preferences.includes(option)}
                    onChange={() =>
                      setPreferences((prev) =>
                        prev.includes(option)
                          ? prev.filter((p) => p !== option)
                          : [...prev, option]
                      )
                    }
                  />{" "}
                  {option}
                </label>
              ))}
              <div style={{ marginTop: "12px" }}>
                <button
                  onClick={handleSave}
                  style={{
                    background: "#007bff",
                    color: "#fff",
                    padding: "8px 16px",
                    border: "none",
                    borderRadius: "8px",
                  }}
                >
                  Save
                </button>
                <button
                  onClick={() => {
                    setEditing(false);
                    let prefs = [];
                    if (Array.isArray(user.preferences)) {
                      prefs = user.preferences;
                    } else if (typeof user.preferences === "string" && user.preferences.trim() !== "") {
                      prefs = user.preferences.split(",").map((p) => p.trim());
                    }
                    setPreferences(prefs);
                  }}
                  style={{
                    background: "#ccc",
                    padding: "8px 16px",
                    border: "none",
                    borderRadius: "8px",
                    marginLeft: "8px",
                  }}
                >
                  Cancel
                </button>
              </div>
            </div>
          ) : (
            <ul style={{ marginTop: "8px" }}>
              {preferences.length > 0 ? (
                preferences.map((p, i) => <li key={i}>{p}</li>)
              ) : (
                <li>No preferences set.</li>
              )}
            </ul>
          )}
        </div>

        {!editing && (
          <button
            onClick={() => setEditing(true)}
            style={{
              marginTop: "16px",
              background: "#28a745",
              color: "#fff",
              padding: "8px 16px",
              border: "none",
              borderRadius: "8px",
            }}
          >
            Edit Preferences
          </button>
        )}
      </div>
    </div>
  );
}
