import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";

export default function AdminDashboard() {
  const [pois, setPois] = useState([]);
  const [usersCount, setUsersCount] = useState(0);

  useEffect(() => {
    const token = localStorage.getItem("token");

    // Fetch POIs
    fetch("http://localhost:5000/api/pois", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then(res => res.json())
      .then(data => setPois(Array.isArray(data) ? data : []))
      .catch(err => console.error(err));

    // Fetch Users count
    fetch("http://localhost:5000/api/users", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then(res => res.json())
      .then(data => setUsersCount(Array.isArray(data) ? data.length : 0))
      .catch(err => console.error(err));
  }, []);

  const mostVisited = pois.reduce((max, poi) => {
    const visitsOrder = { "Very High": 4, High: 3, Moderate: 2, Low: 1 };
    return visitsOrder[poi.visitors] > (visitsOrder[max.visitors] || 0) ? poi : max;
  }, {});

  return (
    <div style={{ marginLeft: "240px", padding: "20px" }}>
      <h1>Admin Dashboard</h1>
      <p>Total Users: {usersCount}</p>
      <p>Total POIs: {pois.length}</p>

      <h2>Most Visited POI</h2>
      {mostVisited.name ? (
        <div>
          <strong>{mostVisited.name}</strong> - {mostVisited.visitors}
        </div>
      ) : (
        <p>No POIs yet</p>
      )}

      <MapContainer
        center={[14.1467, 121.4708]}
        zoom={13}
        style={{ height: "400px", width: "100%", marginTop: 20 }}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; OpenStreetMap contributors'
        />
        {pois.map(poi => (
          <Marker key={poi.id} position={[poi.lat, poi.lng]}>
            <Popup>
              <strong>{poi.name}</strong>
              <br />
              {poi.description}
              <br />
              Visitors: {poi.visitors}
              <br />
              Tags: {poi.tags}
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
}
