import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import { useNavigate, useLocation } from "react-router-dom";

export default function RouteDetails() {
  const location = useLocation();
  const navigate = useNavigate();
  const routeSpots = location.state?.itinerary || [];
  const itineraryIdFromState = location.state?.id || null;

  const [segments, setSegments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [poiDetails, setPoiDetails] = useState([]);
  const [saving, setSaving] = useState(false);
  const [saveMessage, setSaveMessage] = useState("");
  const [status, setStatus] = useState("pending");
  const [userLocation, setUserLocation] = useState(null);
  const [itineraryId, setItineraryId] = useState(itineraryIdFromState);
  const [tracking, setTracking] = useState(false);
  const [activeIndex, setActiveIndex] = useState(0);
  const [nextPreview, setNextPreview] = useState(null);
  const [routeToStop, setRouteToStop] = useState([]);

  const mapRef = useRef(null);
  const watchIdRef = useRef(null);

  const ORS_API_KEY = "eyJvcmciOiI1YjNjZTM1OTc4NTExMTAwMDFjZjYyNDgiLCJpZCI6ImJhNWU3NmIxOTRmMzQyYTg4NzY4MDc2MzVmZDY1ZDRhIiwiaCI6Im11cm11cjY0In0=";

  // ---------- ICONS ----------
  const numberedIcon = (number) =>
    L.divIcon({
      html: `<div style="
        background:#1976d2;
        color:#fff;
        width:28px;
        height:28px;
        border-radius:50%;
        text-align:center;
        line-height:28px;
        font-weight:600;
        border:2px solid #fff;
        box-shadow:0 2px 6px rgba(0,0,0,0.3);
      ">${number}</div>`,
      className: "",
      iconSize: [28, 28],
    });

  const userIcon = L.divIcon({
    html: `<div style="
      background:#4caf50;
      color:#fff;
      width:30px;
      height:30px;
      border-radius:50%;
      text-align:center;
      line-height:30px;
      font-weight:700;
      border:2px solid #fff;
      box-shadow:0 2px 8px rgba(0,0,0,0.3);
    ">üßç</div>`,
    className: "",
    iconSize: [30, 30],
  });

  // ---------- LOCATION ----------
  useEffect(() => {
    if (!navigator.geolocation) return;
    navigator.geolocation.getCurrentPosition(
      (pos) => setUserLocation({ lat: pos.coords.latitude, lng: pos.coords.longitude }),
      (err) => console.warn("Location error:", err),
      { enableHighAccuracy: true }
    );
  }, []);

  // ---------- FETCH POIs ----------
  const fetchPoiDetails = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/pois");
      const data = await res.json();
      const matched = routeSpots.map((spot) =>
        data.find((poi) => poi.name === spot.name || poi.id === spot.id)
      );
      setPoiDetails(matched);
    } catch (err) {
      console.error("Error fetching POI details:", err);
    }
  };

  // ---------- SNAP TO ROAD ----------
  const snapToRoad = async (poi) => {
    try {
      const res = await fetch("https://api.openrouteservice.org/v2/snap/driving-car", {
        method: "POST",
        headers: { Authorization: ORS_API_KEY, "Content-Type": "application/json" },
        body: JSON.stringify({ coordinates: [[poi.lng, poi.lat]] }),
      });
      const data = await res.json();
      if (data.snappedLocations?.length) {
        const [lng, lat] = data.snappedLocations[0].location;
        return { lat, lng };
      }
      return poi;
    } catch {
      return poi;
    }
  };

  // ---------- FETCH ROUTE ----------
  const fetchRoute = async (from, to) => {
    if (!from || !to) return [];
    const snappedFrom = await snapToRoad(from);
    const snappedTo = await snapToRoad(to);
    try {
      const res = await fetch("https://api.openrouteservice.org/v2/directions/driving-car/geojson", {
        method: "POST",
        headers: { Authorization: ORS_API_KEY, "Content-Type": "application/json" },
        body: JSON.stringify({
          coordinates: [
            [snappedFrom.lng, snappedFrom.lat],
            [snappedTo.lng, snappedTo.lat],
          ],
          preference: "fastest",
          instructions: false,
        }),
      });
      const data = await res.json();
      if (data.features?.length) {
        return data.features[0].geometry.coordinates.map(([lng, lat]) => [lat, lng]);
      }
    } catch {
      return [[from.lat, from.lng], [to.lat, to.lng]];
    }
    return [];
  };

  // ---------- INITIAL ROUTE SEGMENTS ----------
  useEffect(() => {
    const loadAllSegments = async () => {
      setLoading(true);
      await fetchPoiDetails();
      const segs = [];
      for (let i = 0; i < routeSpots.length - 1; i++) {
        const seg = await fetchRoute(routeSpots[i], routeSpots[i + 1]);
        if (seg.length) segs.push({ coords: seg });
      }
      setSegments(segs);
      setLoading(false);
    };
    if (routeSpots.length > 0) loadAllSegments();
  }, [routeSpots]);

  // ---------- TOUR CONTROL ----------
  const startTour = async () => {
    if (!userLocation || routeSpots.length === 0) return;
    setTracking(true);
    setActiveIndex(0);
    setNextPreview(routeSpots[1] || null);

    const firstStop = routeSpots[0];
    const initialRoute = await fetchRoute(userLocation, firstStop);
    setRouteToStop(initialRoute);

    // live tracking
    watchIdRef.current = navigator.geolocation.watchPosition(
      async (pos) => {
        const current = { lat: pos.coords.latitude, lng: pos.coords.longitude };
        setUserLocation(current);

        // live update of line
        const liveRoute = await fetchRoute(current, routeSpots[activeIndex]);
        setRouteToStop(liveRoute);
      },
      (err) => console.warn("Watch error:", err),
      { enableHighAccuracy: true, maximumAge: 2000, timeout: 10000 }
    );
  };

  const handleNextStop = async () => {
    if (activeIndex + 1 >= routeSpots.length) {
      alert("üéâ You've completed the tour!");
      stopTour();
      return;
    }

    const nextStop = routeSpots[activeIndex + 1];
    setActiveIndex((prev) => prev + 1);
    setNextPreview(routeSpots[activeIndex + 2] || null);

    if (userLocation) {
      const updatedRoute = await fetchRoute(userLocation, nextStop);
      setRouteToStop(updatedRoute);
    }
  };

  const stopTour = () => {
    if (watchIdRef.current) navigator.geolocation.clearWatch(watchIdRef.current);
    setTracking(false);
    setRouteToStop([]);
    setActiveIndex(0);
    setNextPreview(null);
  };

  // ---------- HELPERS ----------
  const handleZoomToPoi = (poi) => {
    if (mapRef.current) mapRef.current.setView([poi.lat, poi.lng], 16, { animate: true });
  };

  const formatDistance = (m) => `${(m / 1000).toFixed(2)} km`;
  const formatDuration = (s) => `${Math.floor(s / 60)} min`;

  if (!routeSpots.length || !userLocation) return <p>Loading route...</p>;

  // ---------- RENDER ----------
  return (
    <div
      style={{
        display: "grid",
        gridTemplateColumns: "1.3fr 0.7fr",
        gap: "24px",
        padding: "24px",
        minHeight: "100vh",
        fontFamily: "'Poppins', sans-serif",
        background: "linear-gradient(to bottom right, #e3f2fd, #e8f5e9)",
      }}
    >
      {/* MAP */}
      <div
        style={{
          height: "650px",
          borderRadius: "18px",
          overflow: "hidden",
          boxShadow: "0 10px 25px rgba(0,0,0,0.15)",
          border: "4px solid #fff",
        }}
      >
        <MapContainer
          ref={mapRef}
          center={[userLocation.lat, userLocation.lng]}
          zoom={13}
          style={{ height: "100%", width: "100%" }}
        >
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" attribution="¬© OpenStreetMap" />
          <Marker position={[userLocation.lat, userLocation.lng]} icon={userIcon}>
            <Popup>You are here</Popup>
          </Marker>

          {/* All planned routes */}
          {segments.map((seg, i) => (
            <Polyline key={i} positions={seg.coords} color="#1976d2" weight={3} opacity={0.5} />
          ))}

          {/* Active route line */}
          {routeToStop.length > 0 && (
            <Polyline positions={routeToStop} color="#ff5722" weight={6} opacity={0.9} />
          )}

          {routeSpots.map((poi, i) => {
            const details = poiDetails[i];
            return (
              <Marker key={i} position={[poi.lat, poi.lng]} icon={numberedIcon(i + 1)}>
                <Popup>
                  <strong>Stop {i + 1}: {details?.name || poi.name}</strong>
                  <p>{details?.description || "No description available."}</p>
                  {details?.image && (
                    <img
                      src={`http://localhost:5000/images/${details.image}`}
                      alt={details.name}
                      style={{ width: "160px", borderRadius: "8px", marginTop: "6px" }}
                    />
                  )}
                </Popup>
              </Marker>
            );
          })}
        </MapContainer>
      </div>

      {/* SIDEBAR */}
      <div
        style={{
          background: "#fff",
          borderRadius: "18px",
          padding: "24px",
          boxShadow: "0 8px 24px rgba(0,0,0,0.1)",
          border: "1px solid #e0e0e0",
          overflowY: "auto",
          maxHeight: "650px",
        }}
      >
        <h2 style={{ color: "#1565c0", fontWeight: "700", marginBottom: "16px" }}>Route Overview</h2>

        {tracking ? (
          <>
            <h3 style={{ color: "#1976d2" }}>Now Heading To:</h3>
            <p><strong>{routeSpots[activeIndex]?.name}</strong></p>
            {nextPreview && (
              <p style={{ color: "#666" }}>
                üëÄ Next: <strong>{nextPreview.name}</strong>
              </p>
            )}
            <button
              onClick={handleNextStop}
              style={{
                width: "100%",
                padding: "12px 20px",
                background: "#1976d2",
                color: "#fff",
                border: "none",
                borderRadius: "8px",
                fontWeight: "600",
                cursor: "pointer",
                marginTop: "12px",
              }}
            >
              ‚è≠ Next Stop
            </button>
            <button
              onClick={stopTour}
              style={{
                width: "100%",
                padding: "12px 20px",
                background: "#f44336",
                color: "#fff",
                border: "none",
                borderRadius: "8px",
                fontWeight: "600",
                cursor: "pointer",
                marginTop: "10px",
              }}
            >
              üõë End Tour
            </button>
          </>
        ) : (
          <button
            onClick={startTour}
            style={{
              width: "100%",
              padding: "12px 20px",
              background: "#4caf50",
              color: "#fff",
              border: "none",
              borderRadius: "8px",
              fontWeight: "600",
              cursor: "pointer",
              marginTop: "10px",
            }}
          >
            ‚ñ∂Ô∏è Start Tour
          </button>
        )}

        <button
          onClick={() => navigate("/home")}
          style={{
            width: "100%",
            padding: "12px 20px",
            background: "#9e9e9e",
            color: "#fff",
            border: "none",
            borderRadius: "8px",
            fontWeight: "600",
            cursor: "pointer",
            marginTop: "10px",
          }}
        >
          Back to Home
        </button>
      </div>
    </div>
  );
}
