import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from "react-leaflet";
import "leaflet/dist/leaflet.css";

export default function RouteDetails() {
  const location = useLocation();
  const navigate = useNavigate();
  const { itinerary, startTime } = location.state || {};
  const [currentIndex, setCurrentIndex] = useState(0);
  const [currentTime, setCurrentTime] = useState(startTime ? new Date(startTime) : new Date());

  if (!itinerary || itinerary.length === 0) {
    return <div>No route found!</div>;
  }

  const currentPOI = itinerary[currentIndex];

  // Move to next POI
  const handleNext = () => {
    if (currentIndex + 1 < itinerary.length) {
      setCurrentIndex(currentIndex + 1);
      // Simulate time passing, e.g., 1 hour per stop
      const nextTime = new Date(currentTime);
      nextTime.setHours(nextTime.getHours() + 1);
      setCurrentTime(nextTime);
    } else {
      alert("You have reached the end of the route!");
    }
  };

  // Finish itinerary
  const handleFinish = () => {
    const confirmFinish = window.confirm("Are you done with the route?");
    if (confirmFinish) {
      navigate("/");
    }
  };

  // Generate Polyline positions for all POIs
  const routePositions = itinerary.map((poi) => [poi.lat, poi.lng]);

  return (
    <div style={{ display: "flex", gap: "16px", flexDirection: "column" }}>
      <h2>Tourism Circuit Route</h2>
      <p>Current Time: {currentTime.toLocaleTimeString()}</p>
      <div style={{ height: "500px", width: "100%" }}>
        <MapContainer
          center={[currentPOI.lat, currentPOI.lng]}
          zoom={14}
          style={{ height: "100%", width: "100%" }}
        >
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/">OSM contributors</a>'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          <Polyline positions={routePositions} color="blue" />
          {itinerary.map((poi, index) => (
            <Marker key={index} position={[poi.lat, poi.lng]}>
              <Popup>
                <strong>{poi.name}</strong>
                <br />
                Visitors: {poi.visitors} (Predicted: {poi.predicted_frequency})
                <p>{poi.description}</p>
              </Popup>
            </Marker>
          ))}
        </MapContainer>
      </div>

      <div className="poi-details">
        <h3>Next Stop: {currentPOI.name}</h3>
        <p>Visitors: {currentPOI.visitors} (Predicted: {currentPOI.predicted_frequency})</p>
        <p>{currentPOI.description}</p>
      </div>

      <div style={{ display: "flex", gap: "8px" }}>
        {currentIndex + 1 < itinerary.length && (
          <button onClick={handleNext}>Next Stop</button>
        )}
        <button onClick={handleFinish}>Finish Route</button>
      </div>
    </div>
  );
}
