import React, { useState } from "react";
import RoutePlanner from "../components/RoutePlanner";
import spotsData from "../data/spots.csv"; // example CSV import (parsed later)

export default function Home() {
  const [route, setRoute] = useState([]);

  const handleStartTour = () => {
    const choice = window.confirm(
      "Do you want the system to create a random tour based on your preferences? \n\nClick OK for Automatic Tour, or Cancel to manually create your route."
    );

    if (choice) {
      // Automatic tour using RoutePlanner
      RoutePlanner(spotsData, null, setRoute);
    } else {
      // Redirect to manual route selection (for example, MapPage)
      window.location.href = "/map";
    }
  };

  return (
    <div className="home">
      <h1>Welcome to Majayjay Tourism Circuit</h1>
      <p>Discover nature, culture, and establishments in Majayjay.</p>

      <button
        onClick={handleStartTour}
        style={{
          padding: "10px 20px",
          background: "#2a9d8f",
          color: "white",
          border: "none",
          borderRadius: "8px",
          cursor: "pointer",
          marginTop: "15px",
        }}
      >
        ðŸš€ Start a Tour
      </button>

      {route.length > 0 && (
        <div style={{ marginTop: "20px" }}>
          <h2>Recommended Tour</h2>
          <ul>
            {route.map((spot, idx) => (
              <li key={idx}>
                <strong>{spot.name}</strong> ({spot.type})
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
