import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import { useNavigate, useLocation } from "react-router-dom";

export default function RouteDetails() {
  const location = useLocation();
  const navigate = useNavigate();
  const routeSpots = location.state?.itinerary || [];

  const [segments, setSegments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [weatherData, setWeatherData] = useState({});

  const ORS_API_KEY =
    "eyJvcmciOiI1YjNjZTM1OTc4NTExMTAwMDFjZjYyNDgiLCJpZCI6ImJhNWU3NmIxOTRmMzQyYTg4NzY4MDc2MzVmZDY1ZDRhIiwiaCI6Im11cm11cjY0In0=";
  const WEATHER_API_KEY = "13d7213054a676ccdd935415b00fac6f";

  // üß≠ Numbered markers
  const numberedIcon = (number) =>
    L.divIcon({
      html: `<div style="
        background:#1976d2;
        color:#fff;
        width:28px;
        height:28px;
        border-radius:50%;
        text-align:center;
        line-height:28px;
        font-weight:600;
        border:2px solid #fff;
        box-shadow:0 2px 6px rgba(0,0,0,0.3);
      ">${number}</div>`,
      className: "",
      iconSize: [28, 28],
    });

  const snapToRoad = async (poi) => {
    try {
      const res = await fetch("https://api.openrouteservice.org/v2/snap/driving-car", {
        method: "POST",
        headers: {
          Authorization: ORS_API_KEY,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ coordinates: [[poi.lng, poi.lat]] }),
      });
      const data = await res.json();
      if (data.snappedLocations?.length) {
        const [lng, lat] = data.snappedLocations[0].location;
        return { lat, lng };
      }
      return poi;
    } catch {
      return poi;
    }
  };

  const fetchRouteSegment = async (from, to) => {
    if (!from || !to) return null;
    const snappedFrom = await snapToRoad(from);
    const snappedTo = await snapToRoad(to);

    try {
      const res = await fetch("https://api.openrouteservice.org/v2/directions/driving-car/geojson", {
        method: "POST",
        headers: {
          Authorization: ORS_API_KEY,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          coordinates: [
            [snappedFrom.lng, snappedFrom.lat],
            [snappedTo.lng, snappedTo.lat],
          ],
          preference: "fastest",
          instructions: false,
        }),
      });

      const data = await res.json();
      if (data.features?.length) {
        const coords = data.features[0].geometry.coordinates.map(([lng, lat]) => [lat, lng]);
        const summary = data.features[0].properties.summary;
        return { coords, summary };
      }
    } catch {
      // ignore
    }

    return {
      coords: [
        [from.lat, from.lng],
        [to.lat, to.lng],
      ],
      summary: { distance: 0, duration: 0 },
    };
  };

  const fetchWeather = async (poi, index) => {
    if (!poi) return;
    try {
      const res = await fetch(
        `https://api.openweathermap.org/data/2.5/weather?lat=${poi.lat}&lon=${poi.lng}&units=metric&appid=${WEATHER_API_KEY}`
      );
      const data = await res.json();
      setWeatherData((prev) => ({ ...prev, [index]: data }));
    } catch {
      // ignore
    }
  };

  useEffect(() => {
    const loadAllSegments = async () => {
      setLoading(true);
      const segs = [];
      for (let i = 0; i < routeSpots.length - 1; i++) {
        const seg = await fetchRouteSegment(routeSpots[i], routeSpots[i + 1]);
        if (seg) segs.push(seg);
        await fetchWeather(routeSpots[i], i);
      }
      setSegments(segs);
      setLoading(false);
    };
    if (routeSpots.length > 1) loadAllSegments();
  }, [routeSpots]);

  const formatDistance = (m) => `${(m / 1000).toFixed(2)} km`;
  const formatDuration = (s) => `${Math.floor(s / 60)} min`;

  if (!routeSpots.length) return <p>No route available.</p>;

  return (
    <div
      style={{
        display: "grid",
        gridTemplateColumns: "1.3fr 0.7fr",
        gap: "24px",
        padding: "24px",
        minHeight: "100vh",
        background: "linear-gradient(to bottom right, #e3f2fd, #e8f5e9)",
        fontFamily: "'Poppins', sans-serif",
      }}
    >
      {/* üó∫Ô∏è Map */}
      <div
        style={{
          height: "650px",
          borderRadius: "18px",
          overflow: "hidden",
          boxShadow: "0 10px 25px rgba(0,0,0,0.15)",
          border: "4px solid #fff",
        }}
      >
        {loading ? (
          <p style={{ textAlign: "center", marginTop: "40%", fontWeight: "500", color: "#555" }}>
            Loading routes & weather...
          </p>
        ) : (
          <MapContainer
            center={[routeSpots[0].lat, routeSpots[0].lng]}
            zoom={13}
            style={{ height: "100%", width: "100%" }}
          >
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution="¬© OpenStreetMap contributors"
            />

            {/* Lines between stops */}
            {segments.map((seg, i) => (
              <Polyline key={i} positions={seg.coords} color="#1976d2" weight={5} opacity={0.9} />
            ))}

            {/* Markers */}
            {routeSpots.map((poi, i) => {
              const w = weatherData[i];
              return (
                <Marker key={i} position={[poi.lat, poi.lng]} icon={numberedIcon(i + 1)}>
                  <Popup>
                    <strong>Stop {i + 1}: {poi.name}</strong>
                    <p style={{ fontSize: "0.9rem", margin: "6px 0" }}>{poi.description}</p>
                    {poi.image && (
                      <img
                        src={poi.image}
                        alt={poi.name}
                        style={{
                          width: "160px",
                          borderRadius: "8px",
                          marginTop: "6px",
                        }}
                      />
                    )}
                    {w && w.weather && (
                      <div style={{ fontSize: "0.85rem", marginTop: "8px" }}>
                        üå§ <b>{w.weather[0].main}</b> ‚Äì {w.weather[0].description}
                        <br />
                        üå° {w.main?.temp}¬∞C (Feels like {w.main?.feels_like}¬∞C)
                        <br />
                        üíß Humidity: {w.main?.humidity}% | üí® Wind: {w.wind?.speed} m/s
                      </div>
                    )}
                  </Popup>
                </Marker>
              );
            })}
          </MapContainer>
        )}
      </div>

      {/* üìã Details sidebar */}
      <div
        style={{
          background: "#fff",
          borderRadius: "18px",
          padding: "24px",
          boxShadow: "0 8px 24px rgba(0,0,0,0.1)",
          border: "1px solid #e0e0e0",
          overflowY: "auto",
          maxHeight: "650px",
        }}
      >
        <h2 style={{ color: "#1565c0", fontWeight: "700", marginBottom: "16px" }}>
          Route Overview
        </h2>

        {routeSpots.map((poi, i) => {
          const seg = segments[i];
          const w = weatherData[i];
          return (
            <div
              key={i}
              style={{
                marginBottom: "20px",
                borderBottom: "1px solid #e0e0e0",
                paddingBottom: "16px",
              }}
            >
              <h3 style={{ color: "#1976d2", fontWeight: "600" }}>
                Stop {i + 1}: {poi.name}
              </h3>
              <p style={{ color: "#444" }}>{poi.description}</p>
              {poi.image && (
                <img
                  src={poi.image}
                  alt={poi.name}
                  style={{
                    width: "100%",
                    maxWidth: "280px",
                    borderRadius: "10px",
                    boxShadow: "0 6px 12px rgba(0,0,0,0.1)",
                    marginBottom: "8px",
                  }}
                />
              )}
              {seg && (
                <p style={{ margin: 0 }}>
                  <strong>To next stop:</strong> {formatDistance(seg.summary.distance)},{" "}
                  {formatDuration(seg.summary.duration)}
                </p>
              )}
              {w && w.weather && (
                <p style={{ margin: "6px 0" }}>
                  <strong>Weather:</strong> {w.weather[0].main} ‚Äì {w.weather[0].description}
                  <br />
                  üå° {w.main?.temp}¬∞C (Feels like {w.main?.feels_like}¬∞C)
                  <br />
