import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import Papa from "papaparse";
import { useNavigate } from "react-router-dom";
import L from "leaflet";

const CENTER = { lat: 14.1467, lng: 121.4708 };
const MAX_TOP_VISITED = 10;

export default function Home() {
  const navigate = useNavigate();
  const [spots, setSpots] = useState([]);
  const [route, setRoute] = useState([]);
  const [weather, setWeather] = useState({ weather: [{ main: "Clear" }] });
  const [userPreferences, setUserPreferences] = useState({
    preferNature: true,
    preferCulture: false,
  });
  const [startTime, setStartTime] = useState("");
  const mapRef = useRef(null); // ðŸ‘ˆ store map reference
  const markerRefs = useRef({}); // ðŸ‘ˆ store marker references

  // Load CSV data (no change)
  useEffect(() => {
    fetch("/data/spots.csv")
      .then(res => res.text())
      .then(csv => {
        Papa.parse(csv, {
          header: true,
          skipEmptyLines: true,
          complete: (results) => {
            const rows = results.data
              .filter(r => r.name && r.lat && r.lng)
              .map(r => ({
                name: r.name.trim(),
                lat: parseFloat(r.lat),
                lng: parseFloat(r.lng),
                visitors: r.visitors.trim() || "Moderate",
                image: r.image || null,
                description: r.description || "No description",
                type: r.tags || "nature",
              }));
            setSpots(rows);
          }
        });
      });
  }, []);

  // ðŸ‘‡ function to handle list item click
  const focusOnSpot = (spot) => {
    if (mapRef.current) {
      mapRef.current.flyTo([spot.lat, spot.lng], 15, { duration: 1.5 });
    }
    if (markerRefs.current[spot.name]) {
      markerRefs.current[spot.name].openPopup();
    }
  };

  // Top visited (same)
  const topVisited = [...spots].sort((a,b) => {
    const visitorRanking = { "Very High": 3, High: 2, Moderate: 1, Low: 0 };
    return (visitorRanking[b.visitors] || 1) - (visitorRanking[a.visitors] || 1);
  }).slice(0, MAX_TOP_VISITED);

  return (
    <div style={{ display: "grid", gap: "20px", gridTemplateColumns: "1fr 1fr", padding: "16px" }}>
      {/* Map */}
      <div className="card" style={{ background: "#fff", borderRadius: "12px", padding: "16px", boxShadow: "0 4px 10px rgba(0,0,0,0.1)" }}>
        <h2 style={{ fontSize: "20px", fontWeight: "bold", marginBottom: "12px", color: "#333", borderBottom: "2px solid #eee", paddingBottom: "6px" }}>
          Majayjay Overview
        </h2>
        <div className="map-wrap" style={{ height: "500px", borderRadius: "10px", overflow: "hidden" }}>
          {spots.length > 0 ? (
            <MapContainer
              center={CENTER}
              zoom={13}
              style={{ height: "100%", width: "100%" }}
              whenCreated={(mapInstance) => { mapRef.current = mapInstance; }} // ðŸ‘ˆ save map instance
            >
              <TileLayer
                attribution='&copy; <a href="https://www.openstreetmap.org/">OSM contributors</a>'
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              />
              {spots.map((s, i) => (
                <Marker
                  key={i}
                  position={[s.lat, s.lng]}
                  ref={(ref) => { if (ref) markerRefs.current[s.name] = ref; }} // ðŸ‘ˆ save marker ref
                >
                  <Popup>
                    <strong style={{ color: "#4f46e5" }}>{s.name}</strong><br />
                    Visitors: {s.visitors}
                    {s.image && (
                      <>
                        <br />
                        <img
                          src={s.image}
                          alt={s.name}
                          style={{ width: "150px", borderRadius: "8px", marginTop: "6px", boxShadow: "0 2px 6px rgba(0,0,0,0.15)" }}
                        />
                      </>
                    )}
                    <p style={{ marginTop: "6px", fontSize: "14px", color: "#555" }}>{s.description}</p>
                  </Popup>
                </Marker>
              ))}
            </MapContainer>
          ) : (
            <div style={{ textAlign: "center", color: "#888", fontStyle: "italic", padding: "20px" }}>No spots available</div>
          )}
        </div>
      </div>

      {/* List */}
      <div className="card" style={{ background: "#fff", borderRadius: "12px", padding: "16px", boxShadow: "0 4px 10px rgba(0,0,0,0.1)" }}>
        <h2 style={{ fontSize: "20px", fontWeight: "bold", marginBottom: "12px", color: "#333", borderBottom: "2px solid #eee", paddingBottom: "6px" }}>
          Top {MAX_TOP_VISITED} Most Visited Places
        </h2>

        <ul style={{ margin: 0, paddingLeft: "20px", color: "#444", lineHeight: "1.6" }}>
          {topVisited.map((s, i) => (
            <li
              key={i}
              style={{ marginBottom: "8px", cursor: "pointer" }}
              onClick={() => focusOnSpot(s)} // ðŸ‘ˆ click handler
            >
              <strong style={{ color: "#4f46e5" }}>{s.name}</strong> â€” {s.visitors} visitors
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
