import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

export default function RoutePlanner() {
  const [pois, setPois] = useState([]);
  const [user, setUser] = useState(null);
  const [showPopup, setShowPopup] = useState(false);
  const [startTime, setStartTime] = useState("now");
  const [scheduledDate, setScheduledDate] = useState("");
  const [mode, setMode] = useState("car"); // default transport
  const navigate = useNavigate();

  useEffect(() => {
    // Load user
    const storedUser = JSON.parse(localStorage.getItem("user"));
    setUser(storedUser);

    // Fetch POIs
    fetch("http://localhost:5000/api/pois")
      .then(res => res.json())
      .then(data => setPois(data))
      .catch(err => {
        console.error("Error fetching POIs:", err);
        setPois([]);
      });
  }, []);

  const handlePlanClick = () => {
    if (!user || !user.preferences || user.preferences.length === 0) {
      alert("Set your preferences first in profile.");
      return;
    }
    setShowPopup(true);
  };

  const recommendRoute = () => {
    const prefs = user.preferences.map(p => p.toLowerCase());

    // Filter POIs based on preferences
    let filtered = pois.filter(poi =>
      prefs.some(pref => poi.tags?.toLowerCase().includes(pref))
    );

    // Transport-based filtering
    if (mode === "foot") {
      // Only close spots (simulate by visitors / tags if no distance)
      filtered = filtered.slice(0, 3);
    } else if (mode === "bike") {
      filtered = filtered.slice(0, 4);
    } else {
      // motor / car → allow more spread
      filtered = filtered.slice(0, 6);
    }

    if (filtered.length === 0) {
      alert("No POIs match your preferences.");
      return;
    }

    setShowPopup(false);

    // Navigate to route details, passing mode + schedule info
    navigate("/routedetails", {
      state: {
        itinerary: filtered,
        transportMode: mode,
        startTime: startTime === "now" ? new Date().toISOString() : scheduledDate
      }
    });
  };

  return (
    <div style={{ textAlign: "center", marginTop: 40 }}>
      <button
        onClick={handlePlanClick}
        style={{
          padding: "12px 24px",
          background: "#2196f3",
          color: "#fff",
          border: "none",
          borderRadius: 8,
          fontSize: 16,
          cursor: "pointer"
        }}
      >
        Plan My Route
      </button>

      {showPopup && (
        <div
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background: "rgba(0,0,0,0.6)",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            zIndex: 9999
          }}
        >
          <div
            style={{
              background: "#fff",
              padding: 20,
              borderRadius: 10,
              width: 320,
              textAlign: "center",
              zIndex: 10000
            }}
          >
            <h3 style={{ marginBottom: 12 }}>Plan Your Trip</h3>

            {/* Start Time */}
            <label>
              <input
                type="radio"
                value="now"
                checked={startTime === "now"}
                onChange={() => setStartTime("now")}
              />{" "}
              Start Now
            </label>
            <br />
            <label>
              <input
                type="radio"
                value="later"
                checked={startTime === "later"}
                onChange={() => setStartTime("later")}
              />{" "}
              Start Later
            </label>
            {startTime === "later" && (
              <input
                type="datetime-local"
                value={scheduledDate}
                onChange={(e) => setScheduledDate(e.target.value)}
                style={{ marginTop: 8, display: "block", width: "100%" }}
              />
            )}

            <hr style={{ margin: "12px 0" }} />

            {/* Transport Mode */}
            <label>Mode of Transport:</label>
            <select
              value={mode}
              onChange={(e) => setMode(e.target.value)}
              style={{ display: "block", margin: "8px auto", padding: 6 }}
            >
              <option value="foot">On Foot</option>
              <option value="bike">Bike</option>
              <option value="motor">Motor</option>
              <option value="car">Car</option>
            </select>

            <div style={{ marginTop: 16 }}>
              <button
                onClick={recommendRoute}
                style={{
                  marginRight: 8,
                  padding: "8px 16px",
                  background: "#3b82f6",
                  color: "#fff",
                  border: "none",
                  borderRadius: 6,
                  cursor: "pointer"
                }}
              >
                Confirm
              </button>
              <button
                onClick={() => setShowPopup(false)}
                style={{
                  padding: "8px 16px",
                  background: "#aaa",
                  color: "#fff",
                  border: "none",
                  borderRadius: 6,
                  cursor: "pointer"
                }}
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
