import React, { useEffect, useState } from "react";
import Papa from "papaparse";
import spotsFile from "../data/spots.csv";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";

export default function MapPage() {
  const [spots, setSpots] = useState([]);
  const [selected, setSelected] = useState(null);

  useEffect(() => {
    Papa.parse(spotsFile, {
      download: true,
      header: true,
      complete: (res) => {
        const rows = res.data.filter(r => r.name && r.lat && r.lng);
        setSpots(rows);
      },
    });
  }, []);

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-2">Majayjay Tourism Map</h2>
      <MapContainer center={[14.1465, 121.4855]} zoom={13} style={{ height: "500px", width: "100%" }}>
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
        {spots.map((spot, idx) => (
          <Marker key={idx} position={[parseFloat(spot.lat), parseFloat(spot.lng)]} eventHandlers={{ click: () => setSelected(spot) }}>
            <Popup>
              <div>
                <h3 className="font-bold">{spot.name}</h3>
                <p>{spot.description}</p>
                {spot.image && (
                  <img src={require(`../data/images/${spot.image}`)} alt={spot.name} className="w-32 h-24 object-cover rounded mt-2" />
                )}
              </div>
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
}
