import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import Papa from "papaparse";

// Fix default marker icons
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require("leaflet/dist/images/marker-icon-2x.png"),
  iconUrl: require("leaflet/dist/images/marker-icon.png"),
  shadowUrl: require("leaflet/dist/images/marker-shadow.png"),
});

const CENTER = { lat: 14.1467, lng: 121.4708 };

export default function MapPage() {
  const [spots, setSpots] = useState([]);
  const [selectedSpot, setSelectedSpot] = useState(null);

  useEffect(() => {
    fetch("/data/spots.csv")
      .then((res) => res.text())
      .then((csvText) => {
        Papa.parse(csvText, {
          header: true,
          skipEmptyLines: true,
          complete: (results) => {
            const rows = results.data
              .filter((r) => r.name && r.lat && r.lng)
              .map((r) => ({
                name: r.name,
                lat: parseFloat(r.lat),
                lng: parseFloat(r.lng),
                description: r.description || "No description available",
                visitors: r.visitors || "Moderate",
                tags: r.tags ? r.tags.split(",").map((t) => t.trim()) : [],
                // Use public folder path directly
                image: r.image ? `/images/${r.image.replace(/^\/+/, "")}` : null,
              }));
            setSpots(rows);
          },
        });
      });
  }, []);

  return (
    <div style={{ display: "flex", height: "80vh", width: "100%" }}>
      {/* Map section */}
      <div style={{ flex: 1 }}>
        <MapContainer
          center={CENTER}
          zoom={13}
          style={{ height: "100%", width: "100%" }}
        >
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/">OSM contributors</a>'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          {spots.map((spot, i) => (
            <Marker
              key={i}
              position={[spot.lat, spot.lng]}
              eventHandlers={{
                click: () => setSelectedSpot(spot),
              }}
            />
          ))}
        </MapContainer>
      </div>

      {/* Right-side info panel */}
      {selectedSpot && (
        <div
          style={{
            width: "40%",
            background: "#fff",
            padding: "20px",
            overflowY: "auto",
            borderLeft: "2px solid #ccc",
          }}
        >
          <button
            onClick={() => setSelectedSpot(null)}
            style={{
              float: "right",
              background: "red",
              color: "white",
              border: "none",
              borderRadius: "5px",
              padding: "4px 8px",
              cursor: "pointer",
            }}
          >
            âœ–
          </button>

          <h2 style={{ fontSize: "1.8rem", fontWeight: "bold", marginBottom: "10px" }}>
            {selectedSpot.name}
          </h2>

          {selectedSpot.image && (
            <img
              src={selectedSpot.image}
              alt={selectedSpot.name}
              style={{ width: "100%", borderRadius: "10px", marginBottom: "15px" }}
            />
          )}

          <p>
            <strong>Visitors:</strong> {selectedSpot.visitors}
          </p>

          <p style={{ marginTop: "10px" }}>{selectedSpot.description}</p>

          {selectedSpot.tags.length > 0 && (
            <p style={{ marginTop: "10px" }}>
              <strong>Tags:</strong> {selectedSpot.tags.join(", ")}
            </p>
          )}
        </div>
      )}
    </div>
  );
}
