import React, { useState, useEffect } from "react";

export default function RoutePlanner({ spots }) {
  const [route, setRoute] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleGenerateRoute = async () => {
    if (!spots.length) return alert("No spots to generate route!");

    setLoading(true);

    try {
      const scoredSpots = await Promise.all(
        spots.map(async (spot) => {
          const response = await fetch("http://localhost:5000/predict", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
              user_preference_nature: spot.tags.includes("nature") ? 1 : 0,
              user_preference_culture: spot.tags.includes("culture") ? 1 : 0,
              weather_clear: 1, // can be dynamic using your WeatherWidget
              distance_to_previous: Math.floor(Math.random() * 10),
            }),
          });

          const data = await response.json();
          let score = 0;
          switch (data.predicted_frequency) {
            case "Very High": score = 4; break;
            case "High": score = 3; break;
            case "Moderate": score = 2; break;
            case "Low": score = 1; break;
            default: score = 0;
          }

          return { ...spot, score };
        })
      );

      scoredSpots.sort((a, b) => b.score - a.score);
      setRoute(scoredSpots);
    } catch (err) {
      console.error("Error generating route:", err);
      alert("Failed to generate route.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ marginTop: 12 }}>
      <button
        onClick={handleGenerateRoute}
        disabled={loading}
        style={{ padding: "8px 16px", background: "#3b82f6", color: "#fff", borderRadius: 6 }}
      >
        {loading ? "Generating..." : "Generate Recommended Route"}
      </button>

      {route.length > 0 && (
        <ul style={{ marginTop: 12, paddingLeft: 20 }}>
          {route.map((spot, i) => (
            <li key={i}>
              {spot.name} â€” Score: {spot.score}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

// Example WeatherWidget
export function WeatherWidget() {
  const [weather, setWeather] = useState("Loading...");

  useEffect(() => {
    const opts = ["Sunny", "Cloudy", "Rainy"];
    const idx = (new Date().getDate() + new Date().getMonth()) % opts.length;
    setWeather(opts[idx]);
  }, []);

  return (
    <div style={{ marginTop: 12, padding: 10, borderRadius: 8, background: "#eff6ff", border: "1px solid #e0f2fe" }}>
      <div style={{ fontSize: 13, color: "#0f172a", fontWeight: 600 }}>ðŸŒ¤ Weather (Majayjay)</div>
      <div style={{ marginTop: 6, color: "#475569" }}>{weather}</div>
    </div>
  );
}
