import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from "react-leaflet";
import L from "leaflet";
import { useNavigate, useLocation } from "react-router-dom";
import { SortableContainer, SortableElement, arrayMove } from "react-sortable-hoc";
import "leaflet/dist/leaflet.css";

const createColoredIcon = (color) =>
  new L.Icon({
    iconUrl: `https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-${color}.png`,
    shadowUrl:
      "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png",
    iconSize: [25, 41],
    iconAnchor: [12, 41],
    popupAnchor: [1, -34],
    shadowSize: [41, 41],
  });

const CATEGORY_COLOR = {
  "Nature & Outdoors": "green",
  "Culture & Heritage": "yellow",
  "Farming & Agriculture": "orange",
  "Food & Drink": "blue",
  "Accommodation & Stay": "red",
};

const determineCategory = (tags = "") => {
  const t = tags.toLowerCase().split(",").map((x) => x.trim());
  if (t.some((x) => ["history", "culture", "religion", "architecture", "spiritual"].includes(x)))
    return "Culture & Heritage";
  if (t.some((x) => ["farm", "agri", "organic", "animal"].includes(x))) return "Farming & Agriculture";
  if (t.some((x) => ["resort", "motel", "hotel", "lodge"].includes(x))) return "Accommodation & Stay";
  if (t.some((x) => ["restaurant", "coffee", "food"].includes(x))) return "Food & Drink";
  return "Nature & Outdoors";
};

// ------------------- Sortable POI Item -------------------
const SortableItem = SortableElement(({ poi, index, setActiveIndex }) => (
  <div
    style={{
      display: "flex",
      alignItems: "center",
      border: "1px solid #eee",
      borderRadius: 6,
      padding: 8,
      marginBottom: 6,
      background: "#f9f9f9",
      cursor: "grab",
    }}
    onClick={() => setActiveIndex(index)}
  >
    <span style={{ fontWeight: 700, marginRight: 8 }}>{index + 1}.</span>
    <div>
      <strong>{poi.name}</strong>
      <p style={{ fontSize: 13, margin: 0 }}>{poi.description}</p>
    </div>
  </div>
));

const SortableList = SortableContainer(({ items, setActiveIndex }) => {
  return (
    <div>
      {items.map((poi, index) => (
        <SortableItem
          key={poi.id}
          index={index}
          poi={poi}
          setActiveIndex={setActiveIndex}
        />
      ))}
    </div>
  );
});

// ------------------- Main Component -------------------
export default function RouteDetails() {
  const location = useLocation();
  const navigate = useNavigate();

  const filteredPois = location.state?.filteredPois || [];
  const startPoint = location.state?.startPoint || null;
  const transportMode = location.state?.transportMode || "driving-car";
  const recommendedPois = location.state?.recommendedPois || [];
  const user = JSON.parse(localStorage.getItem("user"));

  const [selectedPois, setSelectedPois] = useState(filteredPois);
  const [userLocation, setUserLocation] = useState(startPoint);
  const [activeIndex, setActiveIndex] = useState(0);
  const [tracking, setTracking] = useState(false);
  const [likedPois, setLikedPois] = useState({});

  const watchIdRef = useRef(null);
  const ORS_API_KEY = "YOUR_ORS_API_KEY_HERE";

  // ---------- User location tracking ----------
  useEffect(() => {
    if (!tracking) return;

    if ("geolocation" in navigator) {
      watchIdRef.current = navigator.geolocation.watchPosition(
        (pos) =>
          setUserLocation({
            lat: pos.coords.latitude,
            lng: pos.coords.longitude,
          }),
        () => console.error("Unable to get location"),
        { enableHighAccuracy: true }
      );
    }
    return () => {
      if (watchIdRef.current) navigator.geolocation.clearWatch(watchIdRef.current);
    };
  }, [tracking]);

  // ---------- LIKE / DISLIKE ----------
  const handleLike = async (poiId, liked) => {
    try {
      const token = user?.token;
      if (!token) return;

      const response = await fetch("http://localhost:5000/api/user_feedback", {
        method: "POST",
        headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
        body: JSON.stringify({ poi_id: poiId, liked }),
      });
      const data = await response.json();
      if (data.success) setLikedPois((prev) => ({ ...prev, [poiId]: liked }));
    } catch (err) {
      console.error(err);
    }
  };

  // ---------- STOP TOUR ----------
  const stopTour = () => {
    if (watchIdRef.current) navigator.geolocation.clearWatch(watchIdRef.current);
    setTracking(false);
    setActiveIndex(0);
  };

  // ---------- NEXT STOP ----------
  const handleNextStop = () => {
    if (activeIndex + 1 < selectedPois.length) {
      setActiveIndex((prev) => prev + 1);
    } else {
      stopTour();
      alert("Tour finished!");
    }
  };

  // ---------- DRAG & REORDER ----------
  const onSortEnd = ({ oldIndex, newIndex }) => {
    setSelectedPois(arrayMove(selectedPois, oldIndex, newIndex));
  };

  if (!userLocation) return <p>Loading route...</p>;

  return (
    <div style={{ display: "grid", gridTemplateColumns: "1.3fr 0.7fr", gap: 24, padding: 24 }}>
      {/* ---------------- MAP ---------------- */}
      <div style={{ height: 650, borderRadius: 18, overflow: "hidden" }}>
        <MapContainer center={[userLocation.lat, userLocation.lng]} zoom={13} style={{ height: "100%", width: "100%" }}>
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
          {/* Markers */}
          {selectedPois.map((poi) => {
            const category = determineCategory(poi.tags);
            return (
              <Marker
                key={poi.id}
                position={[poi.lat, poi.lng]}
                icon={createColoredIcon(CATEGORY_COLOR[category] || "blue")}
              >
                <Popup>
                  <strong>{poi.name}</strong>
                  <br />
                  {poi.description}
                </Popup>
              </Marker>
            );
          })}
          {/* Polyline connecting POIs */}
          <Polyline positions={selectedPois.map((poi) => [poi.lat, poi.lng])} color="#1976d2" weight={4} />
          {/* Line from user to active POI */}
          {tracking && selectedPois[activeIndex] && (
            <Polyline
              positions={[
                [userLocation.lat, userLocation.lng],
                [selectedPois[activeIndex].lat, selectedPois[activeIndex].lng],
              ]}
              color="#4caf50"
              weight={3}
              dashArray="5,10"
            />
          )}
        </MapContainer>
      </div>

      {/* ---------------- SIDEBAR ---------------- */}
      <div style={{ background: "#fff", borderRadius: 18, padding: 24, boxShadow: "0 8px 24px rgba(0,0,0,0.1)" }}>
        {/* Recommended POIs */}
        {recommendedPois.length > 0 && (
          <div style={{ marginBottom: 24 }}>
            <h3>Recommended POIs</h3>
            {recommendedPois.map((poi) => (
              <div
                key={poi.id}
                style={{
                  border: "1px solid #eee",
                  borderRadius: 6,
                  padding: 8,
                  marginBottom: 6,
                  background: "#f9f9f9",
                  cursor: "pointer",
                }}
                onClick={() => {
                  const index = selectedPois.findIndex((p) => p.id === poi.id);
                  if (index !== -1) setActiveIndex(index);
                }}
              >
                <strong>{poi.name}</strong>
                <p style={{ fontSize: 13, margin: 0 }}>{poi.description}</p>
              </div>
            ))}
          </div>
        )}

        {/* Tracking / Selected POIs */}
        {tracking ? (
          <>
            <h3>Now Visiting:</h3>
            <strong>{selectedPois[activeIndex]?.name}</strong>
            <p>{selectedPois[activeIndex]?.description}</p>

            <div style={{ display: "flex", gap: 8, marginTop: 12 }}>
              <button
                onClick={() => handleLike(selectedPois[activeIndex].id, 1)}
                style={{
                  flex: 1,
                  padding: 10,
                  background: likedPois[selectedPois[activeIndex]?.id] === 1 ? "#4caf50" : "#e0e0e0",
                  color: likedPois[selectedPois[activeIndex]?.id] === 1 ? "#fff" : "#000",
                  border: "none",
                  borderRadius: 6,
                  cursor: "pointer",
                  fontWeight: 600,
                }}
              >
                Like
              </button>
              <button
                onClick={() => handleLike(selectedPois[activeIndex].id, 0)}
                style={{
                  flex: 1,
                  padding: 10,
                  background: likedPois[selectedPois[activeIndex]?.id] === 0 ? "#f44336" : "#e0e0e0",
                  color: likedPois[selectedPois[activeIndex]?.id] === 0 ? "#fff" : "#000",
                  border: "none",
                  borderRadius: 6,
                  cursor: "pointer",
                  fontWeight: 600,
                }}
              >
                Dislike
              </button>
            </div>

            <button
              onClick={handleNextStop}
              style={{
                width: "100%",
                padding: 12,
                background: "#1976d2",
                color: "#fff",
                border: "none",
                borderRadius: 8,
                marginTop: 12,
                fontWeight: 600,
                cursor: "pointer",
              }}
            >
              Next Stop
            </button>

            <button
              onClick={stopTour}
              style={{
                width: "100%",
                padding: 12,
                background: "#f44336",
                color: "#fff",
                border: "none",
                borderRadius: 8,
                marginTop: 10,
                fontWeight: 600,
                cursor: "pointer",
              }}
            >
              Cancel Tour
            </button>
          </>
        ) : (
          <>
            <h2>Selected POIs (Drag to reorder)</h2>
            <SortableList items={selectedPois} onSortEnd={onSortEnd} setActiveIndex={setActiveIndex} />
            <button
              onClick={() => setTracking(true)}
              style={{
                width: "100%",
                padding: 12,
                background: "#4caf50",
                color: "#fff",
                border: "none",
                borderRadius: 8,
                fontWeight: 600,
                cursor: "pointer",
              }}
            >
              Start Tour
            </button>
          </>
        )}

        <button
          onClick={() => navigate("/home")}
          style={{
            width: "100%",
            padding: 12,
            background: "#9e9e9e",
            color: "#fff",
            border: "none",
            borderRadius: 8,
            fontWeight: 600,
            cursor: "pointer",
            marginTop: 12,
          }}
        >
          Back to Home
        </button>
      </div>
    </div>
  );
}
