import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import "../leafletfix";
import Papa from "papaparse";

const CENTER = { lat: 14.1467, lng: 121.4708 };

export default function MapPage() {
  const [spots, setSpots] = useState([]);
  const [selected, setSelected] = useState(null);

  useEffect(() => {
    Papa.parse("/data/spots.csv", {
      download: true,
      header: true,
      complete: (res) => {
        const rows = res.data
          .filter((r) => r.name && r.lat && r.lng)
          .map((r) => ({
            name: r.name,
            lat: parseFloat(r.lat),
            lng: parseFloat(r.lng),
            description: r.description || "No description available",
            tags: r.tags?.split(",").map(t => t.trim()).filter(Boolean),
            visitors: r.visitors || "Moderate",
            image: r.image ? `/images/${r.image}` : null,
          }));
        setSpots(rows);
      },
    });
  }, []);

  return (
    <div>
      <div style={{ height: selected ? "50vh" : "80vh" }}>
        <MapContainer center={CENTER} zoom={13} className="h-full w-full">
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/">OSM</a>'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          {spots.map((s, i) => (
            <Marker key={i} position={[s.lat, s.lng]} eventHandlers={{ click: () => setSelected(s) }} />
          ))}
        </MapContainer>
      </div>

      {selected && (
        <div className="spot-description p-4 border-t bg-white shadow-md">
          <button onClick={() => setSelected(null)}>âœ– Close</button>
          <h2>{selected.name}</h2>
          {selected.image && <img src={selected.image} alt={selected.name} style={{ width: "100%", maxHeight: "250px", objectFit: "cover" }} />}
          <p>{selected.description}</p>
          {selected.tags?.length && <p><strong>Tags:</strong> {selected.tags.join(", ")}</p>}
          <p><strong>Visitors:</strong> {selected.visitors}</p>
        </div>
      )}
    </div>
  );
}
