import React, { useEffect, useState } from "react";

export default function ProfilePage() {
  const storedUser = JSON.parse(localStorage.getItem("user"));
  const token = localStorage.getItem("token");

  const [user, setUser] = useState(null);
  const [preferences, setPreferences] = useState([]);
  const [editing, setEditing] = useState(false);

  const preferenceOptions = ["Nature", "Culture", "Food", "History", "Adventure"];

  useEffect(() => {
    if (!storedUser) return;

    fetch(`http://localhost:5000/api/users/email/${storedUser.email}`, {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => {
        setUser(data);

        let prefs = [];
        if (Array.isArray(data.preferences)) {
          prefs = data.preferences;
        } else if (typeof data.preferences === "string" && data.preferences.trim() !== "") {
          try {
            // try parsing JSON string first
            prefs = JSON.parse(data.preferences);
            if (!Array.isArray(prefs)) {
              prefs = data.preferences.split(",").map((p) => p.trim());
            }
          } catch {
            prefs = data.preferences.split(",").map((p) => p.trim());
          }
        }
        setPreferences(prefs);
      })
      .catch((err) => console.error("Error fetching user:", err));
  }, [storedUser, token]);

  const handleSave = () => {
    if (!user) return;

    fetch(`http://localhost:5000/api/users/${user.id}/preferences`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ preferences }),
    })
      .then((res) => res.json())
      .then(() => {
        setEditing(false);
        setUser({ ...user, preferences });
      })
      .catch((err) => console.error("Error updating preferences:", err));
  };

  if (!user) return <div>Loading...</div>;

  return (
    <div style={{ padding: "24px", maxWidth: "600px", margin: "0 auto" }}>
      <h1>Profile</h1>

      <p><strong>Username:</strong> {user.username}</p>
      <p><strong>Email:</strong> {user.email}</p>
      <p><strong>Full Name:</strong> {`${user.first_name || ""} ${user.middle_name || ""} ${user.last_name || ""}`}</p>
      <p><strong>Age:</strong> {user.age || "Not provided"}</p>

      <div style={{ marginTop: "16px" }}>
        <strong>Preferences:</strong>
        {editing ? (
          <div>
            {preferenceOptions.map((option) => (
              <label key={option} style={{ display: "block", marginTop: "6px" }}>
                <input
                  type="checkbox"
                  checked={preferences.includes(option)}
                  onChange={() =>
                    setPreferences((prev) =>
                      prev.includes(option)
                        ? prev.filter((p) => p !== option)
                        : [...prev, option]
                    )
                  }
                />
                {option}
              </label>
            ))}
            <div style={{ marginTop: "12px" }}>
              <button onClick={handleSave}>Save</button>
              <button
                onClick={() => {
                  setEditing(false);
                  setPreferences(user.preferences || []);
                }}
              >
                Cancel
              </button>
            </div>
          </div>
        ) : (
          <ul>
            {preferences.length > 0 ? (
              preferences.map((p, i) => <li key={i}>{p}</li>)
            ) : (
              <li>No preferences set.</li>
            )}
          </ul>
        )}
      </div>

      {!editing && <button onClick={() => setEditing(true)}>Edit Preferences</button>}
    </div>
  );
}
