import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, Marker, Polyline, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import { useNavigate, useLocation } from "react-router-dom";

export default function RouteDetails() {
  const navigate = useNavigate();
  const location = useLocation();
  const { itinerary, startTime } = location.state || { itinerary: [], startTime: new Date() };

  const [currentIndex, setCurrentIndex] = useState(0);
  const [currentTime, setCurrentTime] = useState(startTime);

  if (!itinerary || itinerary.length === 0) {
    return <div>No itinerary found. Go back and generate a route first.</div>;
  }

  const currentPOI = itinerary[currentIndex];
  const nextPOI = itinerary[currentIndex + 1];

  const handleNext = () => {
    if (currentIndex < itinerary.length - 1) {
      setCurrentIndex(currentIndex + 1);
      // Optionally update time assuming 1h per POI
      const nextTime = new Date(currentTime);
      nextTime.setHours(nextTime.getHours() + 1);
      setCurrentTime(nextTime);
    } else {
      alert("You have reached the end of the itinerary!");
    }
  };

  const handleFinish = () => {
    const confirmFinish = window.confirm("Are you sure you want to finish the itinerary?");
    if (confirmFinish) {
      navigate("/");
    }
  };

  const handleCancel = () => {
    const confirmCancel = window.confirm("Are you sure you want to cancel the itinerary?");
    if (confirmCancel) {
      navigate("/");
    }
  };

  return (
    <div style={{ display: "grid", gap: 16 }}>
      <h2>Tourism Itinerary</h2>
      <div>
        <strong>Current Time:</strong> {new Date(currentTime).toLocaleTimeString()}
      </div>
      <div>
        <strong>Current Stop:</strong> {currentPOI.name} ({currentPOI.visitors} visitors, Predicted: {currentPOI.predicted_frequency})
      </div>
      {currentPOI.image && (
        <img src={currentPOI.image} alt={currentPOI.name} style={{ width: "300px", borderRadius: "8px" }} />
      )}
      <p>{currentPOI.description}</p>

      <div style={{ height: "400px" }}>
        <MapContainer center={[currentPOI.lat, currentPOI.lng]} zoom={14} style={{ height: "100%", width: "100%" }}>
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" attribution="&copy; OSM" />
          <Marker position={[currentPOI.lat, currentPOI.lng]}>
            <Popup>{currentPOI.name}</Popup>
          </Marker>
          {nextPOI && (
            <>
              <Marker position={[nextPOI.lat, nextPOI.lng]}>
                <Popup>Next: {nextPOI.name}</Popup>
              </Marker>
              <Polyline positions={[[currentPOI.lat, currentPOI.lng], [nextPOI.lat, nextPOI.lng]]} color="blue" />
            </>
          )}
        </MapContainer>
      </div>

      <div style={{ display: "flex", gap: 12 }}>
        {currentIndex < itinerary.length - 1 && <button onClick={handleNext}>Next Stop</button>}
        <button onClick={handleCancel}>Cancel</button>
        {currentIndex === itinerary.length - 1 && <button onClick={handleFinish}>Finish</button>}
      </div>
    </div>
  );
}
