import React, { useState, useEffect, useRef } from "react";

export default function AdminReports() {
  const [activeReport, setActiveReport] = useState("users");
  const [reportData, setReportData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [filter, setFilter] = useState("");
  const reportRef = useRef();

  useEffect(() => {
    fetchReport(activeReport);
  }, [activeReport]);

  const fetchReport = async (type) => {
    setLoading(true);
    setError(null);
    try {
      const token = localStorage.getItem("token"); // JWT
      const res = await fetch(`http://localhost:5000/api/reports?type=${type}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (!res.ok) throw new Error(`Failed to fetch report: ${res.status}`);
      const data = await res.json();
      setReportData(data);
    } catch (err) {
      console.error(err);
      setError(err.message);
      setReportData(null);
    } finally {
      setLoading(false);
    }
  };

  const handlePrint = () => {
    if (!reportRef.current) return;
    const WinPrint = window.open("", "", "width=900,height=650");
    WinPrint.document.write(`<html><head><title>Report</title></head><body>${reportRef.current.innerHTML}</body></html>`);
    WinPrint.document.close();
    WinPrint.focus();
    WinPrint.print();
    WinPrint.close();
  };

  const filterData = (array, keys) => {
    if (!filter.trim()) return array;
    return array.filter((item) =>
      keys.some((key) =>
        String(item[key] || "").toLowerCase().includes(filter.toLowerCase())
      )
    );
  };

  const renderReport = () => {
    if (loading) return <p>Loading...</p>;
    if (error) return <p style={{ color: "red" }}>{error}</p>;
    if (!reportData) return <p>No report available.</p>;

    if (activeReport === "users") {
      const users = filterData(reportData.users || [], ["id", "username", "email"]);
      return (
        <div>
          <p><strong>Total Users:</strong> {reportData.totalUsers}</p>
          <input
            type="text"
            placeholder="Filter by ID, Username, Email"
            value={filter}
            onChange={(e) => setFilter(e.target.value)}
            style={{ marginBottom: 10, padding: 5, width: "300px" }}
          />
          <table border="1" cellPadding="5" style={{ borderCollapse: "collapse" }}>
            <thead>
              <tr>
                <th>ID</th><th>Username</th><th>Email</th><th>Role</th>
              </tr>
            </thead>
            <tbody>
              {users.map((user) => (
                <tr key={user.id}>
                  <td>{user.id}</td>
                  <td>{user.username}</td>
                  <td>{user.email}</td>
                  <td>{user.role}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      );
    }

    if (activeReport === "pois") {
      const pois = filterData(reportData.pois || [], ["id", "name", "visitors"]);
      return (
        <div>
          <p><strong>Total POIs:</strong> {reportData.totalPOIs}</p>
          <input
            type="text"
            placeholder="Filter by ID, Name, Visitors"
            value={filter}
            onChange={(e) => setFilter(e.target.value)}
            style={{ marginBottom: 10, padding: 5, width: "300px" }}
          />
          <table border="1" cellPadding="5" style={{ borderCollapse: "collapse" }}>
            <thead>
              <tr>
                <th>ID</th><th>Name</th><th>Visitors</th>
              </tr>
            </thead>
            <tbody>
              {pois.map((poi) => (
                <tr key={poi.id}>
                  <td>{poi.id}</td>
                  <td>{poi.name}</td>
                  <td>{poi.visitors}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      );
    }

    return <p>No report available.</p>;
  };

  return (
    <div style={{ marginLeft: 220, padding: 20 }}>
      <h2>Admin Reports</h2>

      <div style={{ marginBottom: 20 }}>
        <button
          onClick={() => { setActiveReport("users"); setFilter(""); }}
          style={{ marginRight: 10, backgroundColor: activeReport === "users" ? "#ccc" : "#fff" }}
        >
          User Reports
        </button>
        <button
          onClick={() => { setActiveReport("pois"); setFilter(""); }}
          style={{ backgroundColor: activeReport === "pois" ? "#ccc" : "#fff" }}
        >
          POI Reports
        </button>
      </div>

      <div ref={reportRef} style={{ marginBottom: 20 }}>
        {renderReport()}
      </div>

      <button onClick={handlePrint}>Print / Save as PDF</button>
    </div>
  );
}
