import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import Papa from "papaparse";

const CENTER = { lat: 14.1467, lng: 121.4708 };

// Average travel time per km (minutes)
const AVG_TRAVEL_SPEED = 20; // Assume 20 km/h, 3 min per km

export default function Home() {
  const [spots, setSpots] = useState([]);
  const [route, setRoute] = useState([]);
  const [itinerary, setItinerary] = useState([]);
  const [userPreferences, setUserPreferences] = useState({
    preferNature: true,
    preferCulture: false,
  });

  useEffect(() => {
    fetch("/data/spots.csv")
      .then((res) => res.text())
      .then((csv) => {
        Papa.parse(csv, {
          header: true,
          skipEmptyLines: true,
          complete: (results) => {
            const rows = results.data
              .filter((r) => r.name && r.lat && r.lng)
              .map((r) => ({
                name: r.name.trim(),
                lat: parseFloat(r.lat),
                lng: parseFloat(r.lng),
                visitors: r.visitors.trim() || "Moderate",
                image: r.image || null,
                description: r.description || "No description",
                type: r.tags || "nature",
              }));
            setSpots(rows);
          },
        });
      });
  }, []);

  const calculateTravelTime = (from, to) => {
    if (!from || !to) return 0;
    const R = 6371; // km
    const dLat = ((to.lat - from.lat) * Math.PI) / 180;
    const dLng = ((to.lng - from.lng) * Math.PI) / 180;
    const a =
      Math.sin(dLat / 2) ** 2 +
      Math.cos((from.lat * Math.PI) / 180) *
        Math.cos((to.lat * Math.PI) / 180) *
        Math.sin(dLng / 2) ** 2;
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    const distance = R * c; // km
    return (distance / AVG_TRAVEL_SPEED) * 60; // minutes
  };

  const generateRoute = () => {
    if (!spots.length) return;

    // Score spots
    const scoredSpots = spots.map((spot, i) => {
      let score = 0;
      if (userPreferences.preferNature && spot.type.includes("nature")) score += 0.3;
      if (userPreferences.preferCulture && spot.type.includes("history")) score += 0.2;
      const visitorRanking = { "Very High": 3, High: 2, Moderate: 1, Low: 0 };
      score += visitorRanking[spot.visitors] || 1;
      return { ...spot, score };
    });

    scoredSpots.sort((a, b) => b.score - a.score);
    setRoute(scoredSpots);

    // Build itinerary with travel and activity time
    const startTime = new Date();
    let currentTime = new Date(startTime);
    let previousSpot = null;
    const itineraryPlan = [];

    scoredSpots.forEach((spot) => {
      // Travel time
      const travelMinutes = previousSpot ? calculateTravelTime(previousSpot, spot) : 0;
      currentTime.setMinutes(currentTime.getMinutes() + travelMinutes);

      // Check for lunch break (12:00–13:00)
      if (currentTime.getHours() === 12) {
        const lunchDuration = 60; // minutes
        itineraryPlan.push({
          name: "Lunch Break",
          type: "restaurant",
          arrival: new Date(currentTime),
          departure: new Date(currentTime.getTime() + lunchDuration * 60000),
          durationMinutes: lunchDuration,
        });
        currentTime.setMinutes(currentTime.getMinutes() + lunchDuration);
      }

      // Activity duration
      let durationMinutes = 60; // default
      if (spot.type.includes("nature")) durationMinutes = 120;
      else if (spot.type.includes("restaurant")) durationMinutes = 60;
      else if (spot.type.includes("cultural") || spot.type.includes("history")) durationMinutes = 90;

      itineraryPlan.push({
        ...spot,
        arrival: new Date(currentTime),
        departure: new Date(currentTime.getTime() + durationMinutes * 60000),
        durationMinutes,
      });

      currentTime.setMinutes(currentTime.getMinutes() + durationMinutes);
      previousSpot = spot;
    });

    setItinerary(itineraryPlan);
    console.log("Itinerary:", itineraryPlan);
  };

  const formatTime = (date) =>
    date.toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" });

  return (
    <div style={{ display: "grid", gap: 16, gridTemplateColumns: "1fr 1fr" }}>
      <div className="card">
        <h2>Majayjay Map & Circuit</h2>
        <div style={{ height: "500px" }}>
          {route.length > 0 && (
            <MapContainer center={CENTER} zoom={13} style={{ height: "100%", width: "100%" }}>
              <TileLayer
                attribution='&copy; <a href="https://www.openstreetmap.org/">OSM contributors</a>'
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              />
              {itinerary.map((s, i) => (
                <Marker key={i} position={[s.lat || CENTER.lat, s.lng || CENTER.lng]}>
                  <Popup>
                    <strong>{s.name}</strong>
                    {s.type !== "restaurant" && (
                      <>
                        <br />Arrival: {formatTime(s.arrival)}
                        <br />Departure: {formatTime(s.departure)}
                      </>
                    )}
                    <p>{s.description}</p>
                  </Popup>
                </Marker>
              ))}
            </MapContainer>
          )}
        </div>
      </div>

      <div className="card">
        <h2>Tourism Circuit Itinerary</h2>
        <button onClick={generateRoute}>Generate Route</button>
        <ul style={{ margin: 0, paddingLeft: 18 }}>
          {itinerary.map((s, i) => (
            <li key={i}>
              <strong>{s.name}</strong> — {s.type} <br />
              Arrival: {formatTime(s.arrival)}, Departure: {formatTime(s.departure)} ({Math.round(s.durationMinutes / 60)} hrs)
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
