import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import { useNavigate, useLocation } from "react-router-dom";

export default function RouteDetails() {
  const location = useLocation();
  const navigate = useNavigate();
  const routeSpots = location.state?.itinerary || []; // full itinerary
  const [currentIndex, setCurrentIndex] = useState(0);
  const [segmentData, setSegmentData] = useState(null); // route from current â†’ next POI
  const [loading, setLoading] = useState(true);
  const [weather, setWeather] = useState(null);

  // ðŸ”‘ Your keys (kept as-is)
  const ORS_API_KEY =
    "eyJvcmciOiI1YjNjZTM1OTc4NTExMTAwMDFjZjYyNDgiLCJpZCI6ImJhNWU3NmIxOTRmMzQyYTg4NzY4MDc2MzVmZDY1ZDRhIiwiaCI6Im11cm11cjY0In0=";
  const WEATHER_API_KEY = "13d7213054a676ccdd935415b00fac6f";

  const currentPOI = routeSpots[currentIndex];
  const nextPOI = routeSpots[currentIndex + 1];

  // Fetch ORS route between two POIs (try car â†’ walking â†’ fallback straight line)
  const fetchRouteSegment = async (from, to) => {
    if (!from || !to) return null;

    const tryFetch = async (profile) => {
      const url = `https://api.openrouteservice.org/v2/directions/${profile}/geojson`;
      console.log(`Requesting ORS route with profile=${profile}`);

      const res = await fetch(url, {
        method: "POST",
        headers: {
          Authorization: ORS_API_KEY,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          coordinates: [
            [from.lng, from.lat], // ORS requires [lng, lat]
            [to.lng, to.lat],
          ],
          preference: "fastest",
          instructions: false,
        }),
      });

      const data = await res.json();
      console.log("ORS response:", data);

      if (!res.ok || !data.features || !data.features.length) {
        throw new Error(`No route found for ${profile}`);
      }

      const coords = data.features[0].geometry.coordinates.map(
        ([lng, lat]) => [lat, lng] // leaflet wants [lat, lng]
      );
      const summary = data.features[0].properties.summary;
      return { coords, summary, profile };
    };

    try {
      return await tryFetch("driving-car");
    } catch (err1) {
      console.warn("Driving route failed:", err1.message);
      try {
        return await tryFetch("foot-walking");
      } catch (err2) {
        console.warn("Walking route failed:", err2.message);
        return {
          coords: [
            [from.lat, from.lng],
            [to.lat, to.lng],
          ],
          summary: { distance: 0, duration: 0 },
          profile: "none",
        };
      }
    }
  };

  // Fetch weather for current POI
  const fetchWeather = async (poi) => {
    if (!poi) return;
    try {
      const res = await fetch(
        `https://api.openweathermap.org/data/2.5/weather?lat=${poi.lat}&lon=${poi.lng}&units=metric&appid=${WEATHER_API_KEY}`
      );
      const data = await res.json();
      setWeather(data);
    } catch (err) {
      console.error("Weather fetch failed:", err);
      setWeather(null);
    }
  };

  // Load route segment + weather whenever index changes
  useEffect(() => {
    const loadSegment = async () => {
      setLoading(true);
      if (currentPOI && nextPOI) {
        const segment = await fetchRouteSegment(currentPOI, nextPOI);
        setSegmentData(segment);
      } else {
        setSegmentData(null);
      }
      await fetchWeather(currentPOI);
      setLoading(false);
    };
    loadSegment();
  }, [currentIndex, routeSpots]);

  // Controls
  const nextPOIHandler = () => {
    if (currentIndex < routeSpots.length - 1) setCurrentIndex(currentIndex + 1);
    else alert("You have reached the end of the route!");
  };

  const finishRoute = () => {
    if (window.confirm("Finish route?")) {
      alert("Route completed!");
      navigate("/home");
    }
  };

  const cancelRoute = () => {
    if (window.confirm("Cancel route? You will lose progress.")) {
      alert("Route cancelled.");
      navigate("/home");
    }
  };

  // Helpers
  const formatDistance = (meters) => `${(meters / 1000).toFixed(2)} km`;
  const formatDuration = (seconds) => `${Math.floor(seconds / 60)} min`;

  if (!currentPOI) return <p>No route available.</p>;

  return (
    <div
      style={{
        display: "grid",
        gridTemplateColumns: "1.2fr 0.8fr",
        gap: 20,
        padding: 20,
        minHeight: "100vh",
        background: "linear-gradient(135deg, #e0f7fa, #f1f8e9)",
      }}
    >
      {/* MAP */}
      <div
        style={{
          height: "600px",
          borderRadius: 12,
          overflow: "hidden",
          boxShadow: "0 6px 18px rgba(0,0,0,0.15)",
        }}
      >
        {loading ? (
          <p style={{ textAlign: "center", marginTop: "40%" }}>Loading route...</p>
        ) : (
          <MapContainer
            center={[currentPOI.lat, currentPOI.lng]}
            zoom={14}
            style={{ height: "100%", width: "100%" }}
          >
            <TileLayer
              attribution='&copy; <a href="https://www.openstreetmap.org/">OSM contributors</a>'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />

            {/* ROUTE line */}
            {segmentData &&
              (segmentData.profile === "none" ? (
                <Polyline positions={segmentData.coords} color="red" dashArray="8,6" />
              ) : (
                <Polyline positions={segmentData.coords} color="blue" />
              ))}

            {/* Markers */}
            {[currentPOI, nextPOI]
              .filter(Boolean)
              .map((poi, i) => (
                <Marker key={i} position={[poi.lat, poi.lng]}>
                  <Popup>
                    <strong>{poi.name}</strong>
                    <p>{poi.description}</p>
                    {poi.image && (
                      <img
                        src={poi.image}
                        alt={poi.name}
                        style={{ width: "160px", borderRadius: 8, marginTop: 6 }}
                      />
                    )}
                  </Popup>
                </Marker>
              ))}
          </MapContainer>
        )}
      </div>

      {/* INFO PANEL */}
      <div
        style={{
          background: "#fff",
          borderRadius: 12,
          padding: 20,
          boxShadow: "0 6px 18px rgba(0,0,0,0.12)",
        }}
      >
        <h2 style={{ marginBottom: 12, color: "#0d47a1" }}>Current POI</h2>
        <p>
          <strong>{currentPOI.name}</strong>
        </p>
        <p>{currentPOI.description}</p>
        {currentPOI.image && (
          <img
            src={currentPOI.image}
            alt={currentPOI.name}
            style={{
              width: "100%",
              maxWidth: 280,
              borderRadius: 10,
              boxShadow: "0 6px 14px rgba(0,0,0,0.2)",
              transition: "transform 0.3s ease",
            }}
            onMouseOver={(e) => (e.currentTarget.style.transform = "scale(1.05)")}
            onMouseOut={(e) => (e.currentTarget.style.transform = "scale(1)")}
          />
        )}

        {segmentData && (
          <div style={{ marginTop: 10 }}>
            <p>Distance to next POI: {formatDistance(segmentData.summary.distance)}</p>
            <p>Estimated time: {formatDuration(segmentData.summary.duration)}</p>
          </div>
        )}

        {weather && (
          <div style={{ marginTop: 10 }}>
            <p>
              Weather: {weather.weather[0].description}, {weather.main.temp}Â°C
            </p>
          </div>
        )}

        <p style={{ marginTop: 20, fontStyle: "italic" }}>
          POI {currentIndex + 1} of {routeSpots.length}
        </p>

        <div style={{ marginTop: 20, display: "flex", gap: 10 }}>
          {currentIndex < routeSpots.length - 1 && (
            <button onClick={nextPOIHandler}>Next POI</button>
          )}
          <button onClick={cancelRoute}>Cancel</button>
          {currentIndex === routeSpots.length - 1 && (
            <button onClick={finishRoute}>Finish</button>
          )}
        </div>
      </div>
    </div>
  );
}
