import React, { useEffect, useState } from "react";

export default function NatureCulturePage() {
  const [places, setPlaces] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/api/pois")
      .then((res) => res.json())
      .then((data) => {
        const filtered = data.filter((p) => {
          if (!p.tags) return false;
          const tagsArray = p.tags.split(",").map((t) => t.trim().toLowerCase());
          return tagsArray.some(
            (tag) => tag.includes("nature") || tag.includes("culture")
          );
        });
        setPlaces(filtered);
      })
      .catch((err) => console.error("Error fetching places:", err));
  }, []);

  return (
    <div className="p-8">
      <h1 className="text-3xl font-bold mb-6 text-center">Nature & Culture</h1>
      {places.length === 0 ? (
        <p className="text-center text-gray-600">No nature or culture spots found.</p>
      ) : (
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
          {places.map((p) => (
            <div
              key={p.id}
              className="bg-white shadow-md rounded-2xl overflow-hidden hover:shadow-xl transition"
            >
              {p.image && (
                <img
                  src={`/images/${p.image}`}
                  alt={p.name}
                  className="w-full h-48 object-cover"
                />
              )}
              <div className="p-4">
                <h2 className="text-xl font-semibold mb-2">{p.name}</h2>
                <p className="text-gray-600 text-sm mb-2">{p.visitors}</p>
                <p className="text-gray-700 text-sm">{p.description}</p>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
