import React from "react";

export default function RoutePlanner({ spots, setRoute, weather }) {
  const handleGenerateRoute = async () => {
    if (!spots.length) return alert("No spots to generate route!");

    const user = JSON.parse(localStorage.getItem("user"));
    if (!user) return alert("User not logged in!");

    const preferences = {
      nature: user.preferences?.includes("nature") ? 1 : 0,
      culture: user.preferences?.includes("culture") ? 1 : 0,
    };

    try {
      const scoredSpots = await Promise.all(
        spots.map(async (spot) => {
          const response = await fetch("http://localhost:5000/predict", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
              user_preference_nature: preferences.nature,
              user_preference_culture: preferences.culture,
              weather_clear: weather === "Sunny" ? 1 : 0,
              distance_to_previous: Math.floor(Math.random() * 10),
            }),
          });
          const data = await response.json();

          let score = 0;
          switch (data.predicted_frequency) {
            case "Very High":
              score = 4;
              break;
            case "High":
              score = 3;
              break;
            case "Moderate":
              score = 2;
              break;
            case "Low":
              score = 1;
              break;
            default:
              score = 0;
          }

          return { ...spot, score };
        })
      );

      scoredSpots.sort((a, b) => b.score - a.score);
      setRoute(scoredSpots);
    } catch (err) {
      console.error("Error generating route:", err);
      alert("Failed to generate route. Try again.");
    }
  };

  return (
    <button
      onClick={handleGenerateRoute}
      style={{
        marginTop: "12px",
        padding: "10px 16px",
        backgroundColor: "#3b82f6",
        color: "#fff",
        border: "none",
        borderRadius: "8px",
        cursor: "pointer",
      }}
    >
      Generate Recommended Route
    </button>
  );
}
