import React, { useState, useEffect } from "react";

export default function AdminUsers() {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null); // for modal
  const [showModal, setShowModal] = useState(false);
  const token = localStorage.getItem("token");

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = () => {
    fetch("http://127.0.0.1/majayjay_tourism/users/", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then(res => res.json())
      .then(data => {
        if (Array.isArray(data)) {
          // Move admins to the bottom
          const admins = data.filter(u => u.role === "admin");
          const others = data.filter(u => u.role !== "admin");
          setUsers([...others, ...admins]);
        } else {
          setUsers([]);
        }
      })
      .catch(err => console.error(err));
  };

  const toggleBan = (userId, currentlyBanned) => {
    fetch(`http://127.0.0.1/majayjay_tourism/users/${userId}/ban`, {
      method: "POST",
      headers: {
        Authorization: `Bearer ${token}`,
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ ban: !currentlyBanned }),
    })
      .then(res => res.json())
      .then(() => fetchUsers())
      .catch(err => console.error(err));
  };

  const viewUser = (user) => {
    setSelectedUser(user);
    setShowModal(true);
  };

  return (
    <div style={{ marginLeft: "240px", padding: "20px" }}>
      <h1 style={{ marginBottom: "20px" }}>All Users</h1>
      {users.length === 0 ? (
        <p>No users found</p>
      ) : (
        <table style={{ width: "100%", borderCollapse: "collapse", background: "#f9f9f9" }}>
          <thead>
            <tr>
              <th style={thStyle}>ID</th>
              <th style={thStyle}>Username</th>
              <th style={thStyle}>Email</th>
              <th style={thStyle}>Role</th>
              <th style={thStyle}>Status</th>
              <th style={thStyle}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map(user => (
              <tr key={user.id} style={{ textAlign: "center" }}>
                <td style={tdStyle}>{user.id}</td>
                <td style={tdStyle}>{user.username}</td>
                <td style={tdStyle}>{user.email}</td>
                <td style={tdStyle}>{user.role}</td>
                <td style={tdStyle}>{user.banned ? "Banned" : "Active"}</td>
                <td style={tdStyle}>
                  <button onClick={() => viewUser(user)} style={actionBtnStyle("#2563eb")}>View</button>
                  {user.role !== "admin" && (
                    <button
                      onClick={() => toggleBan(user.id, user.banned)}
                      style={actionBtnStyle(user.banned ? "#4caf50" : "#f44336")}
                    >
                      {user.banned ? "Unban" : "Ban"}
                    </button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {/* User Modal */}
      {showModal && selectedUser && (
        <div style={modalOverlayStyle}>
          <div style={modalStyle}>
            <h2>User Details</h2>
            <p><strong>ID:</strong> {selectedUser.id}</p>
            <p><strong>Username:</strong> {selectedUser.username}</p>
            <p><strong>Email:</strong> {selectedUser.email}</p>
            <p><strong>First Name:</strong> {selectedUser.first_name}</p>
            <p><strong>Middle Name:</strong> {selectedUser.middle_name || "-"}</p>
            <p><strong>Last Name:</strong> {selectedUser.last_name}</p>
            <p><strong>Age:</strong> {selectedUser.age || "-"}</p>
            <p><strong>Role:</strong> {selectedUser.role}</p>
            <p><strong>Preferences:</strong> {selectedUser.preferences || "-"}</p>
            <p><strong>Address:</strong> {selectedUser.address || "-"}</p>
            <p><strong>Status:</strong> {selectedUser.banned ? "Banned" : "Active"}</p>
            <button onClick={() => setShowModal(false)} style={{ ...actionBtnStyle("#2563eb"), marginTop: "10px" }}>
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

// Styles
const thStyle = { borderBottom: "2px solid #ccc", padding: "10px", background: "#2563eb", color: "#fff" };
const tdStyle = { borderBottom: "1px solid #ccc", padding: "10px" };
const actionBtnStyle = (bgColor) => ({ padding: "6px 12px", margin: "0 4px", background: bgColor, color: "#fff", border: "none", borderRadius: "4px", cursor: "pointer" });
const modalOverlayStyle = { position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh", background: "rgba(0,0,0,0.5)", display: "flex", alignItems: "center", justifyContent: "center", zIndex: 1000 };
const modalStyle = { background: "#fff", padding: "20px", borderRadius: "8px", minWidth: "350px", boxShadow: "0 2px 10px rgba(0,0,0,0.2)" };
