import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import { useNavigate, useLocation } from "react-router-dom";

export default function RouteDetails() {
  const location = useLocation();
  const navigate = useNavigate();
  const routeSpots = location.state?.itinerary || [];

  const [poiDetails, setPoiDetails] = useState([]);
  const [userLocation, setUserLocation] = useState(null);
  const [tracking, setTracking] = useState(false);
  const [activeIndex, setActiveIndex] = useState(0);
  const [routeToStop, setRouteToStop] = useState([]);
  const mapRef = useRef(null);
  const watchIdRef = useRef(null);

  const ORS_API_KEY =
    "YOUR_ORS_KEY_HERE";

  // ---------- ICONS ----------
  const numberedIcon = (number, isActive = false) =>
    L.divIcon({
      html: `<div style="
        background:${isActive ? '#388e3c' : '#1976d2'};
        color:#fff;
        width:28px;
        height:28px;
        border-radius:50%;
        text-align:center;
        line-height:28px;
        font-weight:600;
        border:2px solid #fff;
        box-shadow:0 2px 6px rgba(0,0,0,0.3);
      ">${number}</div>`,
      className: "",
      iconSize: [28, 28],
    });

  const userIcon = L.divIcon({
    html: `<div style="
      background:#4caf50;
      color:#fff;
      width:30px;
      height:30px;
      border-radius:50%;
      text-align:center;
      line-height:30px;
      font-weight:700;
      border:2px solid #fff;
      box-shadow:0 2px 8px rgba(0,0,0,0.3);
    ">U</div>`,
    className: "",
    iconSize: [30, 30],
  });

  // ---------- USER LOCATION ----------
  useEffect(() => {
    if (!navigator.geolocation) return;
    navigator.geolocation.getCurrentPosition(
      (pos) => setUserLocation({ lat: pos.coords.latitude, lng: pos.coords.longitude }),
      () => setUserLocation({ lat: 14.1461, lng: 121.5012 }), // fallback
      { enableHighAccuracy: true }
    );
  }, []);

  // ---------- FETCH POI DETAILS ----------
  useEffect(() => {
    const fetchPois = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/pois");
        const allPois = await res.json();

        // Match POIs with routeSpots by id if available, else by name
        const matched = routeSpots.map((spot) => {
          return allPois.find((p) => p.id === spot.id || p.name === spot.name) || spot;
        });

        setPoiDetails(matched);
      } catch (err) {
        console.error("Error fetching POI details:", err);
        setPoiDetails(routeSpots); // fallback to passed spots
      }
    };
    fetchPois();
  }, [routeSpots]);

  // ---------- FETCH ROUTE ----------
  const fetchRoute = async (from, to) => {
    if (!from || !to) return [];
    try {
      const res = await fetch("https://api.openrouteservice.org/v2/directions/driving-car/geojson", {
        method: "POST",
        headers: { Authorization: ORS_API_KEY, "Content-Type": "application/json" },
        body: JSON.stringify({
          coordinates: [
            [from.lng, from.lat],
            [to.lng, to.lat],
          ],
          instructions: false,
        }),
      });
      const data = await res.json();
      if (data.features?.length) {
        return data.features[0].geometry.coordinates.map(([lng, lat]) => [lat, lng]);
      }
    } catch {
      return [[from.lat, from.lng], [to.lat, to.lng]];
    }
    return [];
  };

  // ---------- TOUR CONTROL ----------
  const startTour = async () => {
    if (!userLocation || poiDetails.length === 0) return;

    setTracking(true);
    setActiveIndex(0);

    const initialRoute = await fetchRoute(userLocation, poiDetails[0]);
    setRouteToStop(initialRoute);

    watchIdRef.current = navigator.geolocation.watchPosition(
      async (pos) => {
        const current = { lat: pos.coords.latitude, lng: pos.coords.longitude };
        setUserLocation(current);
        const liveRoute = await fetchRoute(current, poiDetails[activeIndex]);
        setRouteToStop(liveRoute);
      },
      (err) => console.warn("Watch error:", err),
      { enableHighAccuracy: true, maximumAge: 2000, timeout: 10000 }
    );
  };

  const handleNextStop = async () => {
    if (activeIndex + 1 >= poiDetails.length) {
      stopTour();
      alert("Tour completed!");
      return;
    }
    const nextStop = poiDetails[activeIndex + 1];
    setActiveIndex((prev) => prev + 1);
    if (userLocation) {
      const updatedRoute = await fetchRoute(userLocation, nextStop);
      setRouteToStop(updatedRoute);
    }
  };

  const stopTour = () => {
    if (watchIdRef.current) navigator.geolocation.clearWatch(watchIdRef.current);
    setTracking(false);
    setRouteToStop([]);
    setActiveIndex(0);
  };

  if (!userLocation || poiDetails.length === 0) return <p>Loading route...</p>;

  return (
    <div style={{ display: "grid", gridTemplateColumns: "1.3fr 0.7fr", gap: 24, padding: 24 }}>
      {/* MAP */}
      <div style={{ height: 650, borderRadius: 18, overflow: "hidden", boxShadow: "0 10px 25px rgba(0,0,0,0.15)" }}>
        <MapContainer
          ref={mapRef}
          center={[userLocation.lat, userLocation.lng]}
          zoom={13}
          style={{ height: "100%", width: "100%" }}
        >
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" attribution="Â© OpenStreetMap" />

          <Marker position={[userLocation.lat, userLocation.lng]} icon={userIcon}>
            <Popup>You are here</Popup>
          </Marker>

          {routeToStop.length > 0 && <Polyline positions={routeToStop} color="#1976d2" weight={6} opacity={0.9} />}

          {poiDetails.map((poi, i) => (
            <Marker key={i} position={[poi.lat, poi.lng]} icon={numberedIcon(i + 1, i === activeIndex)}>
              <Popup>
                <strong>{poi.name}</strong>
                <p>{poi.description || "No description available."}</p>
                {poi.image && <img src={`http://localhost:5000/images/${poi.image}`} alt={poi.name} style={{ width: 160, borderRadius: 8 }} />}
              </Popup>
            </Marker>
          ))}
        </MapContainer>
      </div>

      {/* SIDEBAR */}
      <div style={{ background: "#fff", borderRadius: 18, padding: 24, boxShadow: "0 8px 24px rgba(0,0,0,0.1)", overflowY: "auto", maxHeight: 650 }}>
        {!tracking ? (
          <>
            <h2 style={{ color: "#1565c0" }}>Route Overview</h2>
            {poiDetails.map((poi, i) => (
              <div key={i} style={{ marginBottom: 18, borderBottom: "1px solid #eee", paddingBottom: 12 }}>
                <h4>{poi.name}</h4>
                {poi.image && <img src={`http://localhost:5000/images/${poi.image}`} alt={poi.name} style={{ width: "100%", borderRadius: 8, marginBottom: 8 }} />}
                <p>{poi.description}</p>
              </div>
            ))}
            <button onClick={startTour} style={{ width: "100%", padding: 12, background: "#4caf50", color: "#fff", border: "none", borderRadius: 8, fontWeight: 600, cursor: "pointer" }}>
              Start Tour
            </button>
          </>
        ) : (
          <>
            <h3>Now Visiting: {poiDetails[activeIndex].name}</h3>
            <button onClick={handleNextStop} style={{ width: "100%", padding: 12, background: "#1976d2", color: "#fff", border: "none", borderRadius: 8, fontWeight: 600, cursor: "pointer", marginTop: 12 }}>
              Next Stop
            </button>
            <button onClick={stopTour} style={{ width: "100%", padding: 12, background: "#f44336", color: "#fff", border: "none", borderRadius: 8, fontWeight: 600, cursor: "pointer", marginTop: 10 }}>
              Cancel Tour
            </button>
          </>
        )}
        <button onClick={() => navigate("/home")} style={{ width: "100%", padding: 12, background: "#9e9e9e", color: "#fff", border: "none", borderRadius: 8, fontWeight: 600, cursor: "pointer", marginTop: 12 }}>
          Back to Home
        </button>
      </div>
    </div>
  );
}
