import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";

export default function RouteDetails() {
  const location = useLocation();
  const navigate = useNavigate();

  // fallback if location.state is null
  const { startPoint, filteredPois, transportMode } = location.state || {
    startPoint: null,
    filteredPois: [],
    transportMode: "foot-walking",
  };

  const [pois, setPois] = useState(filteredPois || []);

  // Redirect if startPoint is missing (optional)
  useEffect(() => {
    if (!startPoint) {
      navigate("/", { replace: true }); // go back to RoutePlanner
    }
  }, [startPoint, navigate]);

  const removePoi = async (poiId) => {
    setPois((prev) => prev.filter((p) => p.id !== poiId));
    const user = JSON.parse(localStorage.getItem("user"));
    if (user?.id) {
      try {
        await fetch("http://localhost:5000/api/feedback", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ user_id: user.id, poi_id: poiId, liked: 0 }),
        });
      } catch (err) {
        console.error("Failed to send feedback:", err);
      }
    }
  };

  if (!startPoint) return null; // prevent rendering before redirect

  return (
    <div style={{ padding: 20 }}>
      <h2>Route Details</h2>
      <p>Start Point: Lat {startPoint.lat}, Lng {startPoint.lng}</p>
      <p>Transport Mode: {transportMode}</p>

      <h3>Planned POIs:</h3>
      {pois.length === 0 ? (
        <p>No POIs selected.</p>
      ) : (
        <ul>
          {pois.map((poi) => (
            <li key={poi.id} style={{ marginBottom: 10 }}>
              <strong>{poi.name}</strong> | Score: {poi.score || 0}{" "}
              <button
                onClick={() => removePoi(poi.id)}
                style={{
                  marginLeft: 10,
                  padding: "4px 8px",
                  background: "#f44336",
                  color: "#fff",
                  border: "none",
                  borderRadius: 4,
                  cursor: "pointer",
                }}
              >
                Remove
              </button>
            </li>
          ))}
        </ul>
      )}

      <button
        onClick={() => navigate(-1)}
        style={{
          marginTop: 20,
          padding: "10px 20px",
          background: "#1976d2",
          color: "#fff",
          border: "none",
          borderRadius: 6,
          cursor: "pointer",
        }}
      >
        Back
      </button>
    </div>
  );
}
