import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from "react-leaflet";
import "leaflet/dist/leaflet.css";

// Keep your actual keys here
const ORS_API_KEY = "eyJvcmciOiI1YjNjZTM1OTc4NTExMTAwMDFjZjYyNDgiLCJpZCI6ImJhNWU3NmIxOTRmMzQyYTg4NzY4MDc2MzVmZDY1ZDRhIiwiaCI6Im11cm11cjY0In0=";
const WEATHER_API_KEY = "13d7213054a676ccdd935415b00fac6f";

export default function RouteDetails({ itinerary = [] }) {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [segmentData, setSegmentData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [weather, setWeather] = useState(null);

  const currentPOI = itinerary[currentIndex] || null;

  // Fetch ORS route between two POIs
  const fetchORSRoute = async (from, to) => {
    const profile = "driving-car"; // change to "foot-hiking" for trails
    const url = `https://api.openrouteservice.org/v2/directions/${profile}?api_key=${ORS_API_KEY}&start=${from.lng},${from.lat}&end=${to.lng},${to.lat}`;
    try {
      const res = await fetch(url);
      const data = await res.json();
      console.log("ORS response:", data); // debug
      if (!data.features || !data.features[0]) throw new Error("No route found");
      const coords = data.features[0].geometry.coordinates.map(([lng, lat]) => [lat, lng]);
      const { distance, duration } = data.features[0].properties.summary;
      return { coords, distance, duration };
    } catch (err) {
      console.error("ORS routing error:", err);
      // fallback: straight line if ORS fails
      return { coords: [[from.lat, from.lng], [to.lat, to.lng]], distance: 0, duration: 0 };
    }
  };

  // Build all route segments
  useEffect(() => {
    const buildSegments = async () => {
      if (itinerary.length < 2) {
        setSegmentData([]);
        setLoading(false);
        return;
      }
      setLoading(true);
      const segments = [];
      for (let i = 0; i < itinerary.length - 1; i++) {
        const segment = await fetchORSRoute(itinerary[i], itinerary[i + 1]);
        segments.push(segment);
      }
      setSegmentData(segments);
      setLoading(false);
    };
    buildSegments();
  }, [itinerary]);

  // Fetch weather for current POI
  useEffect(() => {
    const fetchWeather = async () => {
      if (!currentPOI) return;
      try {
        const res = await fetch(
          `https://api.openweathermap.org/data/2.5/weather?lat=${currentPOI.lat}&lon=${currentPOI.lng}&units=metric&appid=${WEATHER_API_KEY}`
        );
        const data = await res.json();
        setWeather(data);
      } catch {
        setWeather(null);
      }
    };
    fetchWeather();
  }, [currentPOI]);

  // Navigation handlers
  const nextPOI = () => {
    if (currentIndex < itinerary.length - 1) setCurrentIndex(currentIndex + 1);
    else alert("You have reached the end of the route!");
  };

  const finishRoute = () => {
    if (window.confirm("Finish route?")) alert("Route completed!");
  };

  const cancelRoute = () => {
    if (window.confirm("Cancel route?")) alert("Route cancelled.");
  };

  if (!currentPOI) return <p>No route available.</p>;

  const currentSegment = segmentData[currentIndex] || null;

  return (
    <div style={{ display: "grid", gridTemplateColumns: "1.2fr 0.8fr", gap: 20, padding: 20, minHeight: "100vh" }}>
      {/* MAP */}
      <div style={{ height: "600px", borderRadius: 12, overflow: "hidden" }}>
        {loading ? (
          <p style={{ textAlign: "center", marginTop: "40%" }}>Loading route...</p>
        ) : (
          <MapContainer center={[currentPOI.lat, currentPOI.lng]} zoom={14} style={{ height: "100%", width: "100%" }}>
            <TileLayer
              attribution='&copy; <a href="https://www.openstreetmap.org/">OSM contributors</a>'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            {segmentData.map((seg, i) => (
              <Polyline key={i} positions={seg.coords} color="blue" />
            ))}
            {itinerary.map((poi, i) => (
              <Marker key={i} position={[poi.lat, poi.lng]}>
                <Popup>
                  <strong>{poi.name}</strong>
                  <p>{poi.description}</p>
                  {poi.image && <img src={poi.image} alt={poi.name} style={{ width: "160px", borderRadius: 8 }} />}
                </Popup>
              </Marker>
            ))}
          </MapContainer>
        )}
      </div>

      {/* INFO PANEL */}
      <div style={{ background: "#fff", borderRadius: 12, padding: 20 }}>
        <h2>Current POI</h2>
        <p><strong>{currentPOI.name}</strong></p>
        <p>{currentPOI.description}</p>
        {currentPOI.image && <img src={currentPOI.image} alt={currentPOI.name} style={{ width: "100%", borderRadius: 10 }} />}

        {currentSegment && (
          <div style={{ marginTop: 10 }}>
            <p>Distance to next: {(currentSegment.distance / 1000).toFixed(2)} km</p>
            <p>Estimated time: {(currentSegment.duration / 60).toFixed(0)} min</p>
          </div>
        )}

        {weather && weather.weather && weather.weather[0] && (
          <div style={{ marginTop: 10 }}>
            <p>Weather: {weather.weather[0].description}, {weather.main.temp}Â°C</p>
          </div>
        )}

        <div style={{ marginTop: 20, display: "flex", gap: 10 }}>
          {currentIndex < itinerary.length - 1 && <button onClick={nextPOI}>Next POI</button>}
          <button onClick={cancelRoute}>Cancel</button>
          {currentIndex === itinerary.length - 1 && <button onClick={finishRoute}>Finish</button>}
        </div>
      </div>
    </div>
  );
}
